/**
 * Sharpee Languages - Built-in language system
 * 
 * This module provides the new author-centric language system where
 * authors can simply call story.languageSet(US_EN) and everything works.
 */

export * from './constants';
export * from './types';
export * from './registry';

// Import and register built-in language definitions
import { getLanguageRegistry } from './registry';
import { createEnglishDefinition } from './definitions/english';

// Auto-register built-in languages
const registry = getLanguageRegistry();

// Register English as the primary language
registry.register(createEnglishDefinition());

// Future: Register other languages
// registry.register(createFrenchDefinition());
// registry.register(createSpanishDefinition());

/**
 * Convenience exports for common usage
 */
import { createLanguageInstance, getLanguageInstance } from './registry';
import { US_EN } from './constants';

export {
  createLanguageInstance,
  getLanguageInstance
};

/**
 * Helper function to create an English language instance
 * This is what will be used internally by the parser test and Story class
 */
export function createEnglishLanguageInstance() {
  return createLanguageInstance(US_EN);
}

/**
 * Helper function to get parser config from language code
 * This provides compatibility with the existing parser architecture
 */
export function getParserConfigForLanguage(languageCode: string) {
  const instance = getLanguageInstance(languageCode as any);
  return instance.getParserConfig();
}

/**
 * Helper function to get grammar patterns from language code
 * This provides compatibility with the existing parser architecture
 */
export function getGrammarPatternsForLanguage(languageCode: string) {
  const instance = getLanguageInstance(languageCode as any);
  return instance.getGrammarPatterns();
}

/**
 * Create a parser provider compatible with the existing IF parser
 * This allows the test to work without importing external packages
 */
export function createEnglishParserProvider() {
  const instance = createEnglishLanguageInstance();

  return {
    getParserConfig() {
      return instance.getParserConfig();
    },
    getGrammarPatterns() {
      return instance.getGrammarPatterns();
    },
    getSynonyms(word: string) {
      return instance.getSynonyms(word);
    }
  };
}

/**
 * Core types for the new Sharpee language system
 */

import { IFParserConfig, GrammarPattern } from '../parser/if-parser-types';
import { VerbDefinition } from '../language/types';
import { SupportedLanguage } from './constants';
import { LanguageData } from '../parser/languages/language-data';

/**
 * Complete language definition containing everything needed for a language
 */
export interface LanguageDefinition {
  /** Language metadata */
  code: SupportedLanguage;
  name: string;
  englishName: string;
  direction: 'ltr' | 'rtl';

  /** Parser configuration */
  parser: IFParserConfig;

  /** Grammar patterns for commands */
  patterns: GrammarPattern[];

  /** Message templates for responses */
  messages: Record<string, string>;

  /** Verb definitions with synonyms */
  verbs: Record<string, VerbDefinition>;

  /** Synonym mappings */
  synonyms: Map<string, string[]>;
}

/**
 * Options for language configuration
 */
export interface LanguageOptions {
  /** Custom message templates to override defaults */
  customMessages?: Record<string, string>;

  /** Custom verb definitions to add or override */
  customVerbs?: Record<string, VerbDefinition>;

  /** Additional synonym mappings */
  customSynonyms?: Record<string, string[]>;
}

/**
 * Language registry interface
 */
export interface LanguageRegistry {
  /** Register a language definition */
  register(definition: LanguageDefinition): void;

  /** Get a language definition by code */
  get(code: SupportedLanguage): LanguageDefinition | undefined;

  /** Check if a language is registered */
  has(code: SupportedLanguage): boolean;

  /** Get all registered language codes */
  getRegisteredCodes(): SupportedLanguage[];
}

/**
 * Language instance with applied options
 */
export interface LanguageInstance {
  /** Original language definition */
  definition: LanguageDefinition;

  /** Applied options */
  options: LanguageOptions;

  /** Get a message template, with custom overrides applied */
  getMessage(key: string): string;

  /** Format a message with parameters */
  formatMessage(key: string, ...params: any[]): string;

  /** Get verb definition, with custom overrides applied */
  getVerb(canonical: string): VerbDefinition | undefined;

  /** Check if a word is a known verb */
  isVerb(word: string): boolean;

  /** Get canonical verb form */
  getCanonicalVerb(verb: string): string | undefined;

  /** Get synonyms for a word */
  getSynonyms(word: string): string[];

  /** Get the parser configuration */
  getParserConfig(): IFParserConfig;

  /** Get grammar patterns */
  getGrammarPatterns(): GrammarPattern[];

  /** Get language data for enhanced parser */
  getLanguageData(): LanguageData;
}

/**
 * Factory function type for creating language definitions
 */
export type LanguageDefinitionFactory = () => LanguageDefinition;

// packages/core/src/language/types.ts

/**
 * Core language provider interface
 * This provides only generic text formatting capabilities,
 * without knowledge of specific game concepts
 */
export interface LanguageProvider {
  /**
   * Format a message with parameters
   * @param template The message template
   * @param params Parameters to substitute
   */
  formatMessage(template: string, params?: any): string;

  /**
   * Format a list of items
   * @param items The items to format
   * @param options Formatting options
   */
  formatList(items: string[], options?: ListFormatOptions): string;

  /**
   * Get the language code
   */
  getLanguageCode(): string;

  /**
   * Get the language name
   */
  getLanguageName(): string;

  /**
   * Get text direction
   */
  getTextDirection(): 'ltr' | 'rtl';
}

/**
 * Options for formatting lists
 */
export interface ListFormatOptions {
  /**
   * Style of list formatting
   */
  style?: 'long' | 'short' | 'narrow';

  /**
   * Type of list
   */
  type?: 'conjunction' | 'disjunction' | 'unit';

  /**
   * Locale-specific list formatting options
   */
  localeOptions?: any;
