/**
 * Asking action - Ask someone about something
 */

import { ActionDefinition, PhaseResult } from '../types';
import { IFCommand } from '../../../parser/if-parser-types';
import { GameContext } from '../../types';
import { createEvent } from '../../../events/event-system';
import { StandardActions, StandardEvents } from '../../../language/types';

/**
 * Asking action definition
 */
export const askingAction: ActionDefinition = {
  id: StandardActions.ASKING,
  name: StandardActions.ASKING,
  verbs: ['ask', 'question', 'query', 'inquire'],
  metadata: {
    changesWorld: false,
    undoable: false,
    categories: ['communication']
  },
  phases: {
    // VALIDATE phase - Check if asking is possible
    validate: (command: IFCommand, context: GameContext): PhaseResult => {
      const constants = context.languageProvider.getConstants();

      // Must have a target (who to ask)
      if (!command.noun || command.noun.length === 0) {
        return {
          continue: false,
          events: [
            createEvent(
              constants.events.ACTION_PREVENTED,
              { reason: 'no_target', message: 'Ask whom?' },
              { narrate: true }
            )
          ]
        };
      }

      const target = command.noun[0].entity;
      const targetId = target.id;

      // Can't ask yourself
      if (targetId === command.actor) {
        return {
          continue: false,
          events: [
            createEvent(
              constants.events.ACTION_PREVENTED,
              { 
                reason: 'ask_self',
                message: "You ponder the question deeply, but no answer comes."
              },
              { narrate: true }
            )
          ]
        };
      }

      // Check if target is a person/NPC or animate
      if (target.type !== 'person' && target.type !== 'npc' && !target.attributes.animate) {
        return {
          continue: false,
          events: [
            createEvent(
              constants.events.ACTION_PREVENTED,
              { 
                reason: 'not_animate',
                message: context.languageProvider.getMessage('action.asking.not_animate', {
                  target: target.attributes.name || 'that'
                }) || `You can't ask ${target.attributes.name || 'that'} questions.`,
                targetName: target.attributes.name
              },
              { narrate: true }
            )
          ]
        };
      }

      // Check if target is in the same location
      const targetLocation = context.findLocationOf(targetId);
      if (targetLocation !== context.currentLocation.id) {
        return {
          continue: false,
          events: [
            createEvent(
              constants.events.ACTION_PREVENTED,
              { 
                reason: 'not_here',
                message: context.languageProvider.getMessage('action.asking.not_here', {
                  target: target.attributes.name || 'them'
                }) || `${target.attributes.name || 'They'} ${target.attributes.plural ? 'are' : 'is'} not here.`,
                targetName: target.attributes.name
              },
              { narrate: true }
            )
          ]
        };
      }

      // Check if we have a topic (what to ask about)
      const topic = command.commandText?.includes('about') ? 
        command.commandText.split('about')[1]?.trim() : 
        command.indirectObject?.[0]?.entity.attributes.name;

      if (!topic) {
        return {
          continue: false,
          events: [
            createEvent(
              constants.events.ACTION_PREVENTED,
              { 
                reason: 'no_topic',
                message: 'Ask about what?'
              },
              { narrate: true }
            )
          ]
        };
      }

      // All checks passed
      return {
        continue: true,
        events: [],
        metadata: { topic }
      };
    },

    // EXECUTE phase - Actually ask
    execute: (command: IFCommand, context: GameContext): PhaseResult => {
      const constants = context.languageProvider.getConstants();
      const target = command.noun![0].entity;
      const targetId = target.id;
      const topic = command.metadata?.topic as string || '';

      // Get responses from target's attributes
      const responses = target.attributes.askResponses as Record<string, string> | undefined;
      const topics = target.attributes.topics as string[] | undefined;
      const defaultResponse = target.attributes.defaultAskResponse as string | undefined;

      // Look for a response to this topic
      let response: string | undefined;

      if (responses) {
        // Check for exact match
        response = responses[topic.toLowerCase()];

        // Check for partial matches
        if (!response) {
          for (const [key, value] of Object.entries(responses)) {
            if (topic.toLowerCase().includes(key.toLowerCase()) || 
                key.toLowerCase().includes(topic.toLowerCase())) {
              response = value;
              break;
            }
          }
        }
      }

      // Use default response if no specific response found
      if (!response) {
        response = defaultResponse || 
          `${target.attributes.name} doesn't seem to know anything about that.`;
      }

      // Create success event
      const events = [
        createEvent(
          constants.events.NPC_ASKED_ABOUT,
          { 
            npcId: targetId,
            npcName: target.attributes.name,
            topic,
            response,
            knownTopics: topics
          },
          { 
            actor: command.actor,
            location: context.currentLocation.id,
            narrate: true
          }
        )
      ];

      return {
        continue: true,
        success: true,
        events,
        context
      };
    }
  }
};

// packages/core/src/events/text-processor.ts

import { SemanticEvent } from './types';
import { StandardEventTypes } from './standard-events';
import { getActiveLanguageProvider, LanguageProvider, StandardTemplateKey } from '../language';

/**
 * Process an event and generate appropriate text output
 * @param event The event to process
 * @param languageProvider The language provider to use for templates
 * @returns Formatted text for the event
 */
export function processEvent(event: SemanticEvent, languageProvider?: LanguageProvider): string {
  // Get the language provider (use active provider if none provided)
  const provider = languageProvider || getActiveLanguageProvider();
  const { type, payload } = event;

  switch (type) {
    // Narrative events (used by rules)
    case StandardEventTypes.NARRATIVE_EVENT:
      // Check if we have a template key and parameters
      if (payload?.messageKey) {
        const messageKey = payload.messageKey as string;
        const messageParams = payload.messageParams as any[] || [];
        return provider.formatMessage(messageKey, ...messageParams);
      }
      // Fallback for direct message
      return payload?.message as string || "Something happened.";

    // Item interaction events
    case StandardEventTypes.ITEM_TAKEN:
      return provider.formatMessage(StandardTemplateKey.TAKE_SUCCESS, payload?.itemName);

    case StandardEventTypes.ITEM_DROPPED:
      return provider.formatMessage(StandardTemplateKey.DROP_SUCCESS, payload?.itemName);

    case StandardEventTypes.ITEM_EXAMINED:
      if (payload?.location) {
        // Looking around a location
        const description = payload.description as string;
        const exits = payload.exits as Record<string, string> || {};
        const visibleItems = payload.visibleItems as Array<{name: string}> || [];

        let result = provider.formatMessage(StandardTemplateKey.LOOK_DESCRIPTION, description);

        // Add exits if there are any
        if (Object.keys(exits).length > 0) {
          const exitsList = Object.entries(exits)
            .map(([direction, locationName]) => `${direction} to ${locationName}`)
            .join(', ');
          result += `\n${provider.formatMessage(StandardTemplateKey.LOOK_EXITS_HEADER)}${exitsList}`;
        }

        // Add visible items if there are any
        if (visibleItems.length > 0) {
          const itemsList = provider.formatList(
            StandardTemplateKey.LOOK_ITEMS_HEADER, 
            StandardTemplateKey.LOOK_ITEM, 
            [], 
            visibleItems.map(item => item.name || 'unnamed object')
          );
          result += `\n${itemsList}`;
        }

        return result;
      } else {
        // Looking at a specific item
        const itemName = payload?.itemName as string;
        const description = payload?.description as string;
        const isContainer = payload?.isContainer as boolean;
        const isOpen = payload?.isOpen as boolean;
        const contents = payload?.contents as Array<{name: string}> || [];

        let result = provider.formatMessage(StandardTemplateKey.LOOK_ITEM_DESCRIPTION, description);

        // Add container contents if it's an open container
        if (isContainer && isOpen && contents.length > 0) {
          const contentsList = provider.formatList(
            StandardTemplateKey.LOOK_CONTAINER_CONTENTS_HEADER, 
            StandardTemplateKey.LOOK_CONTAINER_CONTENT, 
            [itemName], 
            contents.map(item => item.name || 'unnamed object')
          );
          result += `\n${contentsList}`;
        }

        return result;
      }

    case StandardEventTypes.ITEM_OPENED:
      return provider.formatMessage(StandardTemplateKey.OPEN_SUCCESS, payload?.itemName);

    case StandardEventTypes.ITEM_CLOSED:
      return provider.formatMessage(StandardTemplateKey.CLOSE_SUCCESS, payload?.itemName);

    case StandardEventTypes.CONTAINER_OPENED:
      return provider.formatMessage(StandardTemplateKey.OPEN_SUCCESS, payload?.containerName);

    case StandardEventTypes.CONTAINER_CLOSED:
      return provider.formatMessage(StandardTemplateKey.CLOSE_SUCCESS, payload?.containerName);

    case StandardEventTypes.DOOR_OPENED:
      return provider.formatMessage(StandardTemplateKey.OPEN_SUCCESS, payload?.doorName);

    case StandardEventTypes.DOOR_CLOSED:
      return provider.formatMessage(StandardTemplateKey.CLOSE_SUCCESS, payload?.doorName);

    // Movement events
    case StandardEventTypes.PLAYER_MOVED:
      return provider.formatMessage(StandardTemplateKey.GO_SUCCESS, payload?.direction);

    case StandardEventTypes.PLAYER_WAITED:
      return provider.formatMessage(StandardTemplateKey.WAIT_SUCCESS);

    // Inventory events
    case StandardEventTypes.INVENTORY_LISTED:
      const items = payload?.items as Array<{name: string}> || [];

      if (items.length === 0) {
        return provider.formatMessage(StandardTemplateKey.INVENTORY_EMPTY);
      }

      return provider.formatList(
        StandardTemplateKey.INVENTORY_HEADER, 
        StandardTemplateKey.INVENTORY_ITEM, 
        [], 
        items.map(item => item.name || 'unnamed object')
      );

    // Ability events
    case StandardEventTypes.ABILITY_ACTIVATED:
      return provider.formatMessage(StandardTemplateKey.ABILITY_ACTIVATED, payload?.abilityName);

    case StandardEventTypes.ABILITY_FAILED:
      return provider.formatMessage(StandardTemplateKey.ABILITY_FAILED, payload?.abilityName);

    // Command failure events
    case StandardEventTypes.COMMAND_FAILED:
      return processFailureEvent(payload?.reason as string, payload, provider);

    // Default response for unknown events
    default:
      return "Something happened.";
  }
}

/**
 * Process failure events and return appropriate error messages
 * @param reason The reason for the failure
 * @param payload Additional payload data
 * @param languageProvider The language provider to use
 * @returns Formatted error message
 */
function processFailureEvent(
  reason: string, 
  payload: Record<string, unknown> = {}, 
  languageProvider: LanguageProvider
): string {
  // Map failure reasons to template keys
  const errorMap: Record<string, StandardTemplateKey> = {
    'objectNotFound': StandardTemplateKey.OBJECT_NOT_FOUND,
    'notTakeable': StandardTemplateKey.CANT_TAKE,
    'notInInventory': StandardTemplateKey.CANT_DROP,
    'notOpenable': StandardTemplateKey.CANT_OPEN,
    'alreadyOpen': StandardTemplateKey.ALREADY_OPEN,
    'containerClosed': StandardTemplateKey.CONTAINER_CLOSED,
    'tooHeavy': StandardTemplateKey.TOO_HEAVY,
    'notAccessible': StandardTemplateKey.NOT_ACCESSIBLE,
    'notVisible': StandardTemplateKey.NOT_VISIBLE,
    'cantGo': StandardTemplateKey.CANT_GO,
    'ambiguousObject': StandardTemplateKey.AMBIGUOUS_OBJECT
  };

  const templateKey = errorMap[reason] || 'GENERIC_FAILURE';
  const itemName = payload.itemName || payload.objectName || '';

  return languageProvider.formatMessage(templateKey, itemName);
}

/**
 * Interface for text processing services
 */
export interface TextService {
  /**
   * Process a list of events into text output
   * @param events Events to process
   * @returns Formatted text output
   */
  processEvents(events: SemanticEvent[]): string;

  /**
   * Format a message using a template
   * @param templateKey The template key
   * @param params Parameters for the template
   * @returns Formatted message
   */
  formatMessage(templateKey: string, ...params: any[]): string;

  /**
   * Format a list of items
   * @param headerTemplate Header template key
   * @param itemTemplate Item template key
   * @param headerParams Parameters for the header
   * @param items Items to format
   * @returns Formatted list
   */
  formatList(headerTemplate: string, itemTemplate: string, headerParams: any[], items: any[]): string;

  /**
   * Set the language provider used by this service
   * @param provider The language provider to use
   */
  setLanguageProvider(provider: LanguageProvider): void;

  /**
   * Get the current language provider
   * @returns The current language provider
   */
  getLanguageProvider(): LanguageProvider;
}

/**
 * Implementation of the TextService interface
 */
export class TextProcessor implements TextService {
  private languageProvider: LanguageProvider;

  /**
   * Create a new text processor
   * @param languageProvider Optional language provider to use
   */
  constructor(languageProvider?: LanguageProvider) {
    this.languageProvider = languageProvider || getActiveLanguageProvider();
  }

  /**
   * Process a list of events into text output
   * @param events Events to process
   * @returns Formatted text output
   */
  processEvents(events: SemanticEvent[]): string {
    const textParts = events
      .filter(event => event.narrate !== false)
      .map(event => processEvent(event, this.languageProvider));

    return textParts.join('\n');
  }

  /**
   * Format a message using a template
   * @param templateKey The template key
   * @param params Parameters for the template
   * @returns Formatted message
   */
  formatMessage(templateKey: string, ...params: any[]): string {
    return this.languageProvider.formatMessage(templateKey, ...params);
  }

  /**
   * Format a list of items
   * @param headerTemplate Header template key
   * @param itemTemplate Item template key
   * @param headerParams Parameters for the header
   * @param items Items to format
   * @returns Formatted list
   */
  formatList(headerTemplate: string, itemTemplate: string, headerParams: any[], items: any[]): string {
    return this.languageProvider.formatList(headerTemplate, itemTemplate, headerParams, items);
  }

  /**
   * Set the language provider used by this service
   * @param provider The language provider to use
   */
  setLanguageProvider(provider: LanguageProvider): void {
    this.languageProvider = provider;
  }

  /**
   * Get the current language provider
   * @returns The current language provider
   */
  getLanguageProvider(): LanguageProvider {
    return this.languageProvider;
  }
}

/**
 * Create a new text service
 * @param languageProvider Optional language provider to use
 * @returns A new TextService instance
 */
export function createTextService(languageProvider?: LanguageProvider): TextService {
  return new TextProcessor(languageProvider);
}

/**
 * Standard event types and tags
 * TODO: Move to proper location
 */

export enum StandardEventTypes {
  ACTION = 'action',
  SYSTEM = 'system',
  NARRATIVE = 'narrative'
}

export enum StandardEventTags {
  SUCCESS = 'success',
  FAILURE = 'failure',
  INFO = 'info',
  WARNING = 'warning',
  ERROR = 'error'
}

export const EventCategories = {
  ACTION: 'action',
  SYSTEM: 'system',
  NARRATIVE: 'narrative'
} as const;

