// packages/core/src/language/index.ts

/**
 * Core language system
 * Provides generic text formatting without game-specific knowledge
 */

export * from './types';
export * from './registry';
export * from './default-provider';

// Re-export commonly used functions
import { getLanguageRegistry } from './registry';
import { LanguageProvider } from './types';
import { createDefaultLanguageProvider } from './default-provider';

/**
 * Get the active language provider
 * @returns The currently active language provider
 */
export function getActiveLanguageProvider(): LanguageProvider {
  const provider = getLanguageRegistry().getProvider();
  if (!provider) {
    // Return a default provider if none is set
    return createDefaultLanguageProvider();
  }
  return provider;
}

/**
 * Format a message using the active language provider
 * @param template The message template
 * @param params Parameters to substitute
 * @returns The formatted message
 */
export function formatMessage(template: string, params?: any): string {
  const provider = getActiveLanguageProvider();
  return provider.formatMessage(template, params);
}

/**
 * Format a list using the active language provider
 * @param items The items to format
 * @param options Formatting options
 * @returns The formatted list
 */
export function formatList(items: string[], options?: any): string {
  const provider = getActiveLanguageProvider();
  return provider.formatList(items, options);
}

// packages/core/src/execution/game-context.ts

import { Entity, EntityId, WorldState } from '../world-model/types';
import { EntityManager } from '../world-model/implementations/entity-manager';
import { GameContext, CommandRouter } from './types';
import { TextService, createTextService } from '../events/text-processor';
import { RelationshipType } from '../world-model/types';
import { createCommandRouter } from './command-router';
import { LanguageProvider, getActiveLanguageProvider } from '../language';

/**
 * Implementation of the GameContext interface
 */
export class GameContextImpl implements GameContext {
  worldState: WorldState;
  player: Entity;
  currentLocation: Entity;
  textService: TextService;
  languageProvider: LanguageProvider;
  commandRouter?: CommandRouter;

  private entityManager: EntityManager;

  /**
   * Create a new game context
   */
  constructor(
    worldState: WorldState,
    playerId: EntityId,
    entityManager: EntityManager,
    textService?: TextService,
    languageProvider?: LanguageProvider,
    commandRouter?: CommandRouter
  ) {
    this.worldState = worldState;
    this.entityManager = entityManager;
    this.languageProvider = languageProvider || getActiveLanguageProvider();
    this.textService = textService || createTextService(this.languageProvider);
    this.commandRouter = commandRouter;

    // Get player entity
    const player = this.getEntity(playerId);
    if (!player) {
      throw new Error(`Player entity not found: ${playerId}`);
    }
    this.player = player;

    // Get current location
    const locationId = this.findPlayerLocation(player);
    const location = this.getEntity(locationId);
    if (!location) {
      throw new Error(`Player location not found: ${locationId}`);
    }
    this.currentLocation = location;
  }

  /**
   * Get an entity by ID
   */
  getEntity(id: EntityId): Entity | undefined {
    return this.worldState.entities[id];
  }

  /**
   * Get entities by type
   */
  getEntitiesByType(type: string): Entity[] {
    return Object.values(this.worldState.entities)
      .filter(entity => entity.type === type);
  }

  /**
   * Get entities with a specific relationship to an entity
   */
  getRelatedEntities(entityId: EntityId, relationshipType: string): Entity[] {
    const entity = this.getEntity(entityId);
    if (!entity) return [];

    const relatedIds = entity.relationships[relationshipType] || [];
    return relatedIds
      .map(id => this.getEntity(id))
      .filter((e): e is Entity => !!e);
  }

  /**
   * Check if an entity is accessible to the player
   */
  isAccessible(entityId: EntityId): boolean {
    const entity = this.getEntity(entityId);
    if (!entity) return false;

    // If it's in the player's inventory, it's accessible
    const playerInventory = this.player.relationships[RelationshipType.CONTAINS] || [];
    if (playerInventory.includes(entityId)) return true;

    // If it's in the current location, it's accessible
    const locationContents = this.currentLocation.relationships[RelationshipType.CONTAINS] || [];
    if (locationContents.includes(entityId)) return true;

    // If it's in an open container in the current location, it's accessible
    const containers = locationContents
      .map(id => this.getEntity(id))
      .filter((e): e is Entity => 
        !!e && 
        e.attributes.container === true && 
        e.attributes.open === true
      );

    for (const container of containers) {
      const containerContents = container.relationships[RelationshipType.CONTAINS] || [];
      if (containerContents.includes(entityId)) return true;
    }

    return false;
  }

  /**
   * Check if an entity is visible to the player
   */
  isVisible(entityId: EntityId): boolean {
    const entity = this.getEntity(entityId);
    if (!entity) return false;

    // Hidden entities are not visible
    if (entity.attributes.hidden === true) return false;

    // If it's accessible, it's visible
    if (this.isAccessible(entityId)) return true;

    // If it's in a closed container, it's not visible
    const containers = Object.values(this.worldState.entities)
      .filter(e => 
        e.attributes.container === true && 
        (e.relationships[RelationshipType.CONTAINS] || []).includes(entityId)
      );

    for (const container of containers) {
      if (container.attributes.open !== true && this.isVisible(container.id)) {
        return false;
      }
    }

    return false;
  }

  /**
   * Find an entity by name
   */
  findEntityByName(name: string, options: {
    location?: EntityId;
    includeInventory?: boolean;
    typeFilter?: string[];
  } = {}): Entity | undefined {
    const entities = this.findEntitiesByName(name, options);
    return entities.length > 0 ? entities[0] : undefined;
  }

  /**
   * Find entities by name
   */
  findEntitiesByName(name: string, options: {
    location?: EntityId;
    includeInventory?: boolean;
    typeFilter?: string[];
  } = {}): Entity[] {
    // Default options
    const { 
      location = this.currentLocation.id,
      includeInventory = true,
      typeFilter
    } = options;

    // Get entities in the location
    const locationEntities: Entity[] = [];
    const locationEntity = this.getEntity(location);

    if (locationEntity) {
      const locationContents = locationEntity.relationships[RelationshipType.CONTAINS] || [];
      locationEntities.push(
        ...locationContents
          .map(id => this.getEntity(id))
          .filter((e): e is Entity => !!e && this.isVisible(e.id))
      );

      // Also include entities in open containers in the location
      const containers = locationEntities.filter(e => 
        e.attributes.container === true && 
        e.attributes.open === true
      );

      for (const container of containers) {
        const containerContents = container.relationships[RelationshipType.CONTAINS] || [];
        locationEntities.push(
          ...containerContents
            .map(id => this.getEntity(id))
            .filter((e): e is Entity => !!e && this.isVisible(e.id))
        );
      }
    }

    // Get entities in the player's inventory
    const inventoryEntities: Entity[] = [];

    if (includeInventory) {
      const playerInventory = this.player.relationships[RelationshipType.CONTAINS] || [];
      inventoryEntities.push(
        ...playerInventory
          .map(id => this.getEntity(id))
          .filter((e): e is Entity => !!e)
      );
    }

    // Combine and filter entities
    return [...locationEntities, ...inventoryEntities]
      .filter(entity => {
        // Filter by type if specified
        if (typeFilter && !typeFilter.includes(entity.type)) {
          return false;
        }

        // Match by name or aliases
        const entityName = entity.attributes.name as string || '';
        const aliases = entity.attributes.aliases as string[] || [];

        return (
          entityName.toLowerCase().includes(name.toLowerCase()) ||
          aliases.some(alias => alias.toLowerCase().includes(name.toLowerCase()))
        );
      });
  }

  /**
   * Find the location that contains an entity
   */
  findLocationOf(entityId: EntityId): EntityId | undefined {
    // If it's the player, return the current location
    if (entityId === this.player.id) {
      return this.currentLocation.id;
    }

    // Check if the entity is in the player's inventory
    const playerInventory = this.player.relationships[RelationshipType.CONTAINS] || [];
    if (playerInventory.includes(entityId)) {
      return this.player.id;
    }

    // Check all entities to find which one contains the entity
    for (const entity of Object.values(this.worldState.entities)) {
      const contents = entity.relationships[RelationshipType.CONTAINS] || [];
      if (contents.includes(entityId)) {
        return entity.id;
      }
    }

    return undefined;
  }

  /**
   * Check if an entity is contained in another entity
   */
  isContainedIn(itemId: EntityId, containerId: EntityId): boolean {
    const container = this.getEntity(containerId);
    if (!container) return false;

    const contents = container.relationships[RelationshipType.CONTAINS] || [];
    return contents.includes(itemId);
  }

  /**
   * Function to update the world state (returns a new context)
   */
  updateWorldState(updater: (state: WorldState) => WorldState): GameContext {
    const newState = updater(this.worldState);

    return new GameContextImpl(
      newState,
      this.player.id,
      this.entityManager,
      this.textService,
      this.languageProvider,
      this.commandRouter
    );
  }

  /**
   * Find the player's current location
   */
  private findPlayerLocation(player: Entity): EntityId {
    // Check all entities to find which one contains the player
    for (const entity of Object.values(this.worldState.entities)) {
      const contents = entity.relationships[RelationshipType.CONTAINS] || [];
      if (contents.includes(player.id)) {
        return entity.id;
      }
    }

    // If player location not found, use the first location
    const locations = this.getEntitiesByType('location');
    if (locations.length > 0) {
      return locations[0].id;
    }

    throw new Error('No valid location found for player');
  }
}

/**
 * Create a new game context
 */
export function createGameContext(
  worldState: WorldState,
  playerId: EntityId,
  entityManager: EntityManager,
  textService?: TextService,
  languageProvider?: LanguageProvider,
  commandRouter?: CommandRouter
): GameContext {
  // Get language provider
  const provider = languageProvider || getActiveLanguageProvider();

  // Create text service if needed
  const textSvc = textService || createTextService(provider);

  // Create command router if not provided
  const router = commandRouter || createCommandRouter(textSvc);

  return new GameContextImpl(
    worldState,
    playerId,
    entityManager,
    textSvc,
    provider,
    router
  );
}

