// packages/core/src/execution/command-router.ts

import { ParsedCommand } from '../parser/core/types';
import { CommandHandler, CommandResult, GameContext, CommandRouter, CommandExecutionOptions } from './types';
import { createEvent } from '../events/event-system';
import { StandardEventTypes } from '../events/standard-events';
import { TextService, createTextService } from '../events/text-processor';
import { RuleSystem, createRuleSystem } from '../rules';

/**
 * Implementation of the CommandRouter interface
 */
export class CommandRouterImpl implements CommandRouter {
  private handlers: CommandHandler[] = [];
  private textService: TextService;
  private ruleSystem?: RuleSystem;

  /**
   * Create a new command router
   */
  constructor(textService?: TextService, ruleSystem?: RuleSystem) {
    this.textService = textService || createTextService();
    this.ruleSystem = ruleSystem;
  }

  /**
   * Register a command handler
   */
  public registerHandler(handler: CommandHandler): void {
    this.handlers.push(handler);
  }

  /**
   * Unregister a command handler
   */
  public unregisterHandler(handler: CommandHandler): void {
    const index = this.handlers.indexOf(handler);
    if (index !== -1) {
      this.handlers.splice(index, 1);
    }
  }

  /**
   * Get a handler for the command
   */
  public getHandler(command: ParsedCommand, context: GameContext): CommandHandler | undefined {
    return this.handlers.find(handler => handler.canHandle(command, context));
  }

  /**
   * Route and execute a command
   */
  public async execute(
    command: ParsedCommand, 
    context: GameContext,
    options: CommandExecutionOptions = {}
  ): Promise<CommandResult> {
    // Get handler for the command
    const handler = this.getHandler(command, context);

    // If no handler is found, return a command not understood result
    if (!handler) {
      const notUnderstoodEvent = createEvent(
        StandardEventTypes.COMMAND_NOT_UNDERSTOOD,
        { command: command.originalText },
        { narrate: true }
      );

      return {
        success: false,
        events: [notUnderstoodEvent],
        error: 'commandNotUnderstood'
      };
    }

    // Validate the command unless validation is skipped
    if (!options.skipValidation && handler.validate) {
      const validationResult = handler.validate(command, context);

      if (!validationResult.valid) {
        const validationFailedEvent = createEvent(
          StandardEventTypes.COMMAND_VALIDATION_FAILED,
          { 
            command: command.originalText,
            reason: validationResult.error || 'unknown',
            itemName: command.directObject
          },
          { narrate: true }
        );

        return {
          success: false,
          events: [validationFailedEvent],
          error: validationResult.error || 'validationFailed'
        };
      }
    }

    // Custom validation if provided
    if (options.customValidation) {
      const customValidationResult = options.customValidation(command, context);

      if (!customValidationResult.valid) {
        const validationFailedEvent = createEvent(
          StandardEventTypes.COMMAND_VALIDATION_FAILED,
          { 
            command: command.originalText,
            reason: customValidationResult.error || 'unknown',
            itemName: command.directObject
          },
          { narrate: true }
        );

        return {
          success: false,
          events: [validationFailedEvent],
          error: customValidationResult.error || 'validationFailed'
        };
      }
    }

    // Run pre-execution hooks if provided
    if (options.preExecute) {
      for (const hook of options.preExecute) {
        await hook(command, context);
      }
    }

    // Create a command-started event for rule processing
    const commandStartedEvent = createEvent(
      `command:${command.verb}:started`,
      {
        command: command.originalText,
        verb: command.verb,
        directObject: command.directObject,
        indirectObject: command.indirectObject
      },
      {
        actor: context.player.id,
        location: context.currentLocation.id,
        narrate: false
      }
    );

    // Process the command through rules if rule system is available
    if (this.ruleSystem) {
      const ruleResult = this.ruleSystem.processEvent(commandStartedEvent, context);

      // If a rule prevented the command, return the result
      if (ruleResult.prevented) {
        return {
          success: false,
          events: ruleResult.events,
          error: 'preventedByRule',
          metadata: {
            preventMessage: ruleResult.preventMessage
          }
        };
      }

      // If rules added events, use the updated context
      if (ruleResult.events.length > 0) {
        context = ruleResult.context;
      }
    }

    // Execute the command
    try {
      const result = await Promise.resolve(handler.execute(command, context));

      // Process the command result through rules if rule system is available
      if (this.ruleSystem && result.success) {
        // Create a command-succeeded event for rule processing
        const commandSucceededEvent = createEvent(
          `command:${command.verb}:succeeded`,
          {
            command: command.originalText,
            verb: command.verb,
            directObject: command.directObject,
            indirectObject: command.indirectObject,
            result: result
          },
          {
            actor: context.player.id,
            location: context.currentLocation.id,
            narrate: false
          }
        );

        // Process through rules
        const afterRuleResult = this.ruleSystem.processEvent(commandSucceededEvent, context);

        // Add any events from rules
        result.events = [...result.events, ...afterRuleResult.events];

        // Update context if needed
        if (afterRuleResult.context !== context) {
          result.metadata = {
            ...result.metadata,
            updatedContext: afterRuleResult.context
          };
        }
      } else if (this.ruleSystem && !result.success) {
        // Create a command-failed event for rule processing
        const commandFailedEvent = createEvent(
          `command:${command.verb}:failed`,
          {
            command: command.originalText,
            verb: command.verb,
            directObject: command.directObject,
            indirectObject: command.indirectObject,
            error: result.error
          },
          {
            actor: context.player.id,
            location: context.currentLocation.id,
            narrate: false
          }
        );

        // Process through rules
        const failedRuleResult = this.ruleSystem.processEvent(commandFailedEvent, context);

        // Add any events from rules
        result.events = [...result.events, ...failedRuleResult.events];

        // Update context if needed
        if (failedRuleResult.context !== context) {
          result.metadata = {
            ...result.metadata,
            updatedContext: failedRuleResult.context
          };
        }
      }

      // Add command executed event (for logging purposes)
      if (result.success) {
        const commandEvent = createEvent(
          StandardEventTypes.COMMAND_EXECUTED,
          { command: command.originalText },
          { narrate: false }
        );

        result.events.push(commandEvent);
      }

      // Run post-execution hooks if provided
      if (options.postExecute) {
        let modifiedResult = result;

        for (const hook of options.postExecute) {
          modifiedResult = await Promise.resolve(hook(modifiedResult, command, context));
        }

        return modifiedResult;
      }

      return result;
    } catch (error) {
      // Handle execution errors
      console.error('Error executing command:', error);

      const errorEvent = createEvent(
        StandardEventTypes.COMMAND_EXECUTION_ERROR,
        { 
          command: command.originalText,
          error: error instanceof Error ? error.message : String(error)
        },
        { narrate: true }
      );

      return {
        success: false,
        events: [errorEvent],
        error: 'executionError'
      };
    }
  }

  /**
   * Process the result of command execution and return text output
   */
  public processResult(result: CommandResult): string {
    return this.textService.processEvents(result.events);
  }

  /**
   * Set the rule system
   */
  public setRuleSystem(ruleSystem: RuleSystem): void {
    this.ruleSystem = ruleSystem;
  }

  /**
   * Get the rule system
   */
  public getRuleSystem(): RuleSystem | undefined {
    return this.ruleSystem;
  }
}

/**
 * Create a new command router
 */
export function createCommandRouter(textService?: TextService, ruleSystem?: RuleSystem): CommandRouter {
  return new CommandRouterImpl(textService, ruleSystem || createRuleSystem());
}

