// packages/core/src/execution/command-handler.ts

import { ParsedCommand } from '../parser/core/types';
import { 
  CommandHandler, 
  CommandResult, 
  GameContext,
  CommandHandlerFactory 
} from './types';
import { createEvent } from '../events/event-system';
import { StandardEventTypes } from '../events/standard-events';
import { LanguageProvider, getActiveLanguageProvider, VerbDefinition } from '../language';

/**
 * Base class for command handlers
 */
export abstract class BaseCommandHandler implements CommandHandler {
  /**
   * The verb or verbs that this handler can process
   */
  public readonly verbs: string[];

  /**
   * The canonical verb this handler is primarily responsible for
   */
  public readonly canonicalVerb?: string;

  /**
   * The verb definition if loaded from a language provider
   */
  public readonly verbDefinition?: VerbDefinition;

  /**
   * Create a new command handler
   * @param verbs The verb or verbs this handler can process
   * @param languageProvider Optional language provider to use for verb definitions
   */
  constructor(verbs: string | string[], languageProvider?: LanguageProvider) {
    // Store raw verb list
    this.verbs = Array.isArray(verbs) ? verbs : [verbs];

    // If we have a language provider and a canonical verb
    if (languageProvider && this.verbs.length > 0) {
      // Try to get the canonical verb (first in the list)
      this.canonicalVerb = this.verbs[0];

      // Try to get the verb definition
      this.verbDefinition = languageProvider.getVerbs().get(this.canonicalVerb);

      // If we have a verb definition, update the verb list to include all synonyms
      if (this.verbDefinition) {
        // Start with the canonical form
        const allVerbs = [this.verbDefinition.canonical];

        // Add all synonyms
        allVerbs.push(...this.verbDefinition.synonyms);

        // Update the verbs array with the complete list
        this.verbs = allVerbs;
      }
    }
  }

  /**
   * Check if this handler can handle the given command
   */
  public canHandle(command: ParsedCommand, context: GameContext): boolean {
    // If we have a context with a language provider, check for canonical forms
    if (context.languageProvider) {
      // Get the canonical form of the command verb
      const canonicalVerb = context.languageProvider.getCanonicalVerb(command.verb);

      // If we have a canonical verb, check if we handle it
      if (canonicalVerb) {
        // Check if we handle the canonical form
        for (const verb of this.verbs) {
          // If we handle this verb or its canonical form, we can handle the command
          if (verb === canonicalVerb || 
              context.languageProvider.getCanonicalVerb(verb) === canonicalVerb) {
            return true;
          }
        }
      }
    }

    // Fall back to direct verb matching
    return this.verbs.includes(command.verb) || this.verbs.includes('*');
  }

  /**
   * Execute the command
   */
  public abstract execute(command: ParsedCommand, context: GameContext): Promise<CommandResult> | CommandResult;

  /**
   * Validate the command before execution
   */
  public validate?(command: ParsedCommand, context: GameContext): { valid: boolean; error?: string };

  /**
   * Helper function to create a successful result
   */
  protected createSuccessResult(events: CommandResult['events'] = [], metadata?: Record<string, unknown>): CommandResult {
    return {
      success: true,
      events,
      metadata
    };
  }

  /**
   * Helper function to create a failure result
   */
  protected createFailureResult(reason: string, itemName?: string, events: CommandResult['events'] = [], metadata?: Record<string, unknown>): CommandResult {
    return {
      success: false,
      events: [
        ...events,
        createEvent(StandardEventTypes.COMMAND_FAILED, { reason, itemName }, { narrate: true })
      ],
      error: reason,
      metadata
    };
  }
}

/**
 * Factory for creating command handlers
 */
export class CommandHandlerFactoryImpl implements CommandHandlerFactory {
  private languageProvider?: LanguageProvider;

  /**
   * Create a command handler factory
   * @param languageProvider Optional language provider to use for verb definitions
   */
  constructor(languageProvider?: LanguageProvider) {
    this.languageProvider = languageProvider || getActiveLanguageProvider();
  }

  /**
   * Create a standard command handler
   */
  public createHandler(config: {
    verbs: string[];
    canHandle?: (command: ParsedCommand, context: GameContext) => boolean;
    execute: (command: ParsedCommand, context: GameContext) => Promise<CommandResult> | CommandResult;
    validate?: (command: ParsedCommand, context: GameContext) => { valid: boolean; error?: string };
  }): CommandHandler {
    // If we have a language provider, expand the verb list
    let expandedVerbs = [...config.verbs];

    if (this.languageProvider && config.verbs.length > 0) {
      // Try to get the canonical verb (first in the list)
      const canonicalVerb = config.verbs[0];

      // Try to get the verb definition
      const verbDefinition = this.languageProvider.getVerbs().get(canonicalVerb);

      // If we have a verb definition, update the verb list to include all synonyms
      if (verbDefinition) {
        // Start with the canonical form
        expandedVerbs = [verbDefinition.canonical];

        // Add all synonyms
        expandedVerbs.push(...verbDefinition.synonyms);
      }
    }

    return {
      verbs: expandedVerbs,
      canHandle: config.canHandle || ((command, context) => {
        // If we have a context with a language provider, check for canonical forms
        if (context.languageProvider) {
          // Get the canonical form of the command verb
          const canonicalVerb = context.languageProvider.getCanonicalVerb(command.verb);

          // If we have a canonical verb, check if we handle it
          if (canonicalVerb) {
            // Check if we handle the canonical form
            return expandedVerbs.some(verb => 
              verb === canonicalVerb || 
              context.languageProvider.getCanonicalVerb(verb) === canonicalVerb
            );
          }
        }

        // Fall back to direct verb matching
        return expandedVerbs.includes(command.verb) || expandedVerbs.includes('*');
      }),
      execute: config.execute,
      validate: config.validate
    };
  }
}

/**
 * Create a new command handler factory
 * @param languageProvider Optional language provider to use for verb definitions
 */
export function createCommandHandlerFactory(languageProvider?: LanguageProvider): CommandHandlerFactory {
  return new CommandHandlerFactoryImpl(languageProvider);
}

