#!/usr/bin/env node

const { spawn } = require('child_process');
const fs = require('fs');
const path = require('path');

// Configuration
const WATCH_DIR = 'packages/core/src';
const ERROR_FILE = 'core-errors.json';
const DEBOUNCE_MS = 1000;

// Colors for console output
const colors = {
  reset: '\x1b[0m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  magenta: '\x1b[35m'
};

let timeout = null;
let isCompiling = false;

function log(message, color = 'reset') {
  console.log(`${colors[color]}${message}${colors.reset}`);
}

function runTypeScriptCheck() {
  if (isCompiling) return;

  isCompiling = true;
  log('\nðŸ” Running TypeScript check...', 'blue');

  const tsc = spawn('npx', ['tsc', '--project', './tsconfig.json', '--noEmit'], {
    cwd: 'packages/core',
    shell: true
  });

  let output = '';
  let errorOutput = '';

  tsc.stdout.on('data', (data) => {
    output += data.toString();
  });

  tsc.stderr.on('data', (data) => {
    errorOutput += data.toString();
  });

  tsc.on('close', (code) => {
    isCompiling = false;

    // Parse TypeScript errors
    const allOutput = output + errorOutput;
    const lines = allOutput.split('\n');
    const errors = [];

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];

      // Skip archive files
      if (line.includes('archive/') || line.includes('\\archive\\')) {
        continue;
      }

      // Match TypeScript error format: file(line,col): error TS####: message
      const match = line.match(/(.+)\((\d+),(\d+)\):\s+error\s+(TS\d+):\s+(.+)/);
      if (match) {
        const [, file, line, column, code, message] = match;
        errors.push({
          file: file.trim(),
          line: parseInt(line),
          column: parseInt(column),
          code,
          message: message.trim()
        });
      }
    }

    // Write errors to file
    fs.writeFileSync(ERROR_FILE, JSON.stringify(errors, null, 2));

    // Display summary
    if (errors.length === 0) {
      log('\nâœ… No errors found!', 'green');
    } else {
      log(`\nâŒ Found ${errors.length} errors`, 'red');

      // Group errors by file
      const errorsByFile = {};
      errors.forEach(error => {
        const shortFile = error.file.replace(/.*[\/\\]packages[\/\\]core[\/\\]src[\/\\]/, '');
        if (!errorsByFile[shortFile]) {
          errorsByFile[shortFile] = [];
        }
        errorsByFile[shortFile].push(error);
      });

      // Display errors by file
      Object.entries(errorsByFile).forEach(([file, fileErrors]) => {
        log(`\nðŸ“„ ${file}:`, 'yellow');
        fileErrors.forEach(error => {
          console.log(`   Line ${error.line}: ${error.code} - ${error.message}`);
        });
      });

      log(`\nðŸ’¾ Full error list saved to ${ERROR_FILE}`, 'magenta');
    }

    log('\nðŸ‘€ Watching for changes...', 'blue');
  });
}

function watchFiles() {
  log('ðŸ‘€ Starting TypeScript watcher for Core package...', 'blue');
  log(`ðŸ“ Watching: ${WATCH_DIR}`, 'blue');

  // Initial check
  runTypeScriptCheck();

  // Watch for changes
  fs.watch(WATCH_DIR, { recursive: true }, (eventType, filename) => {
    // Skip non-TypeScript files
    if (!filename || !filename.endsWith('.ts')) return;

    // Skip test files
    if (filename.includes('.test.') || filename.includes('.spec.')) return;

    log(`\nðŸ“ Change detected: ${filename}`, 'yellow');

    // Debounce to avoid multiple rapid compilations
    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(() => {
      runTypeScriptCheck();
    }, DEBOUNCE_MS);
  });
}

// Handle exit gracefully
process.on('SIGINT', () => {
  log('\n\nðŸ‘‹ Stopping watcher...', 'yellow');
  process.exit(0);
});

// Start watching
watchFiles();

# TypeScript Watcher for Core Package
param(
    [int]$CheckInterval = 2
)

$ErrorFile = "core-errors.json"
$CorePath = "packages\core"
$LastWriteTime = @{}

Write-Host "ðŸ‘€ Starting TypeScript watcher for Core package..." -ForegroundColor Blue
Write-Host "ðŸ“ Watching: $CorePath\src" -ForegroundColor Blue
Write-Host "â±ï¸  Check interval: $CheckInterval seconds" -ForegroundColor Blue
Write-Host "Press Ctrl+C to stop" -ForegroundColor Yellow
Write-Host ""

function Get-FileHash {
    param($Path)
    $files = Get-ChildItem -Path $Path -Filter "*.ts" -Recurse | Where-Object { 
        $_.FullName -notmatch "test\." -and 
        $_.FullName -notmatch "spec\." -and
        $_.FullName -notmatch "\\archive\\"
    }

    $hash = ""
    foreach ($file in $files) {
        $hash += "$($file.FullName):$($file.LastWriteTime);"
    }
    return $hash
}

function Run-TypeScriptCheck {
    Write-Host "`nðŸ” Running TypeScript check..." -ForegroundColor Blue

    Push-Location $CorePath

    # Run TypeScript compiler
    $output = & npx tsc --project ./tsconfig.json --noEmit 2>&1 | Out-String

    Pop-Location

    # Parse errors
    $errors = @()
    $lines = $output -split "`n"

    foreach ($line in $lines) {
        # Skip archive files
        if ($line -match "archive[/\\]") { continue }

        # Match TypeScript error format
        if ($line -match "(.+)\((\d+),(\d+)\):\s+error\s+(TS\d+):\s+(.+)") {
            $errors += @{
                file = $matches[1].Trim()
                line = [int]$matches[2]
                column = [int]$matches[3]
                code = $matches[4]
                message = $matches[5].Trim()
            }
        }
    }

    # Save errors to JSON
    $errors | ConvertTo-Json -Depth 10 | Out-File -FilePath $ErrorFile -Encoding UTF8

    # Display summary
    if ($errors.Count -eq 0) {
        Write-Host "`nâœ… No errors found!" -ForegroundColor Green
    }
    else {
        Write-Host "`nâŒ Found $($errors.Count) errors" -ForegroundColor Red

        # Group by file
        $errorsByFile = $errors | Group-Object -Property { 
            $_.file -replace ".*[/\\]packages[/\\]core[/\\]src[/\\]", ""
        }

        foreach ($group in $errorsByFile) {
            Write-Host "`nðŸ“„ $($group.Name):" -ForegroundColor Yellow
            foreach ($error in $group.Group) {
                Write-Host "   Line $($error.line): $($error.code) - $($error.message)"
            }
        }

        Write-Host "`nðŸ’¾ Full error list saved to $ErrorFile" -ForegroundColor Magenta
    }
}

# Initial check
$currentHash = Get-FileHash -Path "$CorePath\src"
Run-TypeScriptCheck

# Watch loop
while ($true) {
    Start-Sleep -Seconds $CheckInterval

    $newHash = Get-FileHash -Path "$CorePath\src"
    if ($newHash -ne $currentHash) {
        Write-Host "`nðŸ“ Changes detected!" -ForegroundColor Yellow
        $currentHash = $newHash
        Run-TypeScriptCheck
        Write-Host "`nðŸ‘€ Watching for changes..." -ForegroundColor Blue
    }
}

#!/usr/bin/env node

const fs = require('fs');
const path = require('path');

const ERROR_FILE = 'core-errors.json';

function analyzeErrors() {
  if (!fs.existsSync(ERROR_FILE)) {
    console.log('No error file found. Run the watcher first.');
    return;
  }

  const errors = JSON.parse(fs.readFileSync(ERROR_FILE, 'utf8'));

  if (errors.length === 0) {
    console.log('âœ… No errors to fix!');
    return;
  }

  // Group errors by type
  const errorTypes = {
    moduleNotFound: [],
    propertyDoesNotExist: [],
    typeImplicit: [],
    argumentMismatch: [],
    other: []
  };

  errors.forEach(error => {
    if (error.code === 'TS2307' || error.message.includes('Cannot find module')) {
      errorTypes.moduleNotFound.push(error);
    } else if (error.code === 'TS2339' || error.message.includes('does not exist on type')) {
      errorTypes.propertyDoesNotExist.push(error);
    } else if (error.code === 'TS7006' || error.message.includes('implicitly has')) {
      errorTypes.typeImplicit.push(error);
    } else if (error.code === 'TS2554' || error.message.includes('Expected')) {
      errorTypes.argumentMismatch.push(error);
    } else {
      errorTypes.other.push(error);
    }
  });

  console.log('\nðŸ“Š Error Analysis:');
  console.log('==================');

  // Module not found errors
  if (errorTypes.moduleNotFound.length > 0) {
    console.log(`\nðŸ” Module Not Found (${errorTypes.moduleNotFound.length}):`);
    const modules = new Set();
    errorTypes.moduleNotFound.forEach(error => {
      const match = error.message.match(/Cannot find module '(.+)'/);
      if (match) modules.add(match[1]);
    });
    modules.forEach(module => {
      console.log(`   - ${module}`);
    });
    console.log('\n   ðŸ’¡ Fix: These modules were likely moved to stdlib. Update imports or remove dependencies.');
  }

  // Property does not exist errors
  if (errorTypes.propertyDoesNotExist.length > 0) {
    console.log(`\nðŸ·ï¸  Property Does Not Exist (${errorTypes.propertyDoesNotExist.length}):`);
    const properties = new Map();
    errorTypes.propertyDoesNotExist.forEach(error => {
      const match = error.message.match(/Property '(.+)' does not exist on type '(.+)'/);
      if (match) {
        const [, prop, type] = match;
        if (!properties.has(type)) properties.set(type, new Set());
        properties.get(type).add(prop);
      }
    });
    properties.forEach((props, type) => {
      console.log(`   On type '${type}':`);
      props.forEach(prop => console.log(`     - ${prop}`));
    });
    console.log('\n   ðŸ’¡ Fix: These are IF-specific properties that have moved to stdlib.');
  }

  // Type implicit errors
  if (errorTypes.typeImplicit.length > 0) {
    console.log(`\nðŸŽ¯ Implicit Type Errors (${errorTypes.typeImplicit.length}):`);
    console.log('   These parameters need explicit types.');
    console.log('\n   ðŸ’¡ Fix: Add type annotations or use generic types.');
  }

  // Argument mismatch errors
  if (errorTypes.argumentMismatch.length > 0) {
    console.log(`\nðŸ“ Argument Mismatch (${errorTypes.argumentMismatch.length}):`);
    errorTypes.argumentMismatch.forEach(error => {
      const match = error.message.match(/Expected (\d+)(?:-(\d+))? arguments, but got (\d+)/);
      if (match) {
        console.log(`   - Expected ${match[1]}${match[2] ? '-' + match[2] : ''} args, got ${match[3]}`);
      }
    });
    console.log('\n   ðŸ’¡ Fix: Update function calls to match new signatures.');
  }

  // Other errors
  if (errorTypes.other.length > 0) {
    console.log(`\nâ“ Other Errors (${errorTypes.other.length}):`);
    errorTypes.other.forEach(error => {
      console.log(`   - ${error.code}: ${error.message.substring(0, 60)}...`);
    });
  }

  console.log('\n\nðŸ“‹ Next Steps:');
  console.log('1. Fix module imports (update to stdlib or remove)');
  console.log('2. Remove IF-specific code from core');
  console.log('3. Add type annotations where needed');
  console.log('4. Update function signatures');

  // Save analysis
  const analysis = {
    timestamp: new Date().toISOString(),
    totalErrors: errors.length,
    errorTypes,
    suggestedFixes: {
      moduleNotFound: 'Move imports to stdlib or remove dependencies',
      propertyDoesNotExist: 'Remove IF-specific properties from core',
      typeImplicit: 'Add explicit type annotations',
      argumentMismatch: 'Update function calls to match signatures'
    }
  };

  fs.writeFileSync('core-error-analysis.json', JSON.stringify(analysis, null, 2));
  console.log('\nðŸ’¾ Detailed analysis saved to core-error-analysis.json');
}

// Run analysis
analyzeErrors();

