/**
 * Enhanced text processor with custom message support
 */

import { SemanticEvent } from './types';
import { TextService } from './text-processor';
import { MessageRegistry, createMessageRegistry } from './message-resolver';
import { GameContext } from '../execution/types';
import { LanguageProvider, getActiveLanguageProvider } from '../language';
import { processEvent } from './text-processor';

/**
 * Enhanced text service that supports custom message resolvers
 */
export interface EnhancedTextService extends TextService {
  /**
   * Get the message registry
   */
  getMessageRegistry(): MessageRegistry;

  /**
   * Process a single event with context
   */
  processEvent(event: SemanticEvent, context?: GameContext): string;
}

/**
 * Implementation of enhanced text service
 */
export class EnhancedTextProcessor implements EnhancedTextService {
  private languageProvider: LanguageProvider;
  private messageRegistry: MessageRegistry;

  constructor(
    languageProvider?: LanguageProvider,
    messageRegistry?: MessageRegistry
  ) {
    this.languageProvider = languageProvider || getActiveLanguageProvider();
    this.messageRegistry = messageRegistry || createMessageRegistry();
  }

  /**
   * Process a single event with context
   */
  processEvent(event: SemanticEvent, context?: GameContext): string {
    // Skip non-narrative events
    if (event.metadata?.narrate === false) {
      return '';
    }

    // First check custom resolvers
    const resolvers = this.messageRegistry.getResolvers(event.type);
    for (const resolver of resolvers) {
      const message = resolver.resolve(event, context);
      if (message !== undefined) {
        return this.interpolate(message, event, context);
      }
    }

    // Fall back to default processing
    return processEvent(event, this.languageProvider);
  }

  /**
   * Process a list of events into text output
   */
  processEvents(events: SemanticEvent[], context?: GameContext): string {
    const textParts = events
      .filter(event => event.metadata?.narrate !== false)
      .map(event => this.processEvent(event, context))
      .filter(text => text.length > 0);

    return textParts.join('\n');
  }

  /**
   * Interpolate variables in a message template
   */
  private interpolate(template: string, event: SemanticEvent, context?: GameContext): string {
    return template.replace(/\{([^}]+)\}/g, (match, path) => {
      // Split the path (e.g., "item.name" or "event.data.itemName")
      const parts = path.split('.');

      // Handle different path prefixes
      if (parts[0] === 'event' && parts[1] === 'data') {
        // Access event data
        const value = this.getNestedValue(event.data, parts.slice(2));
        return value !== undefined ? String(value) : match;
      }

      if (parts[0] === 'item' && context) {
        // Access item entity
        const itemId = event.data?.itemId || event.data?.target;
        if (itemId) {
          const item = context.getEntity(itemId);
          if (item) {
            const value = this.getNestedValue(item, parts.slice(1));
            return value !== undefined ? String(value) : match;
          }
        }
      }

      if (parts[0] === 'actor' && context) {
        // Access actor entity
        const actorId = event.metadata?.actor || event.data?.actor;
        if (actorId) {
          const actor = context.getEntity(actorId);
          if (actor) {
            const value = this.getNestedValue(actor, parts.slice(1));
            return value !== undefined ? String(value) : match;
          }
        }
      }

      if (parts[0] === 'location' && context) {
        // Access current location
        const value = this.getNestedValue(context.currentLocation, parts.slice(1));
        return value !== undefined ? String(value) : match;
      }

      // Direct event data access (backward compatibility)
      const value = this.getNestedValue(event.data, parts);
      return value !== undefined ? String(value) : match;
    });
  }

  /**
   * Get a nested value from an object using a path array
   */
  private getNestedValue(obj: any, path: string[]): any {
    let current = obj;
    for (const key of path) {
      if (current && typeof current === 'object' && key in current) {
        current = current[key];
      } else {
        return undefined;
      }
    }
    return current;
  }

  /**
   * Format a message using a template
   */
  formatMessage(templateKey: string, ...params: any[]): string {
    return this.languageProvider.formatMessage(templateKey, ...params);
  }

  /**
   * Format a list of items
   */
  formatList(headerTemplate: string, itemTemplate: string, headerParams: any[], items: any[]): string {
    return this.languageProvider.formatList(headerTemplate, itemTemplate, headerParams, items);
  }

  /**
   * Set the language provider
   */
  setLanguageProvider(provider: LanguageProvider): void {
    this.languageProvider = provider;
  }

  /**
   * Get the language provider
   */
  getLanguageProvider(): LanguageProvider {
    return this.languageProvider;
  }

  /**
   * Get the message registry
   */
  getMessageRegistry(): MessageRegistry {
    return this.messageRegistry;
  }
}

/**
 * Create an enhanced text service
 */
export function createEnhancedTextService(
  languageProvider?: LanguageProvider,
  messageRegistry?: MessageRegistry
): EnhancedTextService {
  return new EnhancedTextProcessor(languageProvider, messageRegistry);
}

