// packages/core/src/index.ts
// Core exports for Sharpee

// Export version
export const version = '0.1.0';

// Re-export core types
export * from './types';

// World model has been moved to stdlib
// Import from @sharpee/stdlib for world model functionality

// Parser has been moved to stdlib
// Import from @sharpee/stdlib for parser functionality

// Re-export from execution module
