/**
 * Helper functions for common rule patterns
 */

import { SemanticEvent, EntityId } from '../events/types';
import { RuleWorld, EntityChange } from './types';

/**
 * Helper to get the target item from an event
 */
export function getTargetItem(event: SemanticEvent, world: RuleWorld) {
  const targetId = event.entities.target || event.payload?.itemId;
  return targetId ? world.getEntity(targetId) : undefined;
}

/**
 * Helper to get the actor from an event
 */
export function getActor(event: SemanticEvent, world: RuleWorld) {
  const actorId = event.entities.actor;
  return actorId ? world.getEntity(actorId) : undefined;
}

/**
 * Helper to check if entity has a specific name/id
 */
export function entityIs(entity: any, nameOrId: string): boolean {
  return entity?.id === nameOrId || entity?.attributes?.name === nameOrId;
}

/**
 * Helper to get entity attribute value
 */
export function getAttribute(entity: any, attribute: string): any {
  return entity?.attributes?.[attribute];
}

/**
 * Helper to check if entity has an ability
 */
export function hasAbility(entity: any, ability: string): boolean {
  return entity?.attributes?.[`ability_${ability}`] === true;
}

/**
 * Helper to create an entity change that gives an ability
 */
export function giveAbility(entityId: EntityId, ability: string): EntityChange {
  return {
    entityId,
    attribute: `ability_${ability}`,
    value: true
  };
}

/**
 * Helper to create an entity change that removes an ability
 */
export function removeAbility(entityId: EntityId, ability: string): EntityChange {
  return {
    entityId,
    attribute: `ability_${ability}`,
    value: undefined
  };
}

/**
 * Helper to create an entity change that sets an attribute
 */
export function setAttribute(entityId: EntityId, attribute: string, value: any): EntityChange {
  return {
    entityId,
    attribute,
    value
  };
}

/**
 * Common condition: item weight vs player strength
 */
export function itemTooHeavy(event: SemanticEvent, world: RuleWorld): boolean {
  const item = getTargetItem(event, world);
  const player = world.getPlayer();

  const itemWeight = getAttribute(item, 'weight') || 0;
  const playerStrength = getAttribute(player, 'strength') || 10;

  return itemWeight > playerStrength;
}

/**
 * Common condition: check if taking a specific item
 */
export function isTaking(itemNameOrId: string) {
  return (event: SemanticEvent, world: RuleWorld): boolean => {
    const item = getTargetItem(event, world);
    return entityIs(item, itemNameOrId);
  };
}

/**
 * Common condition: player has specific ability
 */
export function playerHasAbility(ability: string) {
  return (event: SemanticEvent, world: RuleWorld): boolean => {
    const player = world.getPlayer();
    return hasAbility(player, ability);
  };
}

/**
 * Simple Rule System v2 - Clean, functional design
 */

import { SemanticEvent, EntityId } from '../events/types';

/**
 * Simple world interface for rules - no complex abstractions
 */
export interface RuleWorld {
  getEntity(id: EntityId): any;
  updateEntity(id: EntityId, changes: Record<string, any>): void;
  getPlayer(): any;
  getCurrentLocation(): any;
}

/**
 * Result of executing a rule
 */
export interface RuleResult {
  /** Prevent the original action from happening */
  prevent?: boolean;
  /** Message to display to the player */
  message?: string;
  /** Additional events to generate */
  events?: SemanticEvent[];
  /** Entity changes to apply */
  changes?: EntityChange[];
}

/**
 * A change to apply to an entity
 */
export interface EntityChange {
  entityId: EntityId;
  attribute: string;
  value: any;
}

/**
 * A simple rule definition
 */
export interface Rule {
  /** Unique identifier for the rule */
  id: string;
  /** Event type this rule responds to (e.g., 'item:taking') */
  eventType: string;
  /** Optional condition - if false, rule doesn't fire */
  condition?: (event: SemanticEvent, world: RuleWorld) => boolean;
  /** Action to take when rule fires */
  action: (event: SemanticEvent, world: RuleWorld) => RuleResult;
  /** Priority (higher = runs first) */
  priority?: number;
}

/**
 * Simple rule system interface
 */
export interface SimpleRuleSystem {
  /** Add a rule */
  addRule(rule: Rule): void;
  /** Remove a rule */
  removeRule(ruleId: string): void;
  /** Process an event through all matching rules */
  processEvent(event: SemanticEvent, world: RuleWorld): RuleResult;
  /** Get all rules */
  getRules(): Rule[];
}

// packages/core/src/events/types.ts

/**
 * Generic entity ID type
 */
export type EntityId = string;

/**
 * Represents a semantic event in the system
 */
export interface SemanticEvent {
  /**
   * Unique identifier for this event
   */
  id: string;

  /**
   * The type of event
   */
  type: string;

  /**
   * Timestamp when the event was created
   */
  timestamp: number;

  /**
   * Entity IDs relevant to this event
   */
  entities: {
    /**
     * The entity that initiated the event (often the player)
     */
    actor?: EntityId;

    /**
     * The primary entity that the event affects
     */
    target?: EntityId;

    /**
     * A secondary entity involved in the event
     */
    instrument?: EntityId;

    /**
     * A location where the event occurred
     */
    location?: EntityId;

    /**
     * Other relevant entities
     */
    others?: EntityId[];
  };

  /**
   * Additional data related to the event
   */
  payload?: Record<string, unknown>;

  /**
   * Tags for categorizing and filtering events
   */
  tags?: string[];

  /**
   * Priority of the event (higher numbers are more important)
   */
  priority?: number;

  /**
   * Whether this event should be narrated
   */
  narrate?: boolean;

  /**
   * Legacy support for data property (same as payload)
   */
  data?: Record<string, unknown>;

  /**
   * Legacy support for metadata property
   */
  metadata?: Record<string, unknown>;
}

/**
 * Interface for event source that collects and manages events
 */
export interface EventSource {
  /**
   * Add an event to the source
   */
  addEvent: (event: SemanticEvent) => void;

  /**
   * Get all events in the source
   */
  getAllEvents: () => SemanticEvent[];

  /**
   * Get events of a specific type
   */
  getEventsByType: (type: string) => SemanticEvent[];

  /**
   * Get events involving a specific entity
   */
  getEventsByEntity: (entityId: EntityId) => SemanticEvent[];

  /**
   * Get events with a specific tag
   */
  getEventsByTag: (tag: string) => SemanticEvent[];

  /**
   * Clear all events
   */
  clearEvents: () => void;

  /**
   * Apply a filter to the events
   */
  filter: (predicate: (event: SemanticEvent) => boolean) => SemanticEvent[];

  /**
   * Get the event emitter associated with this source
   */
  getEmitter: () => EventEmitter;
}

/**
 * Event listener for semantic events
 */
export type EventListener = (event: SemanticEvent) => void;

/**
 * Event emitter for semantic events
 */
export interface EventEmitter {
  /**
   * Add an event listener for a specific event type
   * Use '*' to listen to all events
   */
  on: (type: string, listener: EventListener) => void;

  /**
   * Remove an event listener
   */
  off: (type: string, listener: EventListener) => void;

  /**
   * Emit an event
   */
  emit: (event: SemanticEvent) => void;
}

/**
 * Configuration options for the event system
 */
export interface EventSystemOptions {
  /**
   * Maximum number of events to store in memory
   */
  maxEvents?: number;

  /**
   * Whether to emit events immediately when added
   */
  emitOnAdd?: boolean;

  /**
   * Custom filter for events that should be emitted
   */
  emitFilter?: (event: SemanticEvent) => boolean;
}

