// packages/core/src/events/types.ts

/**
 * Generic entity ID type
 */
export type EntityId = string;

/**
 * Represents a semantic event in the system
 */
export interface SemanticEvent {
  /**
   * Unique identifier for this event
   */
  id: string;

  /**
   * The type of event
   */
  type: string;

  /**
   * Timestamp when the event was created
   */
  timestamp: number;

  /**
   * Entity IDs relevant to this event
   */
  entities: {
    /**
     * The entity that initiated the event (often the player)
     */
    actor?: EntityId;

    /**
     * The primary entity that the event affects
     */
    target?: EntityId;

    /**
     * A secondary entity involved in the event
     */
    instrument?: EntityId;

    /**
     * A location where the event occurred
     */
    location?: EntityId;

    /**
     * Other relevant entities
     */
    others?: EntityId[];
  };

  /**
   * Additional data related to the event
   */
  payload?: Record<string, unknown>;

  /**
   * Tags for categorizing and filtering events
   */
  tags?: string[];

  /**
   * Priority of the event (higher numbers are more important)
   */
  priority?: number;

  /**
   * Whether this event should be narrated
   */
  narrate?: boolean;

  /**
   * Legacy support for data property (same as payload)
   */
  data?: Record<string, unknown>;

  /**
   * Legacy support for metadata property
   */
  metadata?: Record<string, unknown>;
}

/**
 * Interface for event source that collects and manages events
 */
export interface EventSource {
  /**
   * Add an event to the source
   */
  addEvent: (event: SemanticEvent) => void;

  /**
   * Get all events in the source
   */
  getAllEvents: () => SemanticEvent[];

  /**
   * Get events of a specific type
   */
  getEventsByType: (type: string) => SemanticEvent[];

  /**
   * Get events involving a specific entity
   */
  getEventsByEntity: (entityId: EntityId) => SemanticEvent[];

  /**
   * Get events with a specific tag
   */
  getEventsByTag: (tag: string) => SemanticEvent[];

  /**
   * Clear all events
   */
  clearEvents: () => void;

  /**
   * Apply a filter to the events
   */
  filter: (predicate: (event: SemanticEvent) => boolean) => SemanticEvent[];

  /**
   * Get the event emitter associated with this source
   */
  getEmitter: () => EventEmitter;
}

/**
 * Event listener for semantic events
 */
export type EventListener = (event: SemanticEvent) => void;

/**
 * Event emitter for semantic events
 */
export interface EventEmitter {
  /**
   * Add an event listener for a specific event type
   * Use '*' to listen to all events
   */
  on: (type: string, listener: EventListener) => void;

  /**
   * Remove an event listener
   */
  off: (type: string, listener: EventListener) => void;

  /**
   * Emit an event
   */
  emit: (event: SemanticEvent) => void;
}

/**
 * Configuration options for the event system
 */
export interface EventSystemOptions {
  /**
   * Maximum number of events to store in memory
   */
  maxEvents?: number;

  /**
   * Whether to emit events immediately when added
   */
  emitOnAdd?: boolean;

  /**
   * Custom filter for events that should be emitted
   */
  emitFilter?: (event: SemanticEvent) => boolean;
}

// packages/core/src/execution/types.ts

import { SemanticEvent } from '../events/types';
import { TextService } from '../events/text-processor';
import { LanguageProvider } from '../language';

/**
 * The result of executing a command
 */
export interface CommandResult {
  /**
   * Whether the command was successful
   */
  success: boolean;

  /**
   * Events generated during command execution
   */
  events: SemanticEvent[];

  /**
   * Error message if the command failed
   */
  error?: string;

  /**
   * Additional metadata about the command execution
   */
  metadata?: Record<string, unknown>;
}

/**
 * Generic parsed command interface
 * The actual implementation with IF-specific details is in stdlib
 */
export interface ParsedCommand {
  /**
   * The raw command text
   */
  raw: string;

  /**
   * The command verb
   */
  verb?: string;

  /**
   * Additional command data
   */
  [key: string]: any;
}

/**
 * Generic context interface for command execution
 * The actual GameContext with IF-specific details is in stdlib
 */
export interface ExecutionContext {
  /**
   * The text service for processing events into text
   */
  textService: TextService;

  /**
   * The language provider for text templates
   */
  languageProvider: LanguageProvider;

  /**
   * Additional context data
   */
  [key: string]: any;
}

/**
 * Interface for command handlers
 */
export interface CommandHandler {
  /**
   * The verb or verbs that this handler can process
   */
  verbs: string[];

  /**
   * Check if this handler can handle the given command
   */
  canHandle: (command: ParsedCommand, context: ExecutionContext) => boolean;

  /**
   * Execute the command
   */
  execute: (command: ParsedCommand, context: ExecutionContext) => Promise<CommandResult> | CommandResult;

  /**
   * Validate the command before execution
   */
  validate?: (command: ParsedCommand, context: ExecutionContext) => { valid: boolean; error?: string };
}

/**
 * Interface for command router
 */
export interface CommandRouter {
  /**
   * Register a command handler
   */
  registerHandler: (handler: CommandHandler) => void;

  /**
   * Unregister a command handler
   */
  unregisterHandler: (handler: CommandHandler) => void;

  /**
   * Get a handler for the command
   */
  getHandler: (command: ParsedCommand, context: ExecutionContext) => CommandHandler | undefined;

  /**
   * Route and execute a command
   */
  execute: (command: ParsedCommand, context: ExecutionContext, options?: CommandExecutionOptions) => Promise<CommandResult>;

  /**
   * Process the result of command execution and return text output
   */
  processResult?: (result: CommandResult) => string;
}

/**
 * Interface for command handler factory
 */
export interface CommandHandlerFactory {
  /**
   * Create a standard command handler
   */
  createHandler: (config: {
    verbs: string[];
    canHandle?: (command: ParsedCommand, context: ExecutionContext) => boolean;
    execute: (command: ParsedCommand, context: ExecutionContext) => Promise<CommandResult> | CommandResult;
    validate?: (command: ParsedCommand, context: ExecutionContext) => { valid: boolean; error?: string };
  }) => CommandHandler;
}

/**
 * Options for command execution
 */
export interface CommandExecutionOptions {
  /**
   * Whether to skip validation
   */
  skipValidation?: boolean;

  /**
   * Custom validation callback
   */
  customValidation?: (command: ParsedCommand, context: ExecutionContext) => { valid: boolean; error?: string };

  /**
   * Pre-execution hooks
   */
  preExecute?: ((command: ParsedCommand, context: ExecutionContext) => Promise<void> | void)[];

  /**
   * Post-execution hooks
   */
  postExecute?: ((result: CommandResult, command: ParsedCommand, context: ExecutionContext) => Promise<CommandResult> | CommandResult)[];
}

