// Node.js script to verify Core package is IF-agnostic
const fs = require('fs');
const path = require('path');

console.log('Verifying Core package is IF-agnostic...\n');

const corePackagePath = path.join(__dirname, 'packages', 'core');
const issues = [];

// Check for IF-specific terms in source files
const ifSpecificTerms = [
    'parser', 'Parser',
    'world-model', 'WorldModel', 'IFWorld',
    'GameObject', 'Thing', 'Room', 'Door',
    'inventory', 'Inventory',
    'movement', 'Movement',
    'taking', 'dropping', 'examining', 'opening', 'closing',
    'GameContext', 'game-context',
    'command-handler', 'CommandHandler',
    'command-router', 'CommandRouter',
    'StandardActions', 'standard-actions'
];

// Directories to check
const srcDir = path.join(corePackagePath, 'src');
const distDir = path.join(corePackagePath, 'dist');

function checkDirectory(dir, isSource = true) {
    if (!fs.existsSync(dir)) {
        console.log(`Directory not found: ${dir}`);
        return;
    }

    const files = fs.readdirSync(dir, { withFileTypes: true });

    files.forEach(file => {
        const fullPath = path.join(dir, file.name);

        if (file.isDirectory()) {
            // Skip node_modules
            if (file.name === 'node_modules') return;

            // Check for IF-specific directory names
            const ifDirs = ['parser', 'world-model', 'stdlib-old', 'story', 'languages'];
            if (ifDirs.includes(file.name)) {
                issues.push(`IF-specific directory found: ${fullPath}`);
            }

            checkDirectory(fullPath, isSource);
        } else if (file.isFile() && (file.name.endsWith('.ts') || file.name.endsWith('.js'))) {
            // Skip test files and type definition files for now
            if (file.name.includes('.test.') || file.name.includes('.spec.')) return;

            const content = fs.readFileSync(fullPath, 'utf8');

            // Check for IF-specific imports
            if (content.includes('from \'./parser\'') || content.includes('from \'./world-model\'')) {
                issues.push(`IF-specific import found in: ${fullPath}`);
            }

            // Check for IF-specific terms in comments and code
            const lines = content.split('\n');
            lines.forEach((line, index) => {
                // Skip pure comment lines for term checking
                const codeLine = line.split('//')[0].trim();

                ifSpecificTerms.forEach(term => {
                    if (codeLine.toLowerCase().includes(term.toLowerCase())) {
                        // Allow some terms in specific contexts
                        if (term === 'parser' && codeLine.includes('ParserExtension')) return;
                        if (term === 'Parser' && codeLine.includes('ParserExtension')) return;

                        issues.push(`IF-specific term "${term}" found in ${fullPath}:${index + 1}`);
                    }
                });
            });
        }
    });
}

// Check source directory
console.log('Checking source directory...');
checkDirectory(srcDir, true);

// Check dist directory
console.log('\nChecking dist directory...');
checkDirectory(distDir, false);

// Check package.json exports
console.log('\nChecking package.json exports...');
const packageJsonPath = path.join(corePackagePath, 'package.json');
if (fs.existsSync(packageJsonPath)) {
    const packageJson = JSON.parse(fs.readFileSync(packageJsonPath, 'utf8'));

    // Check main and types fields
    if (packageJson.main && packageJson.main.includes('parser')) {
        issues.push('package.json main field references parser');
    }
    if (packageJson.types && packageJson.types.includes('parser')) {
        issues.push('package.json types field references parser');
    }
}

// Report results
console.log('\n' + '='.repeat(50));
if (issues.length === 0) {
    console.log('âœ… Core package appears to be IF-agnostic!');
} else {
    console.log(`âŒ Found ${issues.length} potential IF-specific issues:\n`);
    issues.forEach((issue, index) => {
        console.log(`${index + 1}. ${issue}`);
    });
}

// Additional checks for clean build
console.log('\n' + '='.repeat(50));
console.log('Additional recommendations:');
console.log('1. Delete the entire dist directory and rebuild');
console.log('2. Review ExecutionContext in types.ts - should it know about TextService?');
console.log('3. Review constants - are entity types and relationships generic enough?');
console.log('4. Update index.ts to remove all IF-specific comments');

