// packages/stdlib/src/language/base/index.ts
/**
 * Base classes and interfaces for IF language plugins
 * Language packages should import and extend these
 */

export * from './if-language-plugin';
export * from './parser-plugin';
export * from './types';

// packages/stdlib/src/language/base/types.ts
/**
 * Types needed by IF language plugins
 */

import { LanguageProvider } from '@sharpee/core';
import { IFActions, IFEvents } from '../../constants';

export interface IFLanguagePlugin extends LanguageProvider {
  // Parser creation
  createParser(): IFParserPlugin;

  // Verb mappings
  getVerbsForAction(action: IFActions): string[];
  getActionForVerb(verb: string): IFActions | undefined;

  // IF-specific formatting
  formatAction(action: IFActions, params: ActionParams): string;
  formatEvent(event: IFEvents, params: EventParams): string;
  formatDirection(direction: string): string;
  formatItemName(name: string, options?: ItemNameOptions): string;

  // Word lists for parser
  getArticles(): string[];
  getPrepositions(): string[];
  getPronouns(): string[];
  getConjunctions(): string[];
  getDirections(): string[];
}

export interface IFParserPlugin {
  // Tokenization
  tokenize(input: string): Token[];

  // POS tagging
  tagPOS(tokens: Token[]): TaggedWord[];

  // Lemmatization
  lemmatize(word: string): string;

  // Grammar analysis
  analyzeGrammar(tagged: TaggedWord[]): ParsedCommand | null;
}

export interface Token {
  value: string;
  type: TokenType;
  position: number;
}

export enum TokenType {
  WORD = 'word',
  PUNCTUATION = 'punctuation',
  NUMBER = 'number',
  WHITESPACE = 'whitespace'
}

export interface TaggedWord {
  value: string;
  tag: POSType;
  lemma?: string;
  position: number;
}

export enum POSType {
  NOUN = 'noun',
  VERB = 'verb',
  ADJECTIVE = 'adjective',
  ADVERB = 'adverb',
  PREPOSITION = 'preposition',
  ARTICLE = 'article',
  PRONOUN = 'pronoun',
  CONJUNCTION = 'conjunction',
  NUMBER = 'number',
  UNKNOWN = 'unknown'
}

export interface ParsedCommand {
  verb: string;
  directObject?: string;
  indirectObject?: string;
  preposition?: string;
  adjectives: string[];
  raw: string;
}

export interface ActionParams {
  actor?: string;
  target?: string;
  item?: string;
  direction?: string;
  [key: string]: any;
}

export interface EventParams {
  [key: string]: any;
}

export interface ItemNameOptions {
  definite?: boolean;
  indefinite?: boolean;
  capitalize?: boolean;
  plural?: boolean;
  count?: number;
}

export interface VerbDefinition {
  action: IFActions;
  verbs: string[];
  patterns?: string[];
}

// packages/stdlib/src/language/base/if-language-plugin.ts
/**
 * Base implementation for IF language plugins
 */

import { 
  IFLanguagePlugin, 
  IFParserPlugin,
  ActionParams,
  EventParams,
  ItemNameOptions,
  VerbDefinition
} from './types';
import { IFActions, IFEvents } from '../../constants';

export abstract class BaseIFLanguagePlugin implements IFLanguagePlugin {
  protected verbs: Map<string, IFActions>;
  protected actionVerbs: Map<IFActions, string[]>;
  protected actionTemplates: Map<string, string>;
  protected eventTemplates: Map<IFEvents, string>;

  constructor() {
    this.verbs = new Map();
    this.actionVerbs = new Map();
    this.actionTemplates = new Map();
    this.eventTemplates = new Map();

    this.initializeLanguageData();
  }

  /**
   * Initialize all language-specific data
   * Subclasses must implement this
   */
  protected abstract initializeLanguageData(): void;

  /**
   * Register verb definitions
   */
  protected registerVerbs(definitions: VerbDefinition[]): void {
    for (const def of definitions) {
      // Store verbs for lookup
      for (const verb of def.verbs) {
        this.verbs.set(verb, def.action);
      }

      // Store reverse mapping
      this.actionVerbs.set(def.action, def.verbs);
    }
  }

  /**
   * Register action message templates
   */
  protected registerActionTemplates(templates: Record<string, string>): void {
    for (const [key, template] of Object.entries(templates)) {
      this.actionTemplates.set(key, template);
    }
  }

  /**
   * Register event message templates
   */
  protected registerEventTemplates(templates: Record<IFEvents, string>): void {
    for (const [event, template] of Object.entries(templates)) {
      this.eventTemplates.set(event as IFEvents, template);
    }
  }

  // IFLanguagePlugin implementation

  abstract createParser(): IFParserPlugin;

  getVerbsForAction(action: IFActions): string[] {
    return this.actionVerbs.get(action) || [];
  }

  getActionForVerb(verb: string): IFActions | undefined {
    return this.verbs.get(verb.toLowerCase());
  }

  formatAction(action: IFActions, params: ActionParams): string {
    const template = this.actionTemplates.get(action);
    if (!template) {
      return `[Missing template for action: ${action}]`;
    }
    return this.formatTemplate(template, params);
  }

  formatEvent(event: IFEvents, params: EventParams): string {
    const template = this.eventTemplates.get(event);
    if (!template) {
      return `[Missing template for event: ${event}]`;
    }
    return this.formatTemplate(template, params);
  }

  /**
   * Format a template with parameters
   */
  protected formatTemplate(template: string, params: any): string {
    return template.replace(/\{(\w+)\}/g, (match, key) => {
      return params[key] !== undefined ? String(params[key]) : match;
    });
  }

  // Abstract methods that must be implemented by language plugins

  abstract getLanguageCode(): string;
  abstract getLanguageName(): string;
  abstract getTextDirection(): 'ltr' | 'rtl';
  abstract formatMessage(template: string, params?: any): string;
  abstract formatList(items: string[], options?: any): string;
  abstract formatDirection(direction: string): string;
  abstract formatItemName(name: string, options?: ItemNameOptions): string;
  abstract getArticles(): string[];
  abstract getPrepositions(): string[];
  abstract getPronouns(): string[];
  abstract getConjunctions(): string[];
  abstract getDirections(): string[];
}

// packages/stdlib/src/language/base/parser-plugin.ts
/**
 * Base parser implementation for language plugins
 */

import {
  IFParserPlugin,
  Token,
  TokenType,
  TaggedWord,
  POSType,
  ParsedCommand
} from './types';

export abstract class BaseIFParserPlugin implements IFParserPlugin {
  /**
   * Default tokenization - can be overridden
   */
  tokenize(input: string): Token[] {
    const tokens: Token[] = [];
    let position = 0;

    // Simple regex-based tokenization
    const regex = /(\s+)|([.,!?;:])|(\d+)|([a-zA-Z]+)|(.)/g;
    let match;

    while ((match = regex.exec(input)) !== null) {
      const value = match[0];
      let type: TokenType;

      if (match[1]) type = TokenType.WHITESPACE;
      else if (match[2]) type = TokenType.PUNCTUATION;
      else if (match[3]) type = TokenType.NUMBER;
      else if (match[4]) type = TokenType.WORD;
      else type = TokenType.PUNCTUATION;

      if (type !== TokenType.WHITESPACE) {
        tokens.push({ value, type, position });
      }

      position += value.length;
    }

    return tokens;
  }

  // Abstract methods to be implemented by language-specific parsers
  abstract tagPOS(tokens: Token[]): TaggedWord[];
  abstract lemmatize(word: string): string;
  abstract analyzeGrammar(tagged: TaggedWord[]): ParsedCommand | null;
}

