/**
 * @file Language Plugin Types
 * @description Core types for IF language plugins
 * 
 * This file defines all the types needed by language plugins to implement
 * parsing, formatting, and language-specific features for interactive fiction.
 */

import { LanguageProvider } from '@sharpee/core';
import { IFActions, IFEvents } from '../../constants';

/**
 * Main interface for IF language plugins
 * Extends the core LanguageProvider with IF-specific functionality
 */
export interface IFLanguagePlugin extends LanguageProvider {
  /**
   * Create a parser instance for this language
   */
  createParser(): IFParserPlugin;

  /**
   * Get all verbs that map to a specific action
   * @param action The IF action
   * @returns Array of verb strings (e.g., ['take', 'get', 'grab'])
   */
  getVerbsForAction(action: IFActions): string[];

  /**
   * Get the action associated with a verb
   * @param verb The verb to look up
   * @returns The action or undefined if not found
   */
  getActionForVerb(verb: string): IFActions | undefined;

  /**
   * Format an action message with parameters
   * @param action The action being performed
   * @param phase The phase of the action (e.g., 'check', 'perform', 'report')
   * @param key The specific message key
   * @param params Parameters for message formatting
   */
  formatActionMessage(action: IFActions, phase: string, key: string, params?: ActionParams): string;

  /**
   * Format an event message
   * @param event The IF event
   * @param params Parameters for message formatting
   */
  formatEventMessage(event: IFEvents, params?: EventParams): string;

  /**
   * Format a direction for display (e.g., "north" -> "to the north")
   * @param direction The direction string
   */
  formatDirection(direction: string): string;

  /**
   * Get canonical form of a direction (e.g., "n" -> "north")
   * @param direction The direction string or abbreviation
   */
  canonicalizeDirection(direction: string): string | undefined;

  /**
   * Format an item/entity name with appropriate article
   * @param name The item name
   * @param options Formatting options
   */
  formatItemName(name: string, options?: ItemNameOptions): string;

  /**
   * Get language-specific word lists for parsing
   */
  getArticles(): string[];
  getPrepositions(): string[];
  getPronouns(): string[];
  getConjunctions(): string[];
  getDirections(): string[];
  getCommonAdjectives(): string[];
}

/**
 * Parser plugin interface
 * Handles tokenization, POS tagging, and command parsing
 */
export interface IFParserPlugin {
  /**
   * Tokenize input text into tokens
   */
  tokenize(input: string): Token[];

  /**
   * Tag tokens with parts of speech
   */
  tagPOS(tokens: Token[]): TaggedWord[];

  /**
   * Lemmatize a word to its base form
   * @param word The word to lemmatize
   * @returns Base form of the word
   */
  lemmatize(word: string): string;

  /**
   * Normalize a phrase for matching (remove articles, lemmatize, etc.)
   * @param phrase The phrase to normalize
   * @returns Array of normalized words
   */
  normalizePhrase(phrase: string): string[];

  /**
   * Analyze tagged words to extract a command
   * @param tagged The POS-tagged words
   * @returns Parsed command or null if invalid
   */
  analyzeGrammar(tagged: TaggedWord[]): ParsedCommand | null;

  /**
   * Identify phrases in tagged words
   * @param tagged The POS-tagged words
   * @returns Array of identified phrases
   */
  identifyPhrases(tagged: TaggedWord[]): Phrase[];
}

/**
 * Token from tokenization
 */
export interface Token {
  value: string;
  type: TokenType;
  position: number;
}

/**
 * Token types
 */
export enum TokenType {
  WORD = 'word',
  PUNCTUATION = 'punctuation',
  NUMBER = 'number',
  WHITESPACE = 'whitespace',
  SYMBOL = 'symbol'
}

/**
 * Word with part-of-speech tag
 */
export interface TaggedWord {
  value: string;
  tag: POSType;
  lemma?: string;
  position: number;
  original: string;
}

/**
 * Part of speech types
 */
export enum POSType {
  NOUN = 'noun',
  VERB = 'verb',
  ADJECTIVE = 'adjective',
  ADVERB = 'adverb',
  PREPOSITION = 'preposition',
  ARTICLE = 'article',
  PRONOUN = 'pronoun',
  CONJUNCTION = 'conjunction',
  NUMBER = 'number',
  DETERMINER = 'determiner',
  INTERJECTION = 'interjection',
  PROPER_NOUN = 'proper_noun',
  UNKNOWN = 'unknown'
}

/**
 * Identified phrase in the input
 */
export interface Phrase {
  type: PhraseType;
  words: TaggedWord[];
  start: number;
  end: number;
}

/**
 * Types of phrases
 */
export enum PhraseType {
  NOUN_PHRASE = 'noun_phrase',
  VERB_PHRASE = 'verb_phrase',
  PREPOSITIONAL_PHRASE = 'prepositional_phrase',
  ADJECTIVE_PHRASE = 'adjective_phrase',
  ADVERB_PHRASE = 'adverb_phrase'
}

/**
 * Parsed command structure
 */
export interface ParsedCommand {
  /**
   * The main verb/action
   */
  verb: string;

  /**
   * Direct object (what the verb acts on)
   */
  directObject?: string;

  /**
   * Indirect object (to/for whom/what)
   */
  indirectObject?: string;

  /**
   * Preposition connecting objects
   */
  preposition?: string;

  /**
   * Adjectives modifying the direct object
   */
  adjectives: string[];

  /**
   * The original raw input
   */
  raw: string;

  /**
   * Additional parsed details
   */
  details?: {
    directObjectWords?: string[];
    indirectObjectWords?: string[];
    quantity?: number;
    all?: boolean;
  };
}

/**
 * Parameters for action message formatting
 */
export interface ActionParams {
  actor?: string;
  target?: string;
  item?: string;
  direction?: string;
  container?: string;
  reason?: string;
  [key: string]: any;
}

/**
 * Parameters for event message formatting
 */
export interface EventParams {
  entity?: string;
  location?: string;
  oldValue?: any;
  newValue?: any;
  [key: string]: any;
}

/**
 * Options for formatting item names
 */
export interface ItemNameOptions {
  /**
   * Use definite article ("the")
   */
  definite?: boolean;

  /**
   * Use indefinite article ("a/an")
   */
  indefinite?: boolean;

  /**
   * Capitalize first letter
   */
  capitalize?: boolean;

  /**
   * Use plural form
   */
  plural?: boolean;

  /**
   * Specific count for pluralization
   */
  count?: number;

  /**
   * Use proper name (no article)
   */
  proper?: boolean;
}

/**
 * Verb definition for action mapping
 */
export interface VerbDefinition {
  /**
   * The action this verb maps to
   */
  action: IFActions;

  /**
   * All verb forms that map to this action
   */
  verbs: string[];

  /**
   * Optional grammar patterns for this verb
   */
  patterns?: string[];

  /**
   * Whether this verb requires an object
   */
  requiresObject?: boolean;

  /**
   * Whether this verb can take an indirect object
   */
  allowsIndirectObject?: boolean;
}

/**
 * Grammar pattern for command matching
 */
export interface GrammarPattern {
  /**
   * Pattern string (e.g., "VERB NOUN", "VERB PREP NOUN")
   */
  pattern: string;

  /**
   * Optional action this pattern maps to
   */
  action?: IFActions;

  /**
   * Priority for disambiguation (higher = preferred)
   */
  priority?: number;
}

/**
 * Configuration for language plugins
 */
export interface IFLanguageConfig {
  /**
   * Language code (e.g., "en-US")
   */
  code: string;

  /**
   * Language name (e.g., "English (US)")
   */
  name: string;

  /**
   * Text direction
   */
  direction: 'ltr' | 'rtl';

  /**
   * Custom message templates
   */
  customTemplates?: Record<string, string>;

  /**
   * Additional verb mappings
   */
  customVerbs?: VerbDefinition[];

  /**
   * Custom grammar patterns
   */
  customPatterns?: GrammarPattern[];
}

/**
 * @file Base IF Language Plugin
 * @description Abstract base class for IF language plugins
 * 
 * This class provides a foundation for implementing language-specific
 * support for interactive fiction, including verb mappings, message
 * formatting, and parser creation.
 */

import { 
  IFLanguagePlugin, 
  IFParserPlugin,
  IFLanguageConfig,
  ActionParams,
  EventParams,
  ItemNameOptions,
  VerbDefinition
} from './types';
import { IFActions, IFEvents } from '../../constants';

/**
 * Abstract base class for IF language plugins
 * 
 * Language implementations should extend this class and implement
 * all abstract methods to provide language-specific functionality.
 */
export abstract class BaseIFLanguagePlugin implements IFLanguagePlugin {
  protected config: IFLanguageConfig;
  protected verbs: Map<string, IFActions>;
  protected actionVerbs: Map<IFActions, string[]>;
  protected actionTemplates: Map<string, string>;
  protected eventTemplates: Map<IFEvents, string>;
  protected directionMap: Map<string, string>;

  constructor(config?: Partial<IFLanguageConfig>) {
    this.config = {
      code: this.getDefaultLanguageCode(),
      name: this.getDefaultLanguageName(),
      direction: this.getDefaultTextDirection(),
      ...config
    };

    this.verbs = new Map();
    this.actionVerbs = new Map();
    this.actionTemplates = new Map();
    this.eventTemplates = new Map();
    this.directionMap = new Map();

    // Initialize language data
    this.initializeLanguageData();

    // Apply any custom configuration
    this.applyCustomConfig();
  }

  /**
   * Initialize all language-specific data
   * Subclasses must implement this to set up:
   * - Verb mappings
   * - Message templates
   * - Direction mappings
   * - Word lists
   */
  protected abstract initializeLanguageData(): void;

  /**
   * Get default language code (e.g., "en-US")
   * Used if not provided in config
   */
  protected abstract getDefaultLanguageCode(): string;

  /**
   * Get default language name (e.g., "English (US)")
   * Used if not provided in config
   */
  protected abstract getDefaultLanguageName(): string;

  /**
   * Get default text direction
   * Used if not provided in config
   */
  protected abstract getDefaultTextDirection(): 'ltr' | 'rtl';

  /**
   * Apply custom configuration from the config object
   */
  protected applyCustomConfig(): void {
    // Apply custom templates
    if (this.config.customTemplates) {
      for (const [key, template] of Object.entries(this.config.customTemplates)) {
        this.actionTemplates.set(key, template);
      }
    }

    // Apply custom verbs
    if (this.config.customVerbs) {
      for (const verbDef of this.config.customVerbs) {
        this.registerVerbDefinition(verbDef);
      }
    }
  }

  /**
   * Register a verb definition
   */
  protected registerVerbDefinition(def: VerbDefinition): void {
    // Map each verb form to the action
    for (const verb of def.verbs) {
      this.verbs.set(verb.toLowerCase(), def.action);
    }

    // Store reverse mapping
    const existing = this.actionVerbs.get(def.action) || [];
    this.actionVerbs.set(def.action, [...new Set([...existing, ...def.verbs])]);
  }

  /**
   * Register multiple verb definitions
   */
  protected registerVerbs(definitions: VerbDefinition[]): void {
    for (const def of definitions) {
      this.registerVerbDefinition(def);
    }
  }

  /**
   * Register action message templates
   * @param templates Object with keys like "take.check.already_have"
   */
  protected registerActionTemplates(templates: Record<string, string>): void {
    for (const [key, template] of Object.entries(templates)) {
      this.actionTemplates.set(key, template);
    }
  }

  /**
   * Register event message templates
   */
  protected registerEventTemplates(templates: Partial<Record<IFEvents, string>>): void {
    for (const [event, template] of Object.entries(templates)) {
      this.eventTemplates.set(event as IFEvents, template);
    }
  }

  /**
   * Register direction mappings (e.g., "n" -> "north")
   */
  protected registerDirections(directions: Record<string, string>): void {
    for (const [abbrev, full] of Object.entries(directions)) {
      this.directionMap.set(abbrev.toLowerCase(), full.toLowerCase());
    }
  }

  // LanguageProvider implementation

  getLanguageCode(): string {
    return this.config.code;
  }

  getLanguageName(): string {
    return this.config.name;
  }

  getTextDirection(): 'ltr' | 'rtl' {
    return this.config.direction;
  }

  /**
   * Format a message template with parameters
   * Subclasses can override for language-specific formatting
   */
  formatMessage(template: string, params?: any): string {
    if (!params) return template;

    return template.replace(/\{(\w+)(?::(\w+))?\}/g, (match, key, modifier) => {
      const value = params[key];
      if (value === undefined) return match;

      // Apply modifier if present
      if (modifier) {
        return this.applyModifier(value, modifier);
      }

      return String(value);
    });
  }

  /**
   * Apply a modifier to a value (e.g., capitalize, plural)
   * Subclasses should override for language-specific modifiers
   */
  protected applyModifier(value: any, modifier: string): string {
    const str = String(value);

    switch (modifier) {
      case 'cap':
      case 'capitalize':
        return str.charAt(0).toUpperCase() + str.slice(1);
      case 'upper':
        return str.toUpperCase();
      case 'lower':
        return str.toLowerCase();
      default:
        return str;
    }
  }

  // IFLanguagePlugin implementation

  getVerbsForAction(action: IFActions): string[] {
    return this.actionVerbs.get(action) || [];
  }

  getActionForVerb(verb: string): IFActions | undefined {
    return this.verbs.get(verb.toLowerCase());
  }

  formatActionMessage(action: IFActions, phase: string, key: string, params?: ActionParams): string {
    const messageKey = `${action}.${phase}.${key}`;
    const template = this.actionTemplates.get(messageKey);

    if (!template) {
      // Try without phase for simpler messages
      const simpleKey = `${action}.${key}`;
      const simpleTemplate = this.actionTemplates.get(simpleKey);

      if (!simpleTemplate) {
        return `[Missing message: ${messageKey}]`;
      }

      return this.formatMessage(simpleTemplate, params);
    }

    return this.formatMessage(template, params);
  }

  formatEventMessage(event: IFEvents, params?: EventParams): string {
    const template = this.eventTemplates.get(event);

    if (!template) {
      return `[Missing message for event: ${event}]`;
    }

    return this.formatMessage(template, params);
  }

  formatDirection(direction: string): string {
    // Default implementation - subclasses can override
    const canonical = this.canonicalizeDirection(direction);
    return canonical || direction;
  }

  canonicalizeDirection(direction: string): string | undefined {
    const lower = direction.toLowerCase();

    // Check if it's already canonical
    if (this.getDirections().includes(lower)) {
      return lower;
    }

    // Check abbreviations
    return this.directionMap.get(lower);
  }

  // Abstract methods that must be implemented

  abstract createParser(): IFParserPlugin;
  abstract formatList(items: string[], options?: any): string;
  abstract formatItemName(name: string, options?: ItemNameOptions): string;
  abstract getArticles(): string[];
  abstract getPrepositions(): string[];
  abstract getPronouns(): string[];
  abstract getConjunctions(): string[];
  abstract getDirections(): string[];
  abstract getCommonAdjectives(): string[];
}

/**
 * Helper function to create message template keys
 */
export function messageKey(action: IFActions, phase: string, key: string): string {
  return `${action}.${phase}.${key}`;
}

/**
 * Helper function to create simple message keys (no phase)
 */
export function simpleMessageKey(action: IFActions, key: string): string {
  return `${action}.${key}`;
}

/**
 * @file Base Parser Plugin
 * @description Abstract base class for language-specific parsers
 * 
 * This class provides common parsing functionality that can be
 * extended by language-specific implementations.
 */

import {
  IFParserPlugin,
  Token,
  TokenType,
  TaggedWord,
  POSType,
  ParsedCommand,
  Phrase,
  PhraseType
} from './types';

/**
 * Abstract base class for IF parser plugins
 * 
 * Provides default implementations for common parsing tasks
 * that can be overridden by language-specific parsers.
 */
export abstract class BaseIFParserPlugin implements IFParserPlugin {
  /**
   * Default tokenization implementation
   * Can be overridden for language-specific tokenization rules
   */
  tokenize(input: string): Token[] {
    const tokens: Token[] = [];
    let position = 0;

    // Regex to match different token types
    // Groups: whitespace | punctuation | numbers | words | symbols
    const regex = /(\s+)|([.,!?;:'"]+)|(\d+(?:\.\d+)?)|([a-zA-Z]+(?:[-'][a-zA-Z]+)*)|(.)/g;
    let match;

    while ((match = regex.exec(input)) !== null) {
      const value = match[0];
      let type: TokenType;

      if (match[1]) {
        // Skip whitespace
        position += value.length;
        continue;
      } else if (match[2]) {
        type = TokenType.PUNCTUATION;
      } else if (match[3]) {
        type = TokenType.NUMBER;
      } else if (match[4]) {
        type = TokenType.WORD;
      } else {
        type = TokenType.SYMBOL;
      }

      tokens.push({
        value,
        type,
        position
      });

      position += value.length;
    }

    return tokens;
  }

  /**
   * Normalize a phrase by removing articles and lemmatizing
   * Default implementation - should be overridden
   */
  normalizePhrase(phrase: string): string[] {
    // Split on whitespace and filter empty
    const words = phrase.toLowerCase().split(/\s+/).filter(w => w.length > 0);

    // Remove common articles (should use language-specific list)
    const articles = ['a', 'an', 'the'];
    const filtered = words.filter(w => !articles.includes(w));

    // Lemmatize each word
    return filtered.map(w => this.lemmatize(w));
  }

  /**
   * Default phrase identification
   * Identifies basic phrase types based on POS tags
   */
  identifyPhrases(tagged: TaggedWord[]): Phrase[] {
    const phrases: Phrase[] = [];
    let i = 0;

    while (i < tagged.length) {
      const word = tagged[i];

      // Identify verb phrases
      if (word.tag === POSType.VERB) {
        const verbPhrase = this.extractVerbPhrase(tagged, i);
        phrases.push(verbPhrase);
        i = verbPhrase.end + 1;
        continue;
      }

      // Identify noun phrases
      if (this.isNounPhraseStart(word)) {
        const nounPhrase = this.extractNounPhrase(tagged, i);
        phrases.push(nounPhrase);
        i = nounPhrase.end + 1;
        continue;
      }

      // Identify prepositional phrases
      if (word.tag === POSType.PREPOSITION) {
        const prepPhrase = this.extractPrepositionalPhrase(tagged, i);
        phrases.push(prepPhrase);
        i = prepPhrase.end + 1;
        continue;
      }

      i++;
    }

    return phrases;
  }

  /**
   * Check if a word can start a noun phrase
   */
  protected isNounPhraseStart(word: TaggedWord): boolean {
    return word.tag === POSType.ARTICLE ||
           word.tag === POSType.DETERMINER ||
           word.tag === POSType.ADJECTIVE ||
           word.tag === POSType.NUMBER ||
           word.tag === POSType.NOUN ||
           word.tag === POSType.PRONOUN;
  }

  /**
   * Extract a verb phrase starting at the given index
   */
  protected extractVerbPhrase(tagged: TaggedWord[], start: number): Phrase {
    const words: TaggedWord[] = [tagged[start]];
    let end = start;

    // Include following adverbs
    for (let i = start + 1; i < tagged.length; i++) {
      if (tagged[i].tag === POSType.ADVERB) {
        words.push(tagged[i]);
        end = i;
      } else {
        break;
      }
    }

    return {
      type: PhraseType.VERB_PHRASE,
      words,
      start,
      end
    };
  }

  /**
   * Extract a noun phrase starting at the given index
   */
  protected extractNounPhrase(tagged: TaggedWord[], start: number): Phrase {
    const words: TaggedWord[] = [];
    let end = start;
    let hasNoun = false;

    for (let i = start; i < tagged.length; i++) {
      const word = tagged[i];

      // Articles, determiners, numbers, and adjectives can precede the noun
      if (word.tag === POSType.ARTICLE ||
          word.tag === POSType.DETERMINER ||
          word.tag === POSType.NUMBER ||
          word.tag === POSType.ADJECTIVE) {
        words.push(word);
        end = i;
      }
      // The noun itself
      else if (word.tag === POSType.NOUN || word.tag === POSType.PRONOUN) {
        words.push(word);
        end = i;
        hasNoun = true;

        // Check for compound nouns (noun followed by noun)
        if (i + 1 < tagged.length && tagged[i + 1].tag === POSType.NOUN) {
          continue;
        } else {
          break;
        }
      }
      // Stop if we hit something else after finding a noun
      else if (hasNoun) {
        break;
      }
      // If we haven't found a noun yet and hit something else, this isn't a noun phrase
      else {
        break;
      }
    }

    return {
      type: PhraseType.NOUN_PHRASE,
      words,
      start,
      end
    };
  }

  /**
   * Extract a prepositional phrase starting at the given index
   */
  protected extractPrepositionalPhrase(tagged: TaggedWord[], start: number): Phrase {
    const words: TaggedWord[] = [tagged[start]]; // The preposition
    let end = start;

    // A prepositional phrase should be followed by a noun phrase
    if (start + 1 < tagged.length) {
      const nounPhraseStart = start + 1;
      if (this.isNounPhraseStart(tagged[nounPhraseStart])) {
        const nounPhrase = this.extractNounPhrase(tagged, nounPhraseStart);
        words.push(...nounPhrase.words);
        end = nounPhrase.end;
      }
    }

    return {
      type: PhraseType.PREPOSITIONAL_PHRASE,
      words,
      start,
      end
    };
  }

  /**
   * Default grammar analysis implementation
   * Extracts basic command structure from phrases
   */
  analyzeGrammar(tagged: TaggedWord[]): ParsedCommand | null {
    // First identify phrases
    const phrases = this.identifyPhrases(tagged);

    // Look for a verb phrase (required for a valid command)
    const verbPhrase = phrases.find(p => p.type === PhraseType.VERB_PHRASE);
    if (!verbPhrase) {
      return null;
    }

    // Extract the verb
    const verbWord = verbPhrase.words.find(w => w.tag === POSType.VERB);
    if (!verbWord) {
      return null;
    }

    const command: ParsedCommand = {
      verb: verbWord.lemma || verbWord.value.toLowerCase(),
      adjectives: [],
      raw: tagged.map(w => w.original || w.value).join(' ')
    };

    // Look for direct object (noun phrase after verb)
    const directObjectPhrase = phrases.find(p => 
      p.type === PhraseType.NOUN_PHRASE &&
      p.start > verbPhrase.end
    );

    if (directObjectPhrase) {
      const { noun, adjectives } = this.extractNounAndAdjectives(directObjectPhrase);
      command.directObject = noun;
      command.adjectives = adjectives;

      // Store the full noun phrase for reference
      if (!command.details) command.details = {};
      command.details.directObjectWords = directObjectPhrase.words.map(w => w.value);
    }

    // Look for prepositional phrase
    const prepPhrase = phrases.find(p => p.type === PhraseType.PREPOSITIONAL_PHRASE);
    if (prepPhrase) {
      const prepWord = prepPhrase.words.find(w => w.tag === POSType.PREPOSITION);
      if (prepWord) {
        command.preposition = prepWord.value.toLowerCase();

        // Extract indirect object from the prepositional phrase
        const indirectNoun = this.extractNounFromPhrase(prepPhrase);
        if (indirectNoun) {
          command.indirectObject = indirectNoun;

          // Store the words for reference
          if (!command.details) command.details = {};
          command.details.indirectObjectWords = prepPhrase.words
            .slice(1) // Skip the preposition
            .map(w => w.value);
        }
      }
    }

    return command;
  }

  /**
   * Extract noun and adjectives from a noun phrase
   */
  protected extractNounAndAdjectives(phrase: Phrase): { noun: string; adjectives: string[] } {
    const adjectives: string[] = [];
    let noun = '';

    // Collect adjectives and find the main noun
    const nouns: string[] = [];

    for (const word of phrase.words) {
      if (word.tag === POSType.ADJECTIVE) {
        adjectives.push(word.value.toLowerCase());
      } else if (word.tag === POSType.NOUN) {
        nouns.push(word.value.toLowerCase());
      } else if (word.tag === POSType.PRONOUN) {
        noun = word.value.toLowerCase();
        break; // Pronoun is the object
      }
    }

    // If we found nouns, join them (could be compound noun)
    if (nouns.length > 0) {
      noun = nouns.join(' ');
    }

    return { noun, adjectives };
  }

  /**
   * Extract just the noun from a phrase (ignoring articles, adjectives, etc.)
   */
  protected extractNounFromPhrase(phrase: Phrase): string | null {
    const nouns = phrase.words.filter(w => 
      w.tag === POSType.NOUN || w.tag === POSType.PRONOUN
    );

    if (nouns.length === 0) {
      return null;
    }

    // Join compound nouns
    return nouns.map(w => w.value.toLowerCase()).join(' ');
  }

  // Abstract methods that must be implemented by language-specific parsers

  /**
   * Tag tokens with parts of speech
   * Must be implemented by language-specific parsers
   */
  abstract tagPOS(tokens: Token[]): TaggedWord[];

  /**
   * Lemmatize a word to its base form
   * Must be implemented by language-specific parsers
   */
  abstract lemmatize(word: string): string;
}

/**
 * @file Language Plugin Base Module
 * @description Exports base classes and types for IF language plugins
 * 
 * This module provides everything needed for language packages to
 * implement IF-specific language support as plugins for StdLib.
 */

// Export all types
export * from './types';

// Export base classes
export { BaseIFLanguagePlugin, messageKey, simpleMessageKey } from './if-language-plugin';
export { BaseIFParserPlugin } from './parser-plugin';

// Re-export useful enums for convenience
export { TokenType, POSType, PhraseType } from './types';

