import { VerbDefinition, VerbCategory } from '@sharpee/core';

/**
 * Standard verb definitions for English language
 */
export const StandardVerbDefinitions: Record<string, VerbDefinition> = {
  // Navigation verbs
  'go': {
    canonical: 'go',
    synonyms: ['move', 'walk', 'run', 'head', 'travel'],
    description: 'Move in a direction',
    category: VerbCategory.MOVEMENT,
    requiresDirectObject: true
  },
  'enter': {
    canonical: 'enter',
    synonyms: ['go in', 'go into'],
    description: 'Enter a location or container',
    category: VerbCategory.MOVEMENT,
    requiresDirectObject: true
  },
  'exit': {
    canonical: 'exit',
    synonyms: ['leave', 'go out', 'get out'],
    description: 'Exit from a location or container',
    category: VerbCategory.MOVEMENT,
    requiresDirectObject: false
  },
  'climb': {
    canonical: 'climb',
    synonyms: ['scale', 'ascend'],
    description: 'Climb an object',
    category: VerbCategory.MOVEMENT,
    requiresDirectObject: true
  },

  // Observation verbs
  'look': {
    canonical: 'look',
    synonyms: ['examine', 'x', 'inspect', 'check', 'view', 'read', 'observe', 'look at'],
    description: 'Look around or examine something',
    category: VerbCategory.OBSERVATION,
    requiresDirectObject: false
  },
  'search': {
    canonical: 'search',
    synonyms: ['find', 'locate'],
    description: 'Search for an object',
    category: VerbCategory.OBSERVATION,
    requiresDirectObject: true
  },
  'listen': {
    canonical: 'listen',
    synonyms: ['hear'],
    description: 'Listen to sounds',
    category: VerbCategory.OBSERVATION,
    requiresDirectObject: false
  },
  'smell': {
    canonical: 'smell',
    synonyms: ['sniff'],
    description: 'Smell an object or the surroundings',
    category: VerbCategory.OBSERVATION,
    requiresDirectObject: false
  },
  'touch': {
    canonical: 'touch',
    synonyms: ['feel'],
    description: 'Touch an object',
    category: VerbCategory.OBSERVATION,
    requiresDirectObject: true
  },

  // Manipulation verbs
  'take': {
    canonical: 'take',
    synonyms: ['get', 'pick', 'grab', 'acquire', 'pick up', 'take up'],
    description: 'Take an object',
    category: VerbCategory.MANIPULATION,
    requiresDirectObject: true
  },
  'drop': {
    canonical: 'drop',
    synonyms: ['put', 'place', 'discard', 'put down', 'throw away'],
    description: 'Drop an object',
    category: VerbCategory.MANIPULATION,
    requiresDirectObject: true
  },
  'open': {
    canonical: 'open',
    synonyms: ['unwrap', 'uncover'],
    description: 'Open something',
    category: VerbCategory.MANIPULATION,
    requiresDirectObject: true
  },
  'lock': {
    canonical: 'lock',
    synonyms: ['secure'],
    description: 'Lock something',
    category: VerbCategory.MANIPULATION,
    requiresDirectObject: true
  },
  'unlock': {
    canonical: 'unlock',
    synonyms: ['unsecure'],
    description: 'Unlock something',
    category: VerbCategory.MANIPULATION,
    requiresDirectObject: true
  },
  'close': {
    canonical: 'close',
    synonyms: ['shut', 'cover'],
    description: 'Close something',
    category: VerbCategory.MANIPULATION,
    requiresDirectObject: true
  },
  'push': {
    canonical: 'push',
