/**
 * Get all standard verb definitions
 */
export function getStandardVerbDefinitions(): Record<string, VerbDefinition> {
  return { ...StandardVerbDefinitions };
}

/**
 * Get verbs by category
 */
export function getVerbsByCategory(category: VerbCategory): VerbDefinition[] {
  return Object.values(StandardVerbDefinitions).filter(verb => verb.category === category);
}

