import { LanguageParserProvider, POSType } from '@sharpee/core';
import { EnglishTokenizer, createEnglishTokenizer } from './tokenizer';
import { EnglishPOSTagger, createEnglishPOSTagger } from './pos-tagger';
import { EnglishPhraseIdentifier, createEnglishPhraseIdentifier } from './phrase-identifier';
import { EnglishGrammarAnalyzer, createEnglishGrammarAnalyzer } from './grammar-analyzer';
import { EnglishLemmatizer, createEnglishLemmatizer } from './lemmatizer';

/**
 * Options for the English parser provider
 */
export interface EnglishParserProviderOptions {
  /**
   * Tokenizer options
   */
  tokenizer?: {
    handleContractions?: boolean;
    recognizeCompoundVerbs?: boolean;
  };

  /**
   * POS tagger options
   */
  posTagger?: {
    useContextualRules?: boolean;
    useHeuristics?: boolean;
    useLemmatization?: boolean;
  };

  /**
   * Phrase identifier options
   */
  phraseIdentifier?: {
    identifyComplexNounPhrases?: boolean;
    identifyPrepositionalPhrases?: boolean;
  };

  /**
   * Grammar analyzer options
   */
  grammarAnalyzer?: {
    extractAdjectives?: boolean;
    handleImplicitObjects?: boolean;
  };
}

/**
 * English language parser provider implementation
 */
export class EnglishParserProvider implements LanguageParserProvider {
  private tokenizer: EnglishTokenizer;
  private posTagger: EnglishPOSTagger;
  private phraseIdentifier: EnglishPhraseIdentifier;
  private grammarAnalyzer: EnglishGrammarAnalyzer;
  private lemmatizer: EnglishLemmatizer;

  /**
   * Create a new English parser provider
   * @param options Configuration options
   */
  constructor(options: EnglishParserProviderOptions = {}) {
    // Initialize components with options
    this.tokenizer = new EnglishTokenizer(options.tokenizer);
    this.posTagger = new EnglishPOSTagger(options.posTagger);
    this.phraseIdentifier = new EnglishPhraseIdentifier(options.phraseIdentifier);
    this.grammarAnalyzer = new EnglishGrammarAnalyzer(options.grammarAnalyzer);
    this.lemmatizer = new EnglishLemmatizer();
  }

  /**
   * Get the tokenizer for this language
   */
  getTokenizer() {
    return this.tokenizer;
  }

  /**
   * Get the POS tagger for this language
   */
  getPOSTagger() {
    return this.posTagger;
  }

  /**
   * Get the phrase identifier for this language
   */
  getPhraseIdentifier() {
    return this.phraseIdentifier;
  }

  /**
   * Get the grammar analyzer for this language
   */
  getGrammarAnalyzer() {
    return this.grammarAnalyzer;
  }

  /**
   * Lemmatize a word (convert to base form)
   * @param word The word to lemmatize
   * @param posTag The part of speech of the word
   * @returns The lemmatized form of the word
   */
  lemmatize(word: string, posTag: POSType): string {
    return this.lemmatizer.lemmatize(word, posTag);
  }

