import { Action } from '@sharpee/stdlib/language/base/types';
import { IFLanguagePlugin } from '@sharpee/stdlib/language/base/types';

/**
 * Helper functions for managing actions in the Sharpee StdLib
 */
export class ActionHelpers {
    /**
     * Retrieves the list of action verbs from the language plugin
     * @param languagePlugin The language plugin instance
     * @returns An array of action verbs
     */
    static getActionVerbList(languagePlugin: IFLanguagePlugin): string[] {
        // Use the new method from the language plugin base class
        return languagePlugin.getRegisteredVerbs().map(verb => verb.infinitive);
    }

    /**
     * Validates an action against the language plugin's verb list
     * @param action The action to validate
     * @param languagePlugin The language plugin instance
     * @returns Boolean indicating if the action is valid
     */
    static validateAction(action: Action, languagePlugin: IFLanguagePlugin): boolean {
        const actionVerbs = this.getActionVerbList(languagePlugin);
        return actionVerbs.includes(action.verb);
    }

    /**
     * Creates a new action with validation
     * @param verb The action verb
     * @param params Action parameters
     * @param languagePlugin The language plugin instance
     * @returns The created action or throws an error if invalid
     */
    static createAction(
        verb: string, 
        params: Record<string, unknown>, 
        languagePlugin: IFLanguagePlugin
    ): Action {
        const actionVerbs = this.getActionVerbList(languagePlugin);

        if (!actionVerbs.includes(verb)) {
            throw new Error(`Invalid action verb: ${verb}. Allowed verbs: ${actionVerbs.join(', ')}`);
        }

        return {
            verb,
            params
        };
    }
}

// Export additional types or interfaces if needed
export interface ActionHelperOptions {
    strict?: boolean;
    allowCustomVerbs?: boolean;
}

