/**
 * Helper functions for initializing actions with language-specific data
 */

import { ActionDefinition } from '@sharpee/core/execution/actions';
import { LanguageProvider } from '@sharpee/core/language';

/**
 * Initialize an action definition with verb lists from the language provider
 * This populates the action's verbs array with the appropriate verbs for the current language
 * 
 * @param action The action definition to initialize
 * @param languageProvider The language provider to get verb lists from
 * @returns The action definition with populated verbs
 */
export function initializeActionWithVerbs(
  action: ActionDefinition, 
  languageProvider: LanguageProvider
): ActionDefinition {
  // Get the verb list for this action from the language provider
  const verbs = languageProvider.getActionVerbList(action.id);

  // Return a new action definition with the populated verbs
  return {
    ...action,
    verbs: verbs
  };
}

/**
 * Initialize multiple actions with verb lists from the language provider
 * 
 * @param actions Array of action definitions to initialize
 * @param languageProvider The language provider to get verb lists from
 * @returns Array of action definitions with populated verbs
 */
export function initializeActionsWithVerbs(
  actions: ActionDefinition[], 
  languageProvider: LanguageProvider
): ActionDefinition[] {
  return actions.map(action => initializeActionWithVerbs(action, languageProvider));
}

/**
 * Register an action with the registry after initializing its verbs
 * This is a convenience function that combines initialization and registration
 * 
 * @param action The action definition to register
 * @param languageProvider The language provider to get verb lists from
 * @param registry The action registry to register with
 */
export function registerActionWithVerbs(
  action: ActionDefinition,
  languageProvider: LanguageProvider,
  registry: { register(action: ActionDefinition): void }
): void {
  const initializedAction = initializeActionWithVerbs(action, languageProvider);
  registry.register(initializedAction);
}

/**
 * Register multiple actions with the registry after initializing their verbs
 * 
 * @param actions Array of action definitions to register
 * @param languageProvider The language provider to get verb lists from
 * @param registry The action registry to register with
 */
export function registerActionsWithVerbs(
  actions: ActionDefinition[],
  languageProvider: LanguageProvider,
  registry: { register(action: ActionDefinition): void }
): void {
  actions.forEach(action => registerActionWithVerbs(action, languageProvider, registry));
}

