// packages/stdlib/src/world-model/types/game-context.ts

import { ExecutionContext } from '../../core-imports';
import { Entity } from './entity';
import { IFWorld } from '../if-world';

/**
 * Game-specific execution context for Interactive Fiction
 * Extends the core ExecutionContext with IF-specific functionality
 */
export interface GameContext extends ExecutionContext {
  /**
   * The game world
   */
  world: IFWorld;

  /**
   * The player entity
   */
  player: Entity;

  /**
   * The current location
   */
  currentLocation: Entity;

  /**
   * Check if an entity is visible from the current context
   */
  isVisible(entityId: string): boolean;

  /**
   * Check if an entity is accessible (can be touched/manipulated)
   */
  isAccessible(entityId: string): boolean;

  /**
   * Find the location of an entity
   */
  findLocationOf(entityId: string): string | undefined;

  /**
   * Get entities in scope for the current command
   */
  getScope(): Entity[];

  /**
   * Get the turn number
   */
  getTurnNumber(): number;

  /**
   * Additional game state
   */
  gameState?: Record<string, any>;
}

/**
 * Factory function to create a game context
 */
export function createGameContext(params: {
  world: IFWorld;
  player: Entity;
  textService: ExecutionContext['textService'];
  languageProvider: ExecutionContext['languageProvider'];
}): GameContext {
  const { world, player, textService, languageProvider } = params;

  // Get current location
  const locationId = world.getEntityLocation(player.id);
  const currentLocation = locationId ? world.getEntity(locationId) : player;

  return {
    world,
    player,
    currentLocation,
    textService,
    languageProvider,

    isVisible(entityId: string): boolean {
      return world.isVisible(player.id, entityId);
    },

    isAccessible(entityId: string): boolean {
      return world.isAccessible(player.id, entityId);
    },

    findLocationOf(entityId: string): string | undefined {
      return world.getEntityLocation(entityId);
    },

    getScope(): Entity[] {
      return world.getEntitiesInScope(player.id);
    },

    getTurnNumber(): number {
      return world.getTurnNumber();
    }
  };
}

