export { TextService, createTextService } from './events/text-processor';

// Re-export from channels module
export * from './channels';

// Re-export from extensions module
export { 
  Extension, 
  CommandExtension, 
  AbilityExtension, 
  ChannelExtension, 
  EventExtension, 
  ParserExtension,
  ExtensionType,
  AnyExtension 
} from './extensions/types';
export { ExtensionRegistry, createExtensionRegistry } from './extensions/registry';

// Re-export from rules module
export {
  // Types
  RuleWorld,
  RuleResult,
  EntityChange,
  Rule,
  SimpleRuleSystem,
  // Functions
  createSimpleRuleSystem,
  createSimpleRuleWorld,
  // Helpers
  getTargetItem,
