#!/usr/bin/env pwsh
# Analyze imports in StdLib to find direct Core imports

Write-Host "Analyzing StdLib imports from Core..." -ForegroundColor Cyan
Write-Host ""

# Find all TypeScript files in stdlib
$files = Get-ChildItem -Path "packages/stdlib/src" -Recurse -Filter "*.ts" | Where-Object { $_.Name -notmatch "\.d\.ts$" }

$directImports = @{}
$coreImportsUsage = @{}

foreach ($file in $files) {
    $content = Get-Content $file.FullName -Raw
    $relativePath = $file.FullName.Replace((Get-Location).Path, "").Replace("\", "/").TrimStart("/")

    # Find direct @sharpee/core imports
    $matches = [regex]::Matches($content, "from\s+['""]@sharpee/core(/[^'""]+)?['""]")
    if ($matches.Count -gt 0) {
        $directImports[$relativePath] = @()
        foreach ($match in $matches) {
            $importPath = $match.Value -replace "from\s+['""]|['""]", ""
            $directImports[$relativePath] += $importPath
        }
    }

    # Find imports from core-imports.ts
    if ($content -match "from\s+['""][./]*core-imports['""]") {
        $coreImportsUsage[$relativePath] = $true
    }
}

Write-Host "Files with direct @sharpee/core imports:" -ForegroundColor Yellow
Write-Host "========================================" -ForegroundColor Yellow
foreach ($file in $directImports.Keys | Sort-Object) {
    Write-Host "`n$file" -ForegroundColor Cyan
    foreach ($import in $directImports[$file]) {
        Write-Host "  - $import" -ForegroundColor Red
    }
}

Write-Host "`n`nFiles using core-imports.ts (good practice):" -ForegroundColor Green
Write-Host "===========================================" -ForegroundColor Green
foreach ($file in $coreImportsUsage.Keys | Sort-Object) {
    Write-Host "   $file" -ForegroundColor Green
}

# Analyze what's being imported
Write-Host "`n`nAnalyzing import patterns..." -ForegroundColor Cyan
$importCategories = @{
    "Entity/Type System" = @("Entity", "EntityId", "EntityCreationParams", "RelationshipType")
    "Events" = @("SemanticEvent", "EventEmitter", "EventSource")
    "Language" = @("LanguageProvider", "ListFormatOptions")
    "Parser" = @("Parser", "ParsedCommand", "Grammar")
    "Execution" = @("GameContext", "CommandHandler", "ExecutionContext")
    "Rules" = @("Rule", "RuleSystem", "RuleContext")
}

foreach ($category in $importCategories.Keys) {
    Write-Host "`n$category imports:" -ForegroundColor Yellow
    $found = $false
    foreach ($file in $directImports.Keys) {
        $content = Get-Content (Join-Path (Get-Location) $file) -Raw
        foreach ($type in $importCategories[$category]) {
            if ($content -match "import\s+.*\b$type\b.*from\s+['""]@sharpee/core") {
                Write-Host "  - $type in $file" -ForegroundColor Red
                $found = $true
            }
        }
    }
    if (-not $found) {
        Write-Host "  None found" -ForegroundColor Green
    }
}

# Summary
$totalFiles = $files.Count
$directImportCount = $directImports.Count
$coreImportsCount = $coreImportsUsage.Count

Write-Host "`n`nSummary:" -ForegroundColor Cyan
Write-Host "========" -ForegroundColor Cyan
Write-Host "Total TypeScript files in StdLib: $totalFiles"
Write-Host "Files with direct @sharpee/core imports: $directImportCount" -ForegroundColor Red
Write-Host "Files using core-imports.ts: $coreImportsCount" -ForegroundColor Green
Write-Host "Files that need refactoring: $directImportCount" -ForegroundColor Yellow

// packages/stdlib/src/stdlib-imports.ts
/**
 * Controlled import boundary for StdLib
 * This file defines what StdLib imports from Core and re-exports for internal use.
 * 
 * IMPORTANT: No other file in StdLib should import directly from @sharpee/core!
 * All Core imports should go through this file.
 */

// ============================================================================
// Event System (Infrastructure - OK to import)
// ============================================================================
export {
  SemanticEvent,
  EventSource,
  EventListener,
  EventEmitter,
  createEvent,
  createEventSource,
  createEventEmitter,
  StandardEventTypes,
  StandardEventTags,
  EventCategories
} from '@sharpee/core/events';

// ============================================================================
// Channel System (Infrastructure - OK to import)
// ============================================================================
export {
  Channel,
  ChannelDefinition,
  ChannelManager,
  ChannelMessage,
  createChannelManager,
  createTextFormatter
} from '@sharpee/core/channels';

// ============================================================================
// Extension System (Infrastructure - OK to import)
// ============================================================================
export {
  Extension,
  AnyExtension,
  ExtensionType
} from '@sharpee/core/extensions';

// ============================================================================
// Language System (Infrastructure - OK to import)
// ============================================================================
export {
  LanguageProvider,
  ListFormatOptions,
  LanguageMetadata
} from '@sharpee/core/language';

// ============================================================================
// TEMPORARY: Entity/Type imports (These should be moved to StdLib)
// ============================================================================
// TODO: These imports indicate a design flaw. Entity concepts are domain-specific
// and should not be in Core. Plan to move these to StdLib's own type system.
export {
  Entity,
  EntityId,
  EntityCreationParams,
  EntityOperationOptions
} from '@sharpee/core/types';

// ============================================================================
// TEMPORARY: Execution imports (Should be IF-specific in StdLib)
// ============================================================================
export {
  CommandResult,
  ExecutionContext,
  CommandHandler,
  CommandRouter,
  CommandExecutionOptions
} from '@sharpee/core/execution';

// ============================================================================
// DO NOT IMPORT: Parser (Moved to StdLib)
// ============================================================================
// Parser is IF-specific and lives in StdLib now

// ============================================================================
// DO NOT IMPORT: Rules (Probably should be in StdLib)
// ============================================================================
// Rules system seems IF-specific and might need to move

/**
 * Version from Core
 */
export { version } from '@sharpee/core';

#!/usr/bin/env pwsh
# Script to help migrate from direct @sharpee/core imports to stdlib-imports.ts

param(
    [switch]$DryRun = $false,
    [switch]$UpdateCoreImports = $false
)

Write-Host "Sharpee Import Migration Tool" -ForegroundColor Cyan
Write-Host "=============================" -ForegroundColor Cyan
Write-Host ""

if ($DryRun) {
    Write-Host "Running in DRY RUN mode - no files will be modified" -ForegroundColor Yellow
}

# Define import mappings
$importMappings = @{
    '@sharpee/core/types' = '../stdlib-imports'
    '@sharpee/core/events' = '../stdlib-imports'
    '@sharpee/core/channels' = '../stdlib-imports'
    '@sharpee/core/extensions' = '../stdlib-imports'
    '@sharpee/core/language' = '../stdlib-imports'
    '@sharpee/core/execution' = '../stdlib-imports'
    '@sharpee/core' = '../stdlib-imports'
}

# Find all TypeScript files in stdlib
$files = Get-ChildItem -Path "packages/stdlib/src" -Recurse -Filter "*.ts" | 
    Where-Object { 
        $_.Name -notmatch "\.d\.ts$" -and 
        $_.Name -ne "stdlib-imports.ts" -and
        $_.Name -ne "core-imports.ts"
    }

$updatedFiles = 0
$skippedFiles = 0

foreach ($file in $files) {
    $content = Get-Content $file.FullName -Raw
    $originalContent = $content
    $updated = $false

    # Calculate relative path from file to stdlib-imports.ts
    $fileDir = Split-Path $file.FullName -Parent
    $stdlibSrc = Join-Path (Get-Location) "packages/stdlib/src"
    $relativePath = [System.IO.Path]::GetRelativePath($fileDir, $stdlibSrc)
    $importPath = if ($relativePath -eq ".") { "./stdlib-imports" } else { "$relativePath/stdlib-imports".Replace("\", "/") }

    # Replace direct Core imports
    foreach ($coreImport in $importMappings.Keys) {
        $pattern = "from\s+['""]$([regex]::Escape($coreImport))(/[^'""]*)?['""]"
        if ($content -match $pattern) {
            $content = $content -replace $pattern, "from '$importPath'"
            $updated = $true
        }
    }

    # Update core-imports references if requested
    if ($UpdateCoreImports) {
        $coreImportPattern = "from\s+['""][./]*core-imports['""]"
        if ($content -match $coreImportPattern) {
            # Calculate path to stdlib-imports from current file
            $newImportPath = $importPath
            $content = $content -replace $coreImportPattern, "from '$newImportPath'"
            $updated = $true
        }
    }

    if ($updated) {
        if (-not $DryRun) {
            Set-Content -Path $file.FullName -Value $content -NoNewline
        }
        Write-Host "Updated: $($file.FullName.Replace((Get-Location).Path, '').TrimStart('\'))" -ForegroundColor Green
        $updatedFiles++
    } else {
        $skippedFiles++
    }
}

Write-Host "`n`nSummary:" -ForegroundColor Cyan
Write-Host "========" -ForegroundColor Cyan
Write-Host "Files processed: $($files.Count)"
Write-Host "Files updated: $updatedFiles" -ForegroundColor Green
Write-Host "Files skipped: $skippedFiles" -ForegroundColor Gray

if ($DryRun) {
    Write-Host "`nThis was a DRY RUN. To apply changes, run without -DryRun flag" -ForegroundColor Yellow
}

if (-not $UpdateCoreImports) {
    Write-Host "`nNote: core-imports.ts references were not updated." -ForegroundColor Yellow
    Write-Host "To also update those, use -UpdateCoreImports flag" -ForegroundColor Yellow
}

# Additional recommendations
Write-Host "`n`nNext Steps:" -ForegroundColor Cyan
Write-Host "1. Review stdlib-imports.ts to ensure all needed imports are present"
Write-Host "2. Run 'npm run build' to check for any missing imports"
Write-Host "3. Consider moving Entity/Type concepts from Core to StdLib"
Write-Host "4. Update tests to use new import structure"

