#!/usr/bin/env pwsh
# Update StdLib imports to use core-imports.ts instead of direct Core imports
# Aligned with refactor plan (2025-06-15)

param(
    [switch]$DryRun = $false,
    [switch]$Verbose = $false
)

Write-Host "StdLib Import Migration - Aligned with Refactor Plan" -ForegroundColor Cyan
Write-Host "====================================================" -ForegroundColor Cyan
Write-Host ""

if ($DryRun) {
    Write-Host "Running in DRY RUN mode - no files will be modified" -ForegroundColor Yellow
    Write-Host ""
}

# Find all TypeScript files in stdlib (excluding generated and special files)
$files = Get-ChildItem -Path "packages/stdlib/src" -Recurse -Filter "*.ts" | 
    Where-Object { 
        $_.Name -notmatch "\.d\.ts$" -and 
        $_.Name -ne "core-imports.ts" -and
        $_.Name -ne "stdlib-imports.ts" -and
        $_.DirectoryName -notmatch "node_modules"
    }

Write-Host "Found $($files.Count) TypeScript files to process" -ForegroundColor Gray
Write-Host ""

$updatedFiles = 0
$errorFiles = 0
$importStats = @{}

foreach ($file in $files) {
    try {
        $content = Get-Content $file.FullName -Raw
        $originalContent = $content
        $updated = $false
        $fileUpdates = @()

        # Calculate relative path from file to core-imports.ts
        $fileDir = Split-Path $file.FullName -Parent
        $coreImportsPath = Join-Path (Get-Location) "packages/stdlib/src/core-imports.ts"
        $coreImportsDir = Split-Path $coreImportsPath -Parent
        $relativePath = [System.IO.Path]::GetRelativePath($fileDir, $coreImportsDir)
        $importPath = if ($relativePath -eq ".") { "./core-imports" } else { "$relativePath/core-imports".Replace("\", "/") }

        # Find all @sharpee/core imports
        $pattern = "from\s+['""]@sharpee/core(/[^'""]*)?['""]"
        $matches = [regex]::Matches($content, $pattern)

        foreach ($match in $matches) {
            $coreImport = $match.Value
            $coreModule = $coreImport -replace "from\s+['""]@sharpee/core/?|['""]", ""

            # Track what modules are being imported
            if ($importStats.ContainsKey($coreModule)) {
                $importStats[$coreModule]++
            } else {
                $importStats[$coreModule] = 1
            }

            # Replace with core-imports
            $content = $content.Replace($coreImport, "from '$importPath'")
            $updated = $true
            $fileUpdates += "  - Replaced: $coreImport -> from '$importPath'"
        }

        # Also handle any imports from ../../core/src paths
        $pattern2 = "from\s+['""][./]*core/src/[^'""]+['""]"
        $matches2 = [regex]::Matches($content, $pattern2)

        foreach ($match in $matches2) {
            $content = $content.Replace($match.Value, "from '$importPath'")
            $updated = $true
            $fileUpdates += "  - Replaced: $($match.Value) -> from '$importPath'"
        }

        if ($updated) {
            if (-not $DryRun) {
                Set-Content -Path $file.FullName -Value $content -NoNewline -Encoding UTF8
            }

            $relativeName = $file.FullName.Replace((Get-Location).Path, "").Replace("\", "/").TrimStart("/")
            Write-Host " Updated: $relativeName" -ForegroundColor Green

            if ($Verbose) {
                foreach ($update in $fileUpdates) {
                    Write-Host $update -ForegroundColor DarkGray
                }
            }

            $updatedFiles++
        }
    }
    catch {
        $errorFiles++
        Write-Host " Error processing: $($file.FullName)" -ForegroundColor Red
        Write-Host "  Error: $_" -ForegroundColor Red
    }
}

Write-Host ""
Write-Host "Import Statistics:" -ForegroundColor Cyan
Write-Host "=================" -ForegroundColor Cyan
foreach ($module in $importStats.Keys | Sort-Object) {
    $moduleName = if ($module -eq "") { "(root)" } else { $module }
    Write-Host "  $moduleName : $($importStats[$module]) imports" -ForegroundColor Gray
}

Write-Host ""
Write-Host "Summary:" -ForegroundColor Cyan
Write-Host "========" -ForegroundColor Cyan
Write-Host "Files processed: $($files.Count)"
Write-Host "Files updated: $updatedFiles" -ForegroundColor Green
Write-Host "Files with errors: $errorFiles" -ForegroundColor $(if ($errorFiles -gt 0) { "Red" } else { "Gray" })

if ($DryRun) {
    Write-Host ""
    Write-Host "This was a DRY RUN. To apply changes, run:" -ForegroundColor Yellow
    Write-Host "  .\fix-stdlib-imports.ps1" -ForegroundColor Yellow
}

Write-Host ""
Write-Host "Next Steps:" -ForegroundColor Cyan
Write-Host "1. Run 'npm run build' in packages/stdlib to check for missing imports"
Write-Host "2. If imports are missing, add them to core-imports.ts"
Write-Host "3. Ensure no direct @sharpee/core imports remain (except in core-imports.ts)"
Write-Host "4. Update any broken import paths"

# Quick verification
Write-Host ""
Write-Host "Verifying remaining direct imports..." -ForegroundColor Cyan
$remaining = Get-ChildItem -Path "packages/stdlib/src" -Recurse -Filter "*.ts" | 
    Where-Object { $_.Name -ne "core-imports.ts" } |
    Select-String -Pattern "@sharpee/core"

if ($remaining.Count -eq 0) {
    Write-Host " No direct @sharpee/core imports found!" -ForegroundColor Green
} else {
    Write-Host " Found $($remaining.Count) remaining direct imports:" -ForegroundColor Yellow
    $remaining | ForEach-Object {
        Write-Host "  $($_.Filename):$($_.LineNumber)" -ForegroundColor Yellow
    }
}

#!/usr/bin/env pwsh
# Check what Core exports vs what core-imports.ts imports

Write-Host "Analyzing Core exports vs StdLib imports..." -ForegroundColor Cyan
Write-Host ""

# Read Core's main export file
$coreIndexPath = "packages/core/src/index.ts"
if (Test-Path $coreIndexPath) {
    Write-Host "Core exports from index.ts:" -ForegroundColor Yellow
    Write-Host "===========================" -ForegroundColor Yellow

    $coreContent = Get-Content $coreIndexPath -Raw

    # Find all exports
    $exportMatches = [regex]::Matches($coreContent, "export\s+.*from\s+['""]([^'""]+)['""]")
    $modules = @{}

    foreach ($match in $exportMatches) {
        $module = $match.Groups[1].Value
        if (-not $modules.ContainsKey($module)) {
            $modules[$module] = @()
        }

        # Try to extract what's being exported
        $exportLine = $match.Value
        if ($exportLine -match "export\s+\{([^}]+)\}") {
            $exports = $matches[1] -split ",\s*"
            $modules[$module] += $exports
        } elseif ($exportLine -match "export\s+\*") {
            $modules[$module] += "(all exports)"
        }
    }

    foreach ($module in $modules.Keys | Sort-Object) {
        Write-Host "`n  Module: $module" -ForegroundColor Cyan
        foreach ($export in $modules[$module]) {
            Write-Host "    - $export" -ForegroundColor Gray
        }
    }
} else {
    Write-Host "Core index.ts not found!" -ForegroundColor Red
}

Write-Host "`n`nChecking core-imports.ts..." -ForegroundColor Yellow
Write-Host "==========================" -ForegroundColor Yellow

$coreImportsPath = "packages/stdlib/src/core-imports.ts"
if (Test-Path $coreImportsPath) {
    $importsContent = Get-Content $coreImportsPath -Raw

    # Find all imports from @sharpee/core
    $importMatches = [regex]::Matches($importsContent, "from\s+['""]@sharpee/core(/[^'""]*)?['""]")

    Write-Host "Found $($importMatches.Count) imports from @sharpee/core" -ForegroundColor Green

    $importedModules = @{}
    foreach ($match in $importMatches) {
        $module = $match.Value -replace "from\s+['""]@sharpee/core/?|['""]", ""
        if ($module -eq "") { $module = "(root)" }

        if ($importedModules.ContainsKey($module)) {
            $importedModules[$module]++
        } else {
            $importedModules[$module] = 1
        }
    }

    Write-Host "`nImported modules:" -ForegroundColor Cyan
    foreach ($module in $importedModules.Keys | Sort-Object) {
        Write-Host "  - $module ($($importedModules[$module]) imports)" -ForegroundColor Gray
    }
}

Write-Host "`n`nRecommendations:" -ForegroundColor Cyan
Write-Host "================" -ForegroundColor Cyan
Write-Host "1. Ensure core-imports.ts imports everything StdLib needs from Core"
Write-Host "2. Remove any IF-specific imports (parser, world-model) as they're now in StdLib"
Write-Host "3. After running fix-stdlib-imports.ps1, check for compilation errors"
Write-Host "4. Add any missing imports to core-imports.ts"

