/**
 * Asking action - Ask someone about something
 */

import { ActionDefinition, PhaseResult } from '../core-imports';
import { IFCommand } from '../core-imports';
import { GameContext } from '../core-imports';
import { createEvent } from '../core-imports';
import { StandardActions, StandardEvents } from '../core-imports';
import { EntityTypes, StandardAttributes } from '../core-imports';

/**
 * Asking action definition
 */
export const askingAction: ActionDefinition = {
  id: StandardActions.ASKING,
  name: StandardActions.ASKING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: false,
    undoable: false,
    categories: ['communication']
  },
  phases: {
    // VALIDATE phase - Check if asking is possible
    validate: (command: IFCommand, context: GameContext): PhaseResult => {
      const constants = context.languageProvider.getConstants();

      // Must have a target (who to ask)
      if (!command.noun || command.noun.length === 0) {
        return {
          continue: false,
          events: [
            createEvent(
              constants.events.ACTION_PREVENTED,
              { 
                reason: 'no_target', 
                message: context.languageProvider.getMessage('action.asking.no_target')
              },
              { narrate: true }
            )
          ]
        };
      }

      const target = command.noun[0].entity;
      const targetId = target.id;

      // Can't ask yourself
      if (targetId === command.actor) {
        return {
          continue: false,
          events: [
            createEvent(
              constants.events.ACTION_PREVENTED,
              { 
                reason: 'ask_self',
                message: context.languageProvider.getMessage('action.asking.ask_self')
              },
              { narrate: true }
            )
          ]
        };
      }

      // Check if target is a person/NPC or animate
      if (target.type !== EntityTypes.PERSON && !target.attributes.animate && !target.attributes[StandardAttributes.CONVERSABLE]) {
        return {
          continue: false,
          events: [
            createEvent(
              constants.events.ACTION_PREVENTED,
              { 
                reason: 'not_person',
                message: context.languageProvider.getMessage('action.asking.not_person', {
                  target: target.attributes[StandardAttributes.NAME] || 'that'
                }),
                targetName: target.attributes[StandardAttributes.NAME]
              },
              { narrate: true }
            )
          ]
        };
      }

      // Check if target is in the same location
      const targetLocation = context.findLocationOf(targetId);
      if (targetLocation !== context.currentLocation.id) {
        return {
          continue: false,
          events: [
            createEvent(
              constants.events.ACTION_PREVENTED,
              { 
                reason: 'not_accessible',
                message: context.languageProvider.getMessage('action.asking.not_accessible', {
                  target: target.attributes[StandardAttributes.NAME] || 'them'
                }),
                targetName: target.attributes[StandardAttributes.NAME]
              },
              { narrate: true }
            )
          ]
        };
      }

      // Check if we have a topic (what to ask about)
      const topic = command.commandText?.includes('about') ? 
        command.commandText.split('about')[1]?.trim() : 
        command.indirect?.[0]?.entity.attributes[StandardAttributes.NAME];

      if (!topic) {
        return {
          continue: false,
          events: [
            createEvent(
              constants.events.ACTION_PREVENTED,
              { 
                reason: 'no_topic',
                message: context.languageProvider.getMessage('action.asking.no_topic')
              },
              { narrate: true }
            )
          ]
        };
      }

      // All checks passed
      return {
        continue: true,
        events: [],
        metadata: { topic }
      };
    },

    // EXECUTE phase - Actually ask
    execute: (command: IFCommand, context: GameContext): PhaseResult => {
      const constants = context.languageProvider.getConstants();
      const target = command.noun![0].entity;
      const targetId = target.id;
      const topic = command.metadata?.topic as string || '';

      // Get responses from target's attributes
      const responses = target.attributes.askResponses as Record<string, string> | undefined;
      const topics = target.attributes.topics as string[] | undefined;
      const defaultResponse = target.attributes.defaultAskResponse as string | undefined;

      // Look for a response to this topic
      let response: string | undefined;

      if (responses) {
        // Check for exact match
        response = responses[topic.toLowerCase()];

        // Check for partial matches
        if (!response) {
          for (const [key, value] of Object.entries(responses)) {
            if (topic.toLowerCase().includes(key.toLowerCase()) || 
                key.toLowerCase().includes(topic.toLowerCase())) {
              response = value;
              break;
            }
          }
        }
      }

      // Use default response if no specific response found
      if (!response) {
        response = defaultResponse || 
          context.languageProvider.getMessage('action.asking.no_knowledge', {
            person: target.attributes[StandardAttributes.NAME] || 'They'
          });
      }

      // Create success event
      const events = [
        createEvent(
          constants.events.NPC_ASKED_ABOUT,
          { 
            npcId: targetId,
            npcName: target.attributes[StandardAttributes.NAME],
            topic,
            response,
            knownTopics: topics
          },
          { 
            actor: command.actor,
            location: context.currentLocation.id,
            narrate: true
          }
        )
      ];

      return {
        continue: true,
        success: true,
        events,
        context
      };
    }
  }
};

// packages/stdlib/src/constants/if-entity-types.ts

/**
 * Interactive Fiction entity types
 * These extend the core entity system with IF-specific concepts
 */
export enum IFEntityType {
  // Location entities
  ROOM = 'if.room',
  REGION = 'if.region',

  // Object entities
  THING = 'if.thing',
  CONTAINER = 'if.container',
  SUPPORTER = 'if.supporter',
  DOOR = 'if.door',
  BACKDROP = 'if.backdrop',

  // Character entities
  PERSON = 'if.person',
  PLAYER = 'if.player',
  NPC = 'if.npc',

  // Device entities
  DEVICE = 'if.device',
  SWITCH = 'if.switch',

  // Special entities
  DIRECTION = 'if.direction',
  SCENE = 'if.scene'
}

/**
 * Type guards for IF entity types
 */
export function isLocation(type: string): boolean {
  return type === IFEntityType.ROOM || type === IFEntityType.REGION;
}

export function isObject(type: string): boolean {
  return [
    IFEntityType.THING,
    IFEntityType.CONTAINER,
    IFEntityType.SUPPORTER,
    IFEntityType.DOOR,
    IFEntityType.BACKDROP,
    IFEntityType.DEVICE,
    IFEntityType.SWITCH
  ].includes(type as IFEntityType);
}

export function isCharacter(type: string): boolean {
  return [
    IFEntityType.PERSON,
    IFEntityType.PLAYER,
    IFEntityType.NPC
  ].includes(type as IFEntityType);
}

export function canContainThings(type: string): boolean {
  return [
    IFEntityType.ROOM,
    IFEntityType.CONTAINER,
    IFEntityType.SUPPORTER,
    IFEntityType.PERSON,
    IFEntityType.PLAYER,
    IFEntityType.NPC
  ].includes(type as IFEntityType);
}

// packages/stdlib/src/actions/types/index.ts

import { IFCommand } from '../../parser/if-parser-types';
import { GameContext } from '../../world-model/types';

/**
 * Base action definition for the action system
 * This is an IF concept that belongs in StdLib, not Core
 */
export interface ActionDefinition {
  /**
   * Unique identifier for the action
   */
  id: string;

  /**
   * Human-readable name for the action
   */
  name: string;

  /**
   * List of verbs that trigger this action
   * These are populated by the language provider
   */
  verbs?: string[];

  /**
   * Action metadata
   */
  metadata?: {
    /**
     * Whether this action is reversible
     */
    reversible?: boolean;

    /**
     * Category of the action
     */
    category?: string;

    /**
     * Additional metadata
     */
    [key: string]: any;
  };

  /**
   * Action execution phases
   */
  phases: {
    /**
     * Check if the action can be performed
     */
    check?: (command: IFCommand, context: GameContext) => boolean | string;

    /**
     * Carry out the action
     */
    carry?: (command: IFCommand, context: GameContext) => void;

    /**
     * Report the result of the action
     */
    report?: (command: IFCommand, context: GameContext) => string | void;
  };
}

/**
 * Action phase names
 */
export enum ActionPhase {
  CHECK = 'check',
  CARRY = 'carry',
  REPORT = 'report'
}

/**
 * Result of an action phase
 */
export interface ActionPhaseResult {
  /**
   * Whether to continue to the next phase
   */
  continue: boolean;

  /**
   * Message to display (if any)
   */
  message?: string;

  /**
   * Events generated during this phase
   */
  events?: any[];
}

