/**
 * Asking action - Ask someone about something
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Asking action definition
 */
export const askingAction: ActionDefinition = {
  id: IFActions.ASKING,
  name: IFActions.ASKING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: false,
    undoable: false,
    category: 'communication'
  },
  phases: {
    // VALIDATE phase - Check if asking is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have a target (who to ask)
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.asking.no_target');
      }

      const target = command.noun[0].entity;
      const targetId = target.id;

      // Can't ask yourself
      if (targetId === command.actor) {
        return context.languageProvider.formatMessage('action.asking.ask_self');
      }

      // Check if target is a person/NPC or animate
      if (target.type !== IFEntityType.PERSON && 
          target.type !== IFEntityType.NPC &&
          !target.attributes.animate && 
          !target.attributes.conversable) {
        return context.languageProvider.formatMessage('action.asking.not_person', {
          target: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if target is in the same location
      const targetLocation = context.findLocationOf(targetId);
      if (targetLocation !== context.currentLocation.id) {
        return context.languageProvider.formatMessage('action.asking.not_accessible', {
          target: target.attributes[IFAttributes.NAME] || 'them'
        });
      }

      // Check if we have a topic (what to ask about)
      // Since IFCommand doesn't have commandText or indirect, we need to get the topic differently
      // The topic would typically be in the second noun or the text field
      let topic: string | undefined;

      if (command.second && command.second.length > 0) {
        // "ask bob about treasure" - treasure is the second noun
        topic = command.second[0].entity.attributes[IFAttributes.NAME] as string;
      } else if (command.text) {
        // "ask bob about the weather" - might be parsed as text
        topic = command.text;
      } else if (command.originalInput.includes('about')) {
        // Try to extract from original input
        const aboutIndex = command.originalInput.indexOf('about');
        topic = command.originalInput.substring(aboutIndex + 5).trim();
      }

      if (!topic) {
        return context.languageProvider.formatMessage('action.asking.no_topic');
      }

      // Store topic for execute phase - we'll use the world's state for this
      // since IFCommand doesn't have metadata
      context.world.updateEntity(targetId, {
        attributes: {
          ...target.attributes,
          _pendingAskTopic: topic
        }
      });

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually ask and emit events
    execute: (command: IFCommand, context: GameContext): void => {
      const target = command.noun[0].entity;
      const targetId = target.id;

      // Retrieve the topic we stored
      const updatedTarget = context.world.getEntity(targetId);
      const topic = updatedTarget?.attributes._pendingAskTopic as string || '';

      // Clean up the temporary attribute
      if (updatedTarget) {
        const { _pendingAskTopic, ...cleanAttributes } = updatedTarget.attributes;
        context.world.updateEntity(targetId, {
          attributes: cleanAttributes
        });
      }

      // Get responses from target's attributes
      const responses = target.attributes.askResponses as Record<string, string> | undefined;
      const topics = target.attributes.topics as string[] | undefined;
      const defaultResponse = target.attributes.defaultAskResponse as string | undefined;

      // Look for a response to this topic
      let response: string | undefined;

      if (responses) {
        // Check for exact match
        response = responses[topic.toLowerCase()];

        // Check for partial matches
        if (!response) {
          for (const [key, value] of Object.entries(responses)) {
            if (topic.toLowerCase().includes(key.toLowerCase()) || 
                key.toLowerCase().includes(topic.toLowerCase())) {
              response = value;
              break;
            }
          }
        }
      }

      // Use default response if no specific response found
      if (!response) {
        response = defaultResponse || 
          context.languageProvider.formatMessage('action.asking.no_knowledge', {
            person: target.attributes[IFAttributes.NAME] || 'They'
          });
      }

      // Emit the asking event - the text service will handle formatting
      const event = createEvent(
        IFEvents.NPC_ASKED_ABOUT,
        { 
          action: IFActions.ASKING,
          actor: command.actor,
          target: targetId,
          targetName: target.attributes[IFAttributes.NAME],
          topic,
          response,
          knownTopics: topics
        },
        { 
          narrate: true,
          location: context.currentLocation.id
        }
      );

      // We need to emit this through the game's event system, not the world's
      // The world only emits world-model events like entity:created
      // We'll use the context's event emitter for game events
      if (context.world && 'emitGameEvent' in context.world) {
        (context.world as any).emitGameEvent(event);
      } else {
        // Fallback - emit as a generic event that the text processor can handle
        context.textService.processEvent(event);
      }
    }
  }
};

