    // VALIDATE phase - Check if dropping is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.dropping.no_target');
      }

      const target = command.noun[0].entity;
      const targetId = target.id;

      // Can't drop yourself
      if (targetId === command.actor) {
        return context.languageProvider.formatMessage('action.dropping.cannot_drop_self');
      }

      // Check if held by the actor
      const heldLocation = context.world.getLocation(targetId);
      if (heldLocation !== command.actor) {
        return context.languageProvider.formatMessage('action.dropping.not_held', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if droppable (default to true if not specified)
      const droppable = target.attributes.droppable !== false;
      if (!droppable) {
        return context.languageProvider.formatMessage('action.dropping.not_droppable', {
          item: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually drop the object
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun || command.noun.length === 0) return [];

      const target = command.noun[0].entity;
      const targetId = target.id;
      const events: SemanticEvent[] = [];

      // Move the item to the current location
      try {
        context.world.moveEntity(targetId, context.currentLocation.id);

        // Create success event
        events.push(createEvent(
          IFEvents.ITEM_DROPPED,
          { 
            itemId: targetId,
            itemName: target.attributes[IFAttributes.NAME],
            location: context.currentLocation.id,
            locationName: context.currentLocation.attributes[IFAttributes.NAME]
          },
          { 
            narrate: true,
            location: context.currentLocation.id
          }
        ));
      } catch (error) {
        // If the move fails, report the error
        events.push(createEvent(
          IFEvents.ACTION_FAILED,
          { 
            action: IFActions.DROPPING,
            reason: 'move_failed',
            message: error instanceof Error ? error.message : 'Failed to drop item'
          },
          { narrate: true }
        ));
      }

      return events;
    }
  }
};

