/**
 * Going action - Move in a direction
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';
import { Direction } from '../world-model/if-entities/relationships';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Going action definition
 * 
 * Handles "go [direction]" and direction commands
 */
export const goingAction: ActionDefinition = {
  id: IFActions.GOING,
  name: IFActions.GOING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'movement'
  },
  phases: {
    // VALIDATE phase - Check if going is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have a direction
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.going.no_direction');
      }

      const directionEntity = command.noun[0].entity;

      // Check if it's actually a direction
      if (directionEntity.type !== IFEntityType.DIRECTION) {
        return context.languageProvider.formatMessage('action.going.not_a_direction', {
          direction: directionEntity.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Get the direction enum value
      const direction = directionEntity.id as Direction;

      // Check if there's an exit in that direction
      const destinationId = context.world.getRoomInDirection(context.currentLocation.id, direction);

      if (!destinationId) {
        return context.languageProvider.formatMessage('action.going.no_exit', {
          direction: directionEntity.attributes[IFAttributes.NAME] || 'that way'
        });
      }

      // Check if the exit is blocked (room attribute)
      const exitBlockedAttr = `${direction}_blocked`;
      if (context.currentLocation.attributes[exitBlockedAttr] === true) {
        const blockedMessage = context.currentLocation.attributes[`${direction}_blocked_message`] as string ||
          context.languageProvider.formatMessage('action.going.exit_blocked', {
            direction: directionEntity.attributes[IFAttributes.NAME] || 'that way'
          });

        return blockedMessage;
      }

      // Check if there's a door in that direction
      const doorAttr = `${direction}_door`;
      const doorId = context.currentLocation.attributes[doorAttr] as string;

      if (doorId) {
        const door = context.world.getEntity(doorId);
        if (door && door.attributes[IFAttributes.OPEN] === false) {
          return context.languageProvider.formatMessage('action.going.door_closed', {
            door: door.attributes[IFAttributes.NAME] || 'door'
          });
        }
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually move the player
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun || command.noun.length === 0) return [];

      const directionEntity = command.noun[0].entity;
      const direction = directionEntity.id as Direction;
      const events: SemanticEvent[] = [];

      // Get destination
      const destinationId = context.world.getRoomInDirection(context.currentLocation.id, direction);
      if (!destinationId) {
        events.push(createEvent(
          IFEvents.ACTION_FAILED,
          { 
            action: IFActions.GOING,
            reason: 'destination_not_found',
            message: context.languageProvider.formatMessage('action.going.destination_error')
          },
          { narrate: true }
        ));
        return events;
      }

      const destination = context.world.getEntity(destinationId);
      if (!destination) {
        events.push(createEvent(
          IFEvents.ACTION_FAILED,
          { 
            action: IFActions.GOING,
            reason: 'destination_not_found',
            message: context.languageProvider.formatMessage('action.going.destination_error')
          },
          { narrate: true }
        ));
        return events;
      }

      // Move the player
      try {
        context.world.moveEntity(command.actor, destinationId);

        // Create movement event
        events.push(createEvent(
          IFEvents.PLAYER_MOVED,
          { 
            from: context.currentLocation.id,
            fromName: context.currentLocation.attributes[IFAttributes.NAME],
            to: destinationId,
            toName: destination.attributes[IFAttributes.NAME],
            direction: direction,
            directionName: directionEntity.attributes[IFAttributes.NAME]
          },
          { 
            narrate: false, // Movement itself doesn't narrate
            location: destinationId
          }
        ));

        // Check if this is the first time visiting
        const visited = destination.attributes[IFAttributes.VISITED];
        if (!visited) {
          context.world.updateEntity(destinationId, {
            attributes: {
              ...destination.attributes,
              [IFAttributes.VISITED]: true
            }
          });

          events.push(createEvent(
            IFEvents.ROOM_FIRST_ENTERED,
            {
              roomId: destinationId,
              roomName: destination.attributes[IFAttributes.NAME]
            },
            { narrate: false }
          ));
        }

        // Always emit room entered event
        events.push(createEvent(
          IFEvents.ROOM_ENTERED,
          {
            roomId: destinationId,
            roomName: destination.attributes[IFAttributes.NAME]
          },
          { narrate: false }
        ));

        // Create room description event
        events.push(createEvent(
          IFEvents.ROOM_DESCRIBED,
          {
            roomId: destinationId,
            roomName: destination.attributes[IFAttributes.NAME],
            roomDescription: destination.attributes[IFAttributes.DESCRIPTION],
            firstTime: !visited
          },
          {
            narrate: true,
            location: destinationId
          }
        ));

      } catch (error) {
        events.push(createEvent(
          IFEvents.ACTION_FAILED,
          { 
            action: IFActions.GOING,
            reason: 'move_failed',
            message: error instanceof Error ? error.message : 'Failed to move'
          },
          { narrate: true }
        ));
      }

      return events;
    }
  }
};

