/**
 * Putting action - Put an object in or on another object
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';
import { IFAttributes } from '../constants/if-attributes';
import { IFRelationship } from '../constants/if-relationships';

/**
 * Putting action definition
 * 
 * Handles "put [object] in [container]", "put [object] on [supporter]", "place [object] in/on [target]" commands
 */
export const puttingAction: ActionDefinition = {
  id: IFActions.PUTTING,
  name: IFActions.PUTTING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if putting is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have a direct object
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.putting.no_target');
      }

      // Must have an indirect object (what to put it in/on)
      if (!command.preposition || !command.indirect || command.indirect.length === 0) {
        return context.languageProvider.formatMessage('action.putting.no_container');
      }

      const item = command.noun[0].entity;
      const container = command.indirect[0].entity;
      const preposition = command.preposition;

      // Can't put something in/on itself
      if (item.id === container.id) {
        return context.languageProvider.formatMessage('action.putting.put_in_self', {
          item: item.attributes[IFAttributes.NAME] || 'it'
        });
      }

      // Check if player is holding the item
      if (!context.isCarriedBy(item.id, command.actor)) {
        return context.languageProvider.formatMessage('action.putting.not_held', {
          item: item.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if container is a valid container or supporter
      const isContainer = container.type === IFEntityType.CONTAINER || 
                         container.attributes[IFAttributes.CONTAINER] === true;
      const isSupporter = container.attributes[IFAttributes.SUPPORTER] === true;

      if (!isContainer && !isSupporter) {
        return context.languageProvider.formatMessage('action.putting.not_container', {
          container: container.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // If putting IN something, it must be a container
      if (preposition === 'in' && !isContainer) {
        return context.languageProvider.formatMessage('action.putting.not_container', {
          container: container.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // If putting ON something, it must be a supporter
      if (preposition === 'on' && !isSupporter) {
        return context.languageProvider.formatMessage('action.putting.not_supporter', {
          supporter: container.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // If putting in a container, check if it's open
      if (preposition === 'in' && isContainer && 
          container.attributes[IFAttributes.OPENABLE] && 
          !container.attributes[IFAttributes.OPEN]) {
        return context.languageProvider.formatMessage('action.putting.container_closed', {
          container: container.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if container is full
      if (container.attributes[IFAttributes.CAPACITY]) {
        const currentContents = context.world.getContents(container.id);
        const capacity = container.attributes[IFAttributes.CAPACITY] as number;
        if (currentContents.length >= capacity) {
          return context.languageProvider.formatMessage('action.putting.wont_fit', {
            item: item.attributes[IFAttributes.NAME] || 'that',
            container: container.attributes[IFAttributes.NAME] || 'it'
          });
        }
      }

      // Check if container is accessible
      if (!context.isAccessible(container.id)) {
        return context.languageProvider.formatMessage('action.putting.not_accessible', {
          container: container.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually put the object
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun || !command.indirect || !command.preposition) return [];

      const item = command.noun[0].entity;
      const container = command.indirect[0].entity;
      const preposition = command.preposition;
      const events: SemanticEvent[] = [];

      // Move the item from player to container
      context.world.moveEntity(item.id, container.id);

      // Create the appropriate event based on whether it's a container or supporter
      const eventType = preposition === 'in' ? IFEvents.ITEM_PUT_IN : IFEvents.ITEM_PUT_ON;

      events.push(createEvent(
        eventType,
        {
          itemId: item.id,
          itemName: item.attributes[IFAttributes.NAME],
          containerId: container.id,
          containerName: container.attributes[IFAttributes.NAME],
          preposition: preposition,
          isContainer: container.attributes[IFAttributes.CONTAINER] === true,
          isSupporter: container.attributes[IFAttributes.SUPPORTER] === true
        },
        {
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      return events;
    }
  }
};

/**
 * IF-specific parser interfaces
 */

import { Entity, EntityId } from '../world-model/types';

/**
 * Represents a scored match for an entity
 */
export interface ScoredMatch {
  entity: Entity;
  score: number;
  matchedWords: string[];
  matchType: MatchType;
  disambiguation?: string; // e.g., "the brass one"
}

export enum MatchType {
  EXACT = 'exact',
  PARTIAL = 'partial',
  SYNONYM = 'synonym',
  PRONOUN = 'pronoun',
  IMPLIED = 'implied'
}

/**
 * Scope context for command parsing
 */
export interface ScopeContext {
  visible: Set<EntityId>;
  reachable: Set<EntityId>;
  known: Set<EntityId>;
  special?: Set<EntityId>; // Command-specific scope
  recentlyMentioned: EntityId[];
}

/**
 * Grammar pattern definition
 */
export interface GrammarPattern {
  id: string;
  pattern: string;              // e.g., "take|get|grab <noun>"
  action: string;               // e.g., "taking"
  prepositions?: string[];      // e.g., ["from", "off of"]
  reversed?: boolean;           // e.g., "take <noun> from <second>"
  matchAll?: boolean;           // e.g., "take all"
  requiresSecond?: boolean;     // Must have a second noun
  allowsImplicitSecond?: boolean; // e.g., "unlock door" implies "with key"
}

/**
 * A parsed IF command
 */
export interface IFCommand {
  action: string;                    // Canonical action name
  noun?: ScoredMatch[];              // Possible matches for first noun
  second?: ScoredMatch[];            // Possible matches for second noun
  actor: EntityId;                   // Who's performing (usually player)
  preposition?: string;              // Used preposition
  text?: string;                     // Free text (for "say" commands)
  pattern: GrammarPattern;           // Which pattern matched
  originalInput: string;
  confidence: number;                // How confident in the parse
  matchAll?: boolean;                // Command applies to all matching objects
}

/**
 * Parser result that may need disambiguation
 */
export interface ParseResult {
  success: boolean;
  commands: IFCommand[];
  needsDisambiguation?: DisambiguationRequest;
  error?: string;
}

/**
 * Request for disambiguation from player
 */
export interface DisambiguationRequest {
  prompt: string;
  options: Array<{
    entity: Entity;
    description: string;
  }>;
  context: 'noun' | 'second';
  originalCommand: IFCommand;
}

/**
 * Interface for IF-specific parsers
 */
export interface IFParser {
  /**
   * Parse input into IF commands
   */
  parse(
    input: string,
    scope: ScopeContext,
    getEntity: (id: EntityId) => Entity | undefined
  ): ParseResult;

  /**
   * Continue parsing after disambiguation
   */
  continueWithDisambiguation(
    original: IFCommand,
    choice: EntityId,
    context: 'noun' | 'second'
  ): IFCommand;

  /**
   * Add custom grammar patterns
   */
  addGrammar(pattern: GrammarPattern): void;

  /**
   * Get all registered patterns
   */
  getGrammarPatterns(): GrammarPattern[];
}

/**
 * Match scoring configuration
 */
export interface ScoringConfig {
  exactMatch: number;
  partialMatch: number;
  synonymMatch: number;
  adjectiveMatch: number;
  visibleBonus: number;
  reachableBonus: number;
  recentlyMentionedBonus: number;
  pronounPenalty: number;
}

/**
 * Language-specific parser configuration
 */
export interface IFParserConfig {
  articles: string[];              // ["a", "an", "the"]
  conjunctions: string[];          // ["and", "then", "but"]
  pronouns: string[];              // ["it", "them", "him", "her"]
  implicitPrepositions: Map<string, string>; // "unlock door" â†’ "with"
  directions: string[];            // ["north", "south", "up", "down"]
  scoring: ScoringConfig;
}

