/**
 * Giving action - Give an object to someone
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';

/**
 * Giving action definition
 * 
 * Handles "give [object] to [person]", "offer [object] to [person]" commands
 */
export const givingAction: ActionDefinition = {
  id: IFActions.GIVING,
  name: IFActions.GIVING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'interaction'
  },
  phases: {
    // VALIDATE phase - Check if giving is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have a direct object
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.giving.no_target');
      }

      // Must have a second object (who to give it to)
      if (!command.second || command.second.length === 0) {
        return context.languageProvider.formatMessage('action.giving.no_recipient');
      }

      const item = command.noun[0].entity;
      const itemId = item.id;
      const recipient = command.second[0].entity;
      const recipientId = recipient.id;

      // Can't give something to itself
      if (itemId === recipientId) {
        return context.languageProvider.formatMessage('action.giving.self_reference');
      }

      // Can't give to yourself
      if (recipientId === command.actor) {
        return context.languageProvider.formatMessage('action.giving.give_to_self', {
          item: item.attributes.name || 'that'
        });
      }

      // Check if player is holding the item
      if (!context.isCarriedBy(itemId, command.actor)) {
        return context.languageProvider.formatMessage('action.giving.not_held', {
          item: item.attributes.name || 'that'
        });
      }

      // Check if recipient is a person or NPC
      if (recipient.type !== IFEntityType.PERSON && !recipient.attributes.animate) {
        return context.languageProvider.formatMessage('action.giving.not_person', {
          recipient: recipient.attributes.name || 'that'
        });
      }

      // Check if recipient is accessible (in same location)
      if (!context.isVisible(recipientId)) {
        return context.languageProvider.formatMessage('action.giving.recipient_not_accessible', {
          recipient: recipient.attributes.name || 'them'
        });
      }

      // Check if recipient will accept the item (custom logic)
      const refusedItems = recipient.attributes.refuses;
      if (refusedItems && Array.isArray(refusedItems)) {
        if (refusedItems.includes(itemId)) {
          return context.languageProvider.formatMessage('action.giving.wont_accept', {
            recipient: recipient.attributes.name || 'They',
            item: item.attributes.name || 'that'
          });
        }
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually give the object
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun || !command.second) return [];

      const item = command.noun[0].entity;
      const recipient = command.second[0].entity;
      const events: SemanticEvent[] = [];

      // Move the item from player to recipient
      context.world.moveEntity(item.id, recipient.id);

      // Create the giving event
      events.push(createEvent(
        IFEvents.ITEM_GIVEN,
        {
          itemId: item.id,
          itemName: item.attributes.name,
          recipientId: recipient.id,
          recipientName: recipient.attributes.name,
          giverId: command.actor,
          giverName: context.world.getEntity(command.actor)?.attributes.name || 'player'
        },
        {
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      // Also emit NPC-specific event if giving to an NPC
      if (recipient.type === IFEntityType.PERSON) {
        events.push(createEvent(
          IFEvents.NPC_GIVEN_ITEM,
          {
            npcId: recipient.id,
            npcName: recipient.attributes.name,
            itemId: item.id,
            itemName: item.attributes.name
          },
          {
            narrate: false,
            location: context.currentLocation.id
          }
        ));
      }

      return events;
    }
  }
};

/**
 * Putting action - Put an object in or on another object
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';

/**
 * Putting action definition
 * 
 * Handles "put [object] in [container]", "put [object] on [supporter]", "place [object] in/on [target]" commands
 */
export const puttingAction: ActionDefinition = {
  id: IFActions.PUTTING,
  name: IFActions.PUTTING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if putting is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have a direct object
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.putting.no_target');
      }

      // Must have a second object (what to put it in/on)
      if (!command.preposition || !command.second || command.second.length === 0) {
        return context.languageProvider.formatMessage('action.putting.no_container');
      }

      const item = command.noun[0].entity;
      const container = command.second[0].entity;
      const preposition = command.preposition;

      // Can't put something in/on itself
      if (item.id === container.id) {
        return context.languageProvider.formatMessage('action.putting.put_in_self', {
          item: item.attributes.name || 'it'
        });
      }

      // Check if player is holding the item
      if (!context.isCarriedBy(item.id, command.actor)) {
        return context.languageProvider.formatMessage('action.putting.not_held', {
          item: item.attributes.name || 'that'
        });
      }

      // Check if container is a valid container or supporter
      const isContainer = container.type === IFEntityType.CONTAINER || 
                         container.attributes.container === true;
      const isSupporter = container.attributes.supporter === true;

      if (!isContainer && !isSupporter) {
        return context.languageProvider.formatMessage('action.putting.not_container', {
          container: container.attributes.name || 'that'
        });
      }

      // If putting IN something, it must be a container
      if (preposition === 'in' && !isContainer) {
        return context.languageProvider.formatMessage('action.putting.not_container', {
          container: container.attributes.name || 'that'
        });
      }

      // If putting ON something, it must be a supporter
      if (preposition === 'on' && !isSupporter) {
        return context.languageProvider.formatMessage('action.putting.not_supporter', {
          supporter: container.attributes.name || 'that'
        });
      }

      // If putting in a container, check if it's open
      if (preposition === 'in' && isContainer && 
          container.attributes.openable && 
          !container.attributes.open) {
        return context.languageProvider.formatMessage('action.putting.container_closed', {
          container: container.attributes.name || 'that'
        });
      }

      // Check if container is full
      if (container.attributes.capacity) {
        const currentContents = context.world.getContents(container.id);
        const capacity = container.attributes.capacity as number;
        if (currentContents.length >= capacity) {
          return context.languageProvider.formatMessage('action.putting.wont_fit', {
            item: item.attributes.name || 'that',
            container: container.attributes.name || 'it'
          });
        }
      }

      // Check if container is accessible
      if (!context.isAccessible(container.id)) {
        return context.languageProvider.formatMessage('action.putting.not_accessible', {
          container: container.attributes.name || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually put the object
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun || !command.second || !command.preposition) return [];

      const item = command.noun[0].entity;
      const container = command.second[0].entity;
      const preposition = command.preposition;
      const events: SemanticEvent[] = [];

      // Move the item from player to container
      context.world.moveEntity(item.id, container.id);

      // Create the appropriate event based on whether it's a container or supporter
      const eventType = preposition === 'in' ? IFEvents.ITEM_PUT_IN : IFEvents.ITEM_PUT_ON;

      events.push(createEvent(
        eventType,
        {
          itemId: item.id,
          itemName: item.attributes.name,
          containerId: container.id,
          containerName: container.attributes.name,
          preposition: preposition,
          isContainer: container.attributes.container === true,
          isSupporter: container.attributes.supporter === true
        },
        {
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      return events;
    }
  }
};
