/**
 * Switching On action - Turn on a device
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Switching On action definition
 * 
 * Handles "turn on [device]", "switch on [device]", "activate [device]" commands
 */
export const switchingOnAction: ActionDefinition = {
  id: IFActions.SWITCHING_ON,
  name: IFActions.SWITCHING_ON,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'device'
  },
  phases: {
    // VALIDATE phase - Check if switching on is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.switching_on.no_target');
      }

      const device = command.noun[0].entity;

      // Check if device is switchable
      if (!device.attributes[IFAttributes.SWITCHABLE]) {
        return context.languageProvider.formatMessage('action.switching_on.not_switchable', {
          device: device.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if already on
      if (device.attributes[IFAttributes.ON] === true) {
        return context.languageProvider.formatMessage('action.switching_on.already_on', {
          device: device.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if device is accessible
      if (!context.isAccessible(device.id)) {
        return context.languageProvider.formatMessage('action.switching_on.not_accessible', {
          device: device.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if device requires power and has it
      if (device.attributes[IFAttributes.REQUIRES_POWER]) {
        if (!device.attributes[IFAttributes.POWERED]) {
          return context.languageProvider.formatMessage('action.switching_on.no_power', {
            device: device.attributes[IFAttributes.NAME] || 'that'
          });
        }
      }

      // Check if device is broken
      if (device.attributes[IFAttributes.BROKEN]) {
        return context.languageProvider.formatMessage('action.switching_on.broken', {
          device: device.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually switch on the device
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const device = command.noun[0].entity;
      const events: SemanticEvent[] = [];

      // Update device state to ON
      context.world.updateEntity(device.id, {
        attributes: {
          ...device.attributes,
          [IFAttributes.ON]: true
        }
      });

      // Build the switch on data
      const switchData: any = {
        deviceId: device.id,
        deviceName: device.attributes[IFAttributes.NAME],
        userId: command.actor,
        userName: context.world.getEntity(command.actor)?.attributes[IFAttributes.NAME] || 'player'
      };

      // Check if device provides light
      if (device.attributes[IFAttributes.PROVIDES_LIGHT]) {
        switchData.providesLight = true;

        // Mark location as lit if it wasn't already
        const location = context.currentLocation;
        if (location.attributes[IFAttributes.DARK] && !location.attributes[IFAttributes.LIT]) {
          context.world.updateEntity(location.id, {
            attributes: {
              ...location.attributes,
              [IFAttributes.LIT]: true
            }
          });
          switchData.illuminatedLocation = true;
        }
      }

      // Check for activation effect
      if (device.attributes[IFAttributes.ACTIVATION_EFFECT]) {
        switchData.effect = device.attributes[IFAttributes.ACTIVATION_EFFECT];
      }

      // Create the switch on event
      events.push(createEvent(
        IFEvents.DEVICE_SWITCHED_ON,
        switchData,
        {
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      // If device illuminated a dark location, emit additional event
      if (switchData.illuminatedLocation) {
        events.push(createEvent(
          IFEvents.LOCATION_ILLUMINATED,
          {
            locationId: context.currentLocation.id,
            locationName: context.currentLocation.attributes[IFAttributes.NAME],
            sourceId: device.id,
            sourceName: device.attributes[IFAttributes.NAME]
          },
          {
            narrate: true,
            location: context.currentLocation.id
          }
        ));
      }

      return events;
    }
  }
};


