import { IFCommand } from '../../parser/if-parser-types';
import { GameContext } from '../../world-model/types';
import { SemanticEvent } from '../../core-imports';

/**
 * Base action definition for the action system
 * This is an IF concept that belongs in StdLib, not Core
 */
export interface ActionDefinition {
  /**
   * Unique identifier for the action
   */
  id: string;

  /**
   * Human-readable name for the action
   */
  name: string;

  /**
   * List of verbs that trigger this action
   * These are populated by the language provider
   */
  verbs?: string[];

  /**
   * Action metadata
   */
  metadata?: {
    /**
     * Whether this action is reversible
     */
    reversible?: boolean;

    /**
     * Category of the action
     */
    category?: string;

    /**
     * Additional metadata
     */
    [key: string]: any;
  };

  /**
   * Action execution phases
   */
  phases: {
    /**
     * Validate if the action can be performed
     * Return true to continue, or a string message to block
     */
    validate?: (command: IFCommand, context: GameContext) => boolean | string;

    /**
     * Execute the action
     * Returns events describing what happened
     */
    execute: (command: IFCommand, context: GameContext) => SemanticEvent[];
  };
}

// packages/stdlib/src/core-imports.ts
/**
 * Controlled import boundary between Core and StdLib
 * 
 * This is the ONLY file in StdLib that should import from @sharpee/core.
 * All other files should import from this file instead.
 * 
 * Per refactor plan (2025-06-15):
 * - Core contains basic types and generic systems
 * - StdLib imports Core interfaces through this boundary
 */

// ============================================================================
// Basic Types (Core should contain these per refactor plan)
// ============================================================================
export {
  Entity,
  EntityId,
  EntityCreationParams,
  EntityOperationOptions
} from '@sharpee/core/types';

export {
  RelationshipType,
  StandardRelationship
} from '@sharpee/core/types';

export {
  AttributeValue,
  AttributeObject,
  AttributeArray,
  StandardAttribute
} from '@sharpee/core/types';

// ============================================================================
// Parser System (Now in StdLib - DO NOT import from Core)
// ============================================================================
// Parser has been moved to StdLib per refactor plan
// These exports are from StdLib's own parser implementation
export {
  ParsedCommand
} from './parser/core/types';

export {
  Grammar
} from './parser/core/grammar';

export {
  Parser,
  ParserFactory as ParserConfig
} from './parser/core/parser';

// ============================================================================
// Execution System (Generic parts from Core)
// ============================================================================
export {
  CommandResult,
  ExecutionContext,  // Note: GameContext is IF-specific and in StdLib
  CommandHandler,
  CommandRouter,
  CommandExecutionOptions
} from '@sharpee/core/execution';

// ============================================================================
// Event System (Generic infrastructure from Core)
// ============================================================================
export {
  EventEmitter,
  EventListener,
  EventSource,
  SemanticEvent
} from '@sharpee/core/events';

export {
  createEvent,
  createEventSource,
  createEventEmitter
} from '@sharpee/core/events';

export {
  StandardEventTypes,
  StandardEventTags,
  EventCategories
} from '@sharpee/core/events';

export {
  TextService,
  createTextService
} from '@sharpee/core/events';

// ============================================================================
// Response Formatting (Now in StdLib - DO NOT import from Core)
// ============================================================================
// These are IF-specific and live in StdLib
export {
  formatResponse,
  formatList
} from './parser/languages/en-US/response-formatter';

export {
  StandardResponses
} from './parser/languages/en-US/response-templates';

// ============================================================================
// Rules System (Generic infrastructure from Core)
// ============================================================================
export {
  RuleSystem,
  RuleManager,
  RuleBuilder,
  RuleContext,
  Rule,
  RulePhase,
  Condition
} from '@sharpee/core/rules';

export {
  createRuleSystem
} from '@sharpee/core/rules';

// Note: Rule helpers might be IF-specific, verify if these should be in Core
export {
  player,
  item,
  location,
  direction,
  EntityReference,
  ValueReference
} from '@sharpee/core/rules';

// ============================================================================
// Channel System (Generic infrastructure from Core)
// ============================================================================
export {
  Channel,
  ChannelDefinition,
  ChannelManager,
  ChannelMessage
} from '@sharpee/core/channels';

export {
  createChannelManager,
  createTextFormatter
} from '@sharpee/core/channels';

// ============================================================================
// Extension System (Generic infrastructure from Core)
// ============================================================================
export {
  Extension,
  AnyExtension,
  ExtensionType
} from '@sharpee/core/extensions';

// ============================================================================
// Movement Systems (Now in StdLib - DO NOT import from Core)
// ============================================================================
// These are IF-specific and live in StdLib
export {
  MOVEMENT_SYSTEMS,
  MovementSystem
} from './constants/movement-systems';

// ============================================================================
// Language System (Basic interface from Core)
// ============================================================================
export {
  LanguageProvider,
  ListFormatOptions,
  LanguageMetadata
} from '@sharpee/core/language';

// ============================================================================
// Core Version
// ============================================================================
export { version } from '@sharpee/core';

/**
 * Examining action - Look at an object in detail
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';

/**
 * Examining action definition
 * 
 * Handles "examine [object]", "look at [object]", "x [object]" commands
 */
export const examiningAction: ActionDefinition = {
  id: IFActions.EXAMINING,
  name: IFActions.EXAMINING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: false,
    undoable: false,
    category: 'observation'
  },
  phases: {
    // VALIDATE phase - Check if examining is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.examining.no_target');
      }

      const target = command.noun[0].entity;
      const targetId = target.id;

      // Check if visible
      if (!context.isVisible(targetId)) {
        return context.languageProvider.formatMessage('action.examining.not_visible', {
          item: target.attributes.name || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Examine the object
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun || command.noun.length === 0) return [];

      const target = command.noun[0].entity;
      const events: SemanticEvent[] = [];

      // Mark as touched (examined)
      if (!target.attributes.touched) {
        // Get fresh entity state to ensure we have complete attributes
        const currentEntity = context.world.getEntity(target.id);
        if (currentEntity) {
          context.world.updateEntity(target.id, {
            attributes: {
              ...currentEntity.attributes,
              touched: true
            }
          });
        }
      }

      // Create examination event with all details
      const examineData: Record<string, unknown> = {
        itemId: target.id,
        itemName: target.attributes.name,
        itemType: target.type,
        description: target.attributes.description || null,
        details: target.attributes.details || null
      };

      // Add container-specific information
      if (target.attributes.container) {
        examineData.isContainer = true;
        examineData.isOpen = target.attributes.open !== false;

        if (examineData.isOpen) {
          // Get contents
          const contents = context.world.getContents(target.id);
          examineData.contents = contents.map(item => ({
            id: item.id,
            name: item.attributes.name
          }));
          examineData.isEmpty = contents.length === 0;
        }
      }

      // Add supporter-specific information
      if (target.attributes.supporter) {
        examineData.isSupporter = true;
        const supported = context.world.getContents(target.id);
        examineData.supportedItems = supported.map(item => ({
          id: item.id,
          name: item.attributes.name
        }));
        examineData.hasItems = supported.length > 0;
      }

      // Add device-specific information
      if (target.attributes.switchable) {
        examineData.isSwitchable = true;
        examineData.isOn = target.attributes.on === true;
      }

      // Add door-specific information
      if (target.type === IFEntityType.DOOR) {
        examineData.isDoor = true;
        examineData.isOpen = target.attributes.open === true;
        examineData.isLocked = target.attributes.locked === true;
      }

      // Add wearable information
      if (target.attributes.wearable) {
        examineData.isWearable = true;
        examineData.isWorn = target.attributes.worn === true;
      }

      // Create the examination event
      events.push(createEvent(
        IFEvents.ITEM_EXAMINED,
        examineData,
        { 
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      return events;
    }
  }
};

