/**
 * Switching Off action definition
 * 
 * Handles "turn off [device]", "switch off [device]", "deactivate [device]" commands
 */
export const switchingOffAction: ActionDefinition = {
  id: IFActions.SWITCHING_OFF,
  name: IFActions.SWITCHING_OFF,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'device'
  },
  phases: {
    // VALIDATE phase - Check if switching off is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.switching_off.no_target');
      }

      const device = command.noun[0].entity;
      const deviceId = device.id;

      // Check if device is switchable
      if (!device.attributes.switchable) {
        return context.languageProvider.formatMessage('action.switching_off.not_switchable', {
          device: device.attributes.name || 'that'
        });
      }

      // Check if already off
      if (device.attributes.on !== true) {
        return context.languageProvider.formatMessage('action.switching_off.already_off', {
          device: device.attributes.name || 'that'
        });
      }

      // Check if device is accessible
      if (!context.isAccessible(deviceId)) {
        return context.languageProvider.formatMessage('action.switching_off.not_accessible', {
          device: device.attributes.name || 'that'
        });
      }

      // Check if device can be turned off (some devices might be stuck on)
      if (device.attributes.stuckOn) {
        return context.languageProvider.formatMessage('action.switching_off.stuck_on', {
          device: device.attributes.name || 'that'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually switch off the device
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const device = command.noun[0].entity;
      const events: SemanticEvent[] = [];

      // Get fresh entity state to ensure we have complete attributes
      const currentDevice = context.world.getEntity(device.id);
      if (!currentDevice) return [];

      // Update device state to OFF
      context.world.updateEntity(device.id, {
        attributes: {
          ...currentDevice.attributes,
          on: false
        }
      });

      // Build the switch off data
      const switchData: Record<string, unknown> = {
        deviceId: device.id,
        deviceName: device.attributes.name,
        userId: command.actor,
        userName: context.world.getEntity(command.actor)?.attributes.name || 'player'
      };

      // Check if device was providing light
      if (device.attributes.providesLight) {
        switchData.providedLight = true;

        // Check if this was the only light source in a dark location
        const location = context.currentLocation;
        if (location.attributes.dark) {
          // Find other light sources in the location
          const otherLightSources = context.getVisibleEntities()
            .filter(e => e.id !== device.id && 
                        e.attributes.providesLight && 
                        e.attributes.on === true);

          // If no other light sources, mark location as unlit
          if (otherLightSources.length === 0) {
            context.world.updateEntity(location.id, {
              attributes: {
                ...location.attributes,
                lit: false
              }
            });
            switchData.darkenedLocation = true;
          }
        }
      }

      // Check for deactivation effect
      if (device.attributes.deactivationEffect) {
        switchData.effect = device.attributes.deactivationEffect;
      }

      // Create the switch off event
      events.push(createEvent(
        IFEvents.DEVICE_SWITCHED_OFF,
        switchData,
        {
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      // If device darkened the location, emit additional event
      if (switchData.darkenedLocation) {
        events.push(createEvent(
          IFEvents.LOCATION_DARKENED,
          {
            locationId: context.currentLocation.id,
            locationName: context.currentLocation.attributes.name
          },
          {
            narrate: true,
            location: context.currentLocation.id
          }
        ));
      }

      return events;
    }
  }
};

