/**
 * IF-specific entity types and interfaces
 */

import { Entity, EntityId } from '../types';

/**
 * Standard IF entity types
 */
export enum CoreIFEntityType {
  ROOM = 'room',
  THING = 'thing',
  CONTAINER = 'container',
  SUPPORTER = 'supporter',
  DOOR = 'door',
  PERSON = 'person',
  DEVICE = 'device',
  PLAYER = 'player'
}

/**
 * Base interface for all IF entities with common properties
 */
export interface IFEntity extends Entity {
  type: CoreIFEntityType;
  attributes: IFAttributes;
}

/**
 * Common attributes for IF entities
 */
export interface IFAttributes {
  // Basic properties
  name: string;
  description?: string;
  initialDescription?: string;
  adjectives?: string[];
  synonyms?: string[];
  article?: 'a' | 'an' | 'the' | 'some';
  plural?: boolean;
  proper?: boolean; // Proper nouns don't use articles

  // Visibility
  visible?: boolean;
  concealed?: boolean;
  scenery?: boolean; // Background objects, not listed in room descriptions
  mentioned?: boolean; // Has been mentioned in room description

  // Interaction
  takeable?: boolean;
  fixed?: boolean; // Cannot be taken
  edible?: boolean;
  wearable?: boolean;
  worn?: boolean;

  // Container/Supporter
  container?: boolean;
  supporter?: boolean;
  transparent?: boolean; // Can see contents when closed
  capacity?: number; // Max items, -1 for unlimited
  bulk?: number; // How much space this takes up

  // Openable
  openable?: boolean;
  open?: boolean;
  lockable?: boolean;
  locked?: boolean;
  key?: EntityId;

  // Light
  lit?: boolean; // Currently providing light
  providesLight?: boolean; // Can provide light when lit
  requiresLight?: boolean; // Needs light to be visible

  // Text/Readable
  readable?: boolean;
  text?: string;

  // Device properties  
  switchable?: boolean;
  on?: boolean;

  // State tracking
  visited?: boolean; // For rooms
  touched?: boolean; // Has player interacted with this
  moved?: boolean; // Has been moved from initial position

  // Character properties
  animate?: boolean; // Is this a living being
  refuses?: EntityId[]; // Items this entity won't accept
}

/**
 * Room-specific entity
 */
export interface Room extends IFEntity {
  type: CoreIFEntityType.ROOM;
  attributes: RoomAttributes;
}

export interface RoomAttributes extends IFAttributes {
  // Room-specific
  dark?: boolean;
  visited?: boolean;
  indoors?: boolean;

  // Cannot have certain properties
  takeable?: false;
  fixed?: true;
}

/**
 * Thing - basic object in the world
 */
export interface Thing extends IFEntity {
  type: CoreIFEntityType.THING;
  attributes: ThingAttributes;
}

export interface ThingAttributes extends IFAttributes {
  // Things have all standard attributes
}

/**
 * Container - can hold other objects inside
 */
export interface Container extends IFEntity {
  type: CoreIFEntityType.CONTAINER;
  attributes: ContainerAttributes;
}

export interface ContainerAttributes extends IFAttributes {
  container: true;
  capacity?: number;
  transparent?: boolean;
}

/**
 * Supporter - can hold objects on top
 */
export interface Supporter extends IFEntity {
  type: CoreIFEntityType.SUPPORTER;
  attributes: SupporterAttributes;
}

export interface SupporterAttributes extends IFAttributes {
  supporter: true;
  capacity?: number;
}

/**
 * Door - connects two rooms
 */
export interface Door extends IFEntity {
  type: CoreIFEntityType.DOOR;
  attributes: DoorAttributes;
}

export interface DoorAttributes extends IFAttributes {
  openable: true;
  open?: boolean;
  lockable?: boolean;
  locked?: boolean;
  key?: EntityId;

  // Doors are always fixed
  takeable?: false;
  fixed?: true;
}

/**
 * Person - NPCs and the player
 */
export interface Person extends IFEntity {
  type: CoreIFEntityType.PERSON;
  attributes: PersonAttributes;
}

export interface PersonAttributes extends IFAttributes {
  proper?: boolean; // Most people have proper names
  gender?: 'male' | 'female' | 'neutral' | 'unknown';
  animate?: true; // People are always animate

  // People are not usually takeable
  takeable?: false;
  fixed?: true;
}

/**
 * Device - switchable objects
 */
export interface Device extends IFEntity {
  type: CoreIFEntityType.DEVICE;
  attributes: DeviceAttributes;
}

export interface DeviceAttributes extends IFAttributes {
  switchable: true;
  on?: boolean;
  providesLight?: boolean; // Many devices are light sources
}

/**
 * Type guards for IF entities
 */
export function isRoom(entity: Entity): entity is Room {
  return entity.type === CoreIFEntityType.ROOM;
}

export function isThing(entity: Entity): entity is Thing {
  return entity.type === CoreIFEntityType.THING;
}

export function isContainer(entity: Entity): entity is Container {
  return entity.type === CoreIFEntityType.CONTAINER || 
    (entity.attributes.container === true);
}

export function isSupporter(entity: Entity): entity is Supporter {
  return entity.type === CoreIFEntityType.SUPPORTER ||
    (entity.attributes.supporter === true);
}

export function isDoor(entity: Entity): entity is Door {
  return entity.type === CoreIFEntityType.DOOR;
}

export function isPerson(entity: Entity): entity is Person {
  return entity.type === CoreIFEntityType.PERSON || 
    entity.type === CoreIFEntityType.PLAYER;
}

export function isDevice(entity: Entity): entity is Device {
  return entity.type === CoreIFEntityType.DEVICE ||
    (entity.attributes.switchable === true);
}

/**
 * Check if an entity can contain other entities
 */
export function canContain(entity: Entity): boolean {
  return isContainer(entity) || isSupporter(entity) || isRoom(entity) || isPerson(entity);
}

/**
 * Check if an entity is portable
 */
export function isPortable(entity: Entity): boolean {
  const attrs = entity.attributes as IFAttributes;
  return attrs.takeable === true && attrs.fixed !== true;
}

/**
 * Check if an entity provides light
 */
export function providesLight(entity: Entity): boolean {
  const attrs = entity.attributes as IFAttributes;
  return attrs.lit === true && attrs.providesLight === true;
}

