/**
 * Talking action - Talk to a person or NPC
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Talking action definition
 * 
 * Handles "talk to [person]", "speak to [person]", "talk" commands
 */
export const talkingAction: ActionDefinition = {
  id: IFActions.TALKING,
  name: IFActions.TALKING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: false,
    undoable: false,
    category: 'communication'
  },
  phases: {
    // VALIDATE phase - Check if talking is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Allow general "talk" command (will list who can be talked to)
      if (!command.noun || command.noun.length === 0) {
        return true;
      }

      const target = command.noun[0].entity;

      // Can't talk to yourself
      if (target.id === command.actor) {
        return context.languageProvider.formatMessage('action.talking.talk_to_self');
      }

      // Check if target is conversable
      const isConversable = target.type === IFEntityType.PERSON || 
                           target.attributes[IFAttributes.ANIMATE] === true || 
                           target.attributes[IFAttributes.CONVERSABLE] === true;

      if (!isConversable) {
        return context.languageProvider.formatMessage('action.talking.not_person', {
          target: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if target is accessible
      if (!context.isVisible(target.id)) {
        return context.languageProvider.formatMessage('action.talking.not_accessible', {
          target: target.attributes[IFAttributes.NAME] || 'them'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually talk
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      const events: SemanticEvent[] = [];

      // Handle general talk command
      if (!command.noun || command.noun.length === 0) {
        // Find all conversable entities in the current location
        const peopleHere = context.getVisibleEntities()
          .filter(e => (e.type === IFEntityType.PERSON || 
                       e.attributes[IFAttributes.ANIMATE] === true || 
                       e.attributes[IFAttributes.CONVERSABLE] === true) && 
                       e.id !== command.actor);

        events.push(createEvent(
          IFEvents.GENERAL_TALK,
          {
            peoplePresent: peopleHere.map(p => ({
              id: p.id,
              name: p.attributes[IFAttributes.NAME]
            })),
            nonePresent: peopleHere.length === 0
          },
          {
            narrate: true,
            location: context.currentLocation.id
          }
        ));

        return events;
      }

      const target = command.noun[0].entity;

      // Mark NPC as talked to
      if (!target.attributes[IFAttributes.TALKED_TO]) {
        context.world.updateEntity(target.id, {
          attributes: {
            ...target.attributes,
            [IFAttributes.TALKED_TO]: true
          }
        });
      }

      // Get conversation content from target's attributes
      const conversationData: any = {
        npcId: target.id,
        npcName: target.attributes[IFAttributes.NAME],
        isFirstTime: !target.attributes[IFAttributes.TALKED_TO]
      };

      // Add greeting if present
      if (target.attributes[IFAttributes.GREETING]) {
        conversationData.greeting = target.attributes[IFAttributes.GREETING];
      }

      // Add topics if present
      if (target.attributes[IFAttributes.TOPICS]) {
        conversationData.topics = target.attributes[IFAttributes.TOPICS];
      }

      // Add default response if present
      if (target.attributes[IFAttributes.DEFAULT_RESPONSE]) {
        conversationData.defaultResponse = target.attributes[IFAttributes.DEFAULT_RESPONSE];
      }

      // Create the talking event
      events.push(createEvent(
        IFEvents.NPC_TALKED_TO,
        conversationData,
        {
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      return events;
    }
  }
};

// packages/stdlib/src/constants/if-entity-types.ts

/**
 * Interactive Fiction entity types
 * These extend the core entity system with IF-specific concepts
 */
export enum IFEntityType {
  // Location entities
  ROOM = 'if.room',
  REGION = 'if.region',

  // Object entities
  THING = 'if.thing',
  CONTAINER = 'if.container',
  SUPPORTER = 'if.supporter',
  DOOR = 'if.door',
  BACKDROP = 'if.backdrop',

  // Character entities
  PERSON = 'if.person',
  PLAYER = 'if.player',
  NPC = 'if.npc',

  // Device entities
  DEVICE = 'if.device',
  SWITCH = 'if.switch',

  // Special entities
  DIRECTION = 'if.direction',
  SCENE = 'if.scene'
}

/**
 * Type guards for IF entity types
 */
export function isLocation(type: string): boolean {
  return type === IFEntityType.ROOM || type === IFEntityType.REGION;
}

export function isObject(type: string): boolean {
  return [
    IFEntityType.THING,
    IFEntityType.CONTAINER,
    IFEntityType.SUPPORTER,
    IFEntityType.DOOR,
    IFEntityType.BACKDROP,
    IFEntityType.DEVICE,
    IFEntityType.SWITCH
  ].includes(type as IFEntityType);
}

export function isCharacter(type: string): boolean {
  return [
    IFEntityType.PERSON,
    IFEntityType.PLAYER,
    IFEntityType.NPC
  ].includes(type as IFEntityType);
}

export function canContainThings(type: string): boolean {
  return [
    IFEntityType.ROOM,
    IFEntityType.CONTAINER,
    IFEntityType.SUPPORTER,
    IFEntityType.PERSON,
    IFEntityType.PLAYER,
    IFEntityType.NPC
  ].includes(type as IFEntityType);
}

