/**
 * Telling action - Tell someone about something or say something to someone
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Telling action definition
 * 
 * Handles "tell [person] about [topic]", "say [topic] to [person]" commands
 */
export const tellingAction: ActionDefinition = {
  id: IFActions.TELLING,
  name: IFActions.TELLING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: false,
    undoable: false,
    category: 'communication'
  },
  phases: {
    // VALIDATE phase - Check if telling is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have someone to tell (direct object)
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.telling.no_recipient');
      }

      // Must have something to tell about (indirect object or quoted text)
      if (!command.indirect && !command.quotedText) {
        return context.languageProvider.formatMessage('action.telling.no_topic');
      }

      const recipient = command.noun[0].entity;

      // Can't tell yourself
      if (recipient.id === command.actor) {
        return context.languageProvider.formatMessage('action.telling.tell_self');
      }

      // Check if recipient is conversable
      const isConversable = recipient.type === IFEntityType.PERSON || 
                           recipient.attributes[IFAttributes.ANIMATE] === true || 
                           recipient.attributes[IFAttributes.CONVERSABLE] === true;

      if (!isConversable) {
        return context.languageProvider.formatMessage('action.telling.not_person', {
          recipient: recipient.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if recipient is accessible
      if (!context.isVisible(recipient.id)) {
        return context.languageProvider.formatMessage('action.telling.not_accessible', {
          recipient: recipient.attributes[IFAttributes.NAME] || 'them'
        });
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually tell
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const recipient = command.noun[0].entity;
      const events: SemanticEvent[] = [];

      // Build the telling data
      const tellingData: any = {
        recipientId: recipient.id,
        recipientName: recipient.attributes[IFAttributes.NAME],
        tellerId: command.actor,
        tellerName: context.world.getEntity(command.actor)?.attributes[IFAttributes.NAME] || 'player'
      };

      // Handle quoted text (direct speech)
      if (command.quotedText) {
        tellingData.quotedText = command.quotedText;
        tellingData.isDirectSpeech = true;
      }
      // Handle topic (indirect object)
      else if (command.indirect) {
        const topic = command.indirect[0].entity;
        tellingData.topicId = topic.id;
        tellingData.topicName = topic.attributes[IFAttributes.NAME];
        tellingData.isAboutTopic = true;

        // Check if recipient has specific response for this topic
        const topicResponses = recipient.attributes[IFAttributes.TOPIC_RESPONSES];
        if (topicResponses && typeof topicResponses === 'object') {
          const specificResponse = topicResponses[topic.id] || topicResponses[topic.attributes[IFAttributes.NAME] as string];
          if (specificResponse) {
            tellingData.response = specificResponse;
          }
        }
      }

      // Add default response if no specific response found
      if (!tellingData.response) {
        if (recipient.attributes[IFAttributes.DEFAULT_RESPONSE]) {
          tellingData.response = recipient.attributes[IFAttributes.DEFAULT_RESPONSE];
        }
      }

      // Create the telling event
      events.push(createEvent(
        IFEvents.NPC_TOLD_ABOUT,
        tellingData,
        {
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      return events;
    }
  }
};

/**
 * IF-specific parser interfaces
 */

import { Entity, EntityId } from '../world-model/types';

/**
 * Represents a scored match for an entity
 */
export interface ScoredMatch {
  entity: Entity;
  score: number;
  matchedWords: string[];
  matchType: MatchType;
  disambiguation?: string; // e.g., "the brass one"
}

export enum MatchType {
  EXACT = 'exact',
  PARTIAL = 'partial',
  SYNONYM = 'synonym',
  PRONOUN = 'pronoun',
  IMPLIED = 'implied'
}

/**
 * Scope context for command parsing
 */
export interface ScopeContext {
  visible: Set<EntityId>;
  reachable: Set<EntityId>;
  known: Set<EntityId>;
  special?: Set<EntityId>; // Command-specific scope
  recentlyMentioned: EntityId[];
}

/**
 * Grammar pattern definition
 */
export interface GrammarPattern {
  id: string;
  pattern: string;              // e.g., "take|get|grab <noun>"
  action: string;               // e.g., "taking"
  prepositions?: string[];      // e.g., ["from", "off of"]
  reversed?: boolean;           // e.g., "take <noun> from <second>"
  matchAll?: boolean;           // e.g., "take all"
  requiresSecond?: boolean;     // Must have a second noun
  allowsImplicitSecond?: boolean; // e.g., "unlock door" implies "with key"
}

/**
 * A parsed IF command
 */
export interface IFCommand {
  action: string;                    // Canonical action name
  noun?: ScoredMatch[];              // Possible matches for first noun
  second?: ScoredMatch[];            // Possible matches for second noun
  actor: EntityId;                   // Who's performing (usually player)
  preposition?: string;              // Used preposition
  text?: string;                     // Free text (for "say" commands)
  pattern: GrammarPattern;           // Which pattern matched
  originalInput: string;
  confidence: number;                // How confident in the parse
  matchAll?: boolean;                // Command applies to all matching objects
}

/**
 * Parser result that may need disambiguation
 */
export interface ParseResult {
  success: boolean;
  commands: IFCommand[];
  needsDisambiguation?: DisambiguationRequest;
  error?: string;
}

/**
 * Request for disambiguation from player
 */
export interface DisambiguationRequest {
  prompt: string;
  options: Array<{
    entity: Entity;
    description: string;
  }>;
  context: 'noun' | 'second';
  originalCommand: IFCommand;
}

/**
 * Interface for IF-specific parsers
 */
export interface IFParser {
  /**
   * Parse input into IF commands
   */
  parse(
    input: string,
    scope: ScopeContext,
    getEntity: (id: EntityId) => Entity | undefined
  ): ParseResult;

  /**
   * Continue parsing after disambiguation
   */
  continueWithDisambiguation(
    original: IFCommand,
    choice: EntityId,
    context: 'noun' | 'second'
  ): IFCommand;

  /**
   * Add custom grammar patterns
   */
  addGrammar(pattern: GrammarPattern): void;

  /**
   * Get all registered patterns
   */
  getGrammarPatterns(): GrammarPattern[];
}

/**
 * Match scoring configuration
 */
export interface ScoringConfig {
  exactMatch: number;
  partialMatch: number;
  synonymMatch: number;
  adjectiveMatch: number;
  visibleBonus: number;
  reachableBonus: number;
  recentlyMentionedBonus: number;
  pronounPenalty: number;
}

/**
 * Language-specific parser configuration
 */
export interface IFParserConfig {
  articles: string[];              // ["a", "an", "the"]
  conjunctions: string[];          // ["and", "then", "but"]
  pronouns: string[];              // ["it", "them", "him", "her"]
  implicitPrepositions: Map<string, string>; // "unlock door"  "with"
  directions: string[];            // ["north", "south", "up", "down"]
  scoring: ScoringConfig;
}

