/**
 * Unlocking action - Unlock a door or container
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';
import { IFEntity } from '../world-model/if-entities/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Unlocking action definition
 * 
 * Handles "unlock [door/container]", "unlock [door/container] with [key]" commands
 */
export const unlockingAction: ActionDefinition = {
  id: IFActions.UNLOCKING,
  name: IFActions.UNLOCKING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'device'
  },
  phases: {
    // VALIDATE phase - Check if unlocking is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have a target
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.unlocking.no_target');
      }

      const target = command.noun[0].entity;

      // Check if target is lockable (and thus unlockable)
      if (!target.attributes[IFAttributes.LOCKABLE]) {
        return context.languageProvider.formatMessage('action.unlocking.not_lockable', {
          target: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if already unlocked
      if (target.attributes[IFAttributes.LOCKED] !== true) {
        return context.languageProvider.formatMessage('action.unlocking.already_unlocked', {
          target: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if target is accessible
      if (!context.isAccessible(target.id)) {
        return context.languageProvider.formatMessage('action.unlocking.not_accessible', {
          target: target.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if a key is required
      const keyRequired = target.attributes[IFAttributes.KEY_ID];
      if (keyRequired) {
        // If key specified in command
        if (command.indirect && command.indirect.length > 0) {
          const providedKey = command.indirect[0].entity;

          // Check if correct key
          if (providedKey.id !== keyRequired) {
            return context.languageProvider.formatMessage('action.unlocking.wrong_key', {
              key: providedKey.attributes[IFAttributes.NAME] || 'that'
            });
          }

          // Check if player has the key
          if (context.world.getLocation(providedKey.id) !== command.actor) {
            return context.languageProvider.formatMessage('action.unlocking.key_not_held', {
              key: providedKey.attributes[IFAttributes.NAME] || 'that'
            });
          }
        } else {
          // No key provided but one is required
          return context.languageProvider.formatMessage('action.unlocking.needs_key', {
            target: target.attributes[IFAttributes.NAME] || 'that'
          });
        }
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually unlock the target
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const target = command.noun[0].entity;
      const events: SemanticEvent[] = [];

      // Update target state to UNLOCKED
      const entityUpdate: Partial<IFEntity> = {
        attributes: {
          ...target.attributes,
          [IFAttributes.LOCKED]: false
        }
      };
      context.world.updateEntity(target.id, entityUpdate);

      // Build the unlocking data
      const unlockData: any = {
        targetId: target.id,
        targetName: target.attributes[IFAttributes.NAME],
        targetType: target.type,
        userId: command.actor,
        userName: context.world.getEntity(command.actor)?.attributes[IFAttributes.NAME] || 'player'
      };

      // Add key information if used
      if (command.indirect && command.indirect.length > 0) {
        const key = command.indirect[0].entity;
        unlockData.keyId = key.id;
        unlockData.keyName = key.attributes[IFAttributes.NAME];
        unlockData.usedKey = true;
      } else {
        unlockData.usedKey = false;
      }

      // Create the appropriate event based on target type
      const eventType = target.type === IFEntityType.DOOR ? 
                       IFEvents.DOOR_UNLOCKED : 
                       IFEvents.CONTAINER_UNLOCKED;

      events.push(createEvent(
        eventType,
        unlockData,
        {
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      return events;
    }
  }
};

