/**
 * Using action - Use an object, optionally with another object
 */

// Import from local action types
import { ActionDefinition } from './types';

// Import from parser types  
import { IFCommand } from '../parser/if-parser-types';

// Import from world model types
import { GameContext } from '../world-model/types';

// Import from Core through controlled boundary
import { SemanticEvent, createEvent } from '../core-imports';

// Import IF-specific constants from local constants
import { IFActions } from '../constants/if-actions';
import { IFEvents } from '../constants/if-events';
import { IFEntityType } from '../constants/if-entity-types';
import { IFAttributes } from '../constants/if-attributes';

/**
 * Using action definition
 * 
 * Handles "use [object]", "use [object] with [object]", "use [object] on [object]" commands
 */
export const usingAction: ActionDefinition = {
  id: IFActions.USING,
  name: IFActions.USING,
  verbs: [], // Will be populated from language provider
  metadata: {
    changesWorld: true,
    undoable: true,
    category: 'manipulation'
  },
  phases: {
    // VALIDATE phase - Check if using is possible
    validate: (command: IFCommand, context: GameContext): boolean | string => {
      // Must have something to use
      if (!command.noun || command.noun.length === 0) {
        return context.languageProvider.formatMessage('action.using.no_target');
      }

      const item = command.noun[0].entity;

      // Check if item is usable
      if (!item.attributes[IFAttributes.USABLE]) {
        return context.languageProvider.formatMessage('action.using.not_usable', {
          item: item.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // Check if item is accessible (carried or in location)
      if (!context.isAccessible(item.id)) {
        return context.languageProvider.formatMessage('action.using.not_accessible', {
          item: item.attributes[IFAttributes.NAME] || 'that'
        });
      }

      // If using with another object
      if (command.indirect && command.indirect.length > 0) {
        const target = command.indirect[0].entity;

        // Can't use something with itself
        if (item.id === target.id) {
          return context.languageProvider.formatMessage('action.using.use_with_self');
        }

        // Check if target is accessible
        if (!context.isAccessible(target.id)) {
          return context.languageProvider.formatMessage('action.using.target_not_accessible', {
            target: target.attributes[IFAttributes.NAME] || 'that'
          });
        }

        // Check if this specific combination is allowed
        const usableWith = item.attributes[IFAttributes.USABLE_WITH];
        if (usableWith && Array.isArray(usableWith)) {
          if (!usableWith.includes(target.id)) {
            return context.languageProvider.formatMessage('action.using.cannot_use_together', {
              item: item.attributes[IFAttributes.NAME] || 'that',
              target: target.attributes[IFAttributes.NAME] || 'that'
            });
          }
        }
      } else {
        // Using without a target - check if item requires a target
        if (item.attributes[IFAttributes.REQUIRES_TARGET]) {
          return context.languageProvider.formatMessage('action.using.requires_target', {
            item: item.attributes[IFAttributes.NAME] || 'that'
          });
        }
      }

      // All checks passed
      return true;
    },

    // EXECUTE phase - Actually use the object
    execute: (command: IFCommand, context: GameContext): SemanticEvent[] => {
      if (!command.noun) return [];

      const item = command.noun[0].entity;
      const events: SemanticEvent[] = [];

      // Build the using data
      const usingData: any = {
        itemId: item.id,
        itemName: item.attributes[IFAttributes.NAME],
        userId: command.actor,
        userName: context.world.getEntity(command.actor)?.attributes[IFAttributes.NAME] || 'player'
      };

      // Handle using with a target
      if (command.indirect && command.indirect.length > 0) {
        const target = command.indirect[0].entity;
        usingData.targetId = target.id;
        usingData.targetName = target.attributes[IFAttributes.NAME];
        usingData.withTarget = true;

        // Check for custom use effect
        const useEffects = item.attributes[IFAttributes.USE_EFFECTS];
        if (useEffects && typeof useEffects === 'object') {
          const effect = useEffects[target.id];
          if (effect) {
            usingData.effect = effect;

            // Apply any world state changes defined in the effect
            if (effect.changes) {
              // This would be handled by a more complex effect system
              usingData.hasChanges = true;
            }
          }
        }
      } else {
        usingData.withTarget = false;

        // Check for default use effect
        if (item.attributes[IFAttributes.USE_EFFECT]) {
          usingData.effect = item.attributes[IFAttributes.USE_EFFECT];
        }
      }

      // Mark item as used
      if (!item.attributes[IFAttributes.USED]) {
        context.world.updateEntity(item.id, {
          attributes: {
            ...item.attributes,
            [IFAttributes.USED]: true
          }
        });
      }

      // Increment use count if tracked
      if (typeof item.attributes[IFAttributes.USE_COUNT] === 'number') {
        context.world.updateEntity(item.id, {
          attributes: {
            ...item.attributes,
            [IFAttributes.USE_COUNT]: (item.attributes[IFAttributes.USE_COUNT] as number) + 1
          }
        });
        usingData.useCount = (item.attributes[IFAttributes.USE_COUNT] as number) + 1;
      }

      // Check if item is consumable and should be removed
      if (item.attributes[IFAttributes.CONSUMABLE]) {
        const maxUses = item.attributes[IFAttributes.MAX_USES];
        const useCount = item.attributes[IFAttributes.USE_COUNT] as number || 1;

        if (!maxUses || useCount >= maxUses) {
          // Remove the item
          context.world.removeEntity(item.id);
          usingData.consumed = true;
        }
      }

      // Create the using event
      events.push(createEvent(
        IFEvents.ITEM_USED,
        usingData,
        {
          narrate: true,
          location: context.currentLocation.id
        }
      ));

      // If there's a specific effect event, emit it too
      if (usingData.effect && usingData.effect.eventType) {
        events.push(createEvent(
          usingData.effect.eventType,
          usingData.effect.eventData || {},
          {
            narrate: true,
            location: context.currentLocation.id
          }
        ));
      }

      return events;
    }
  }
};

