// packages/stdlib/src/actions/if-action-definition.ts

import { ActionDefinition } from './types';
import { IFCommand } from '../parser/if-parser-types';
import { GameContext } from '../world-model/types';
import { IFActions } from '../constants/if-actions';
import { ActionVerbRegistry } from '../language/action-verb-registry';

/**
 * Extended action definition for Interactive Fiction
 * Adds IF-specific features like verb registration
 */
export interface IFActionDefinition extends ActionDefinition {
  /**
   * The IF action ID from the constants
   */
  id: IFActions;

  /**
   * Register verbs for this action
   * Called during initialization
   */
  registerVerbs(registry: ActionVerbRegistry): void;

  /**
   * Whether this action requires light to perform
   */
  requiresLight?: boolean;

  /**
   * Scope requirements for the action
   */
  scopeRequirements?: {
    directObject?: ScopeRequirement;
    indirectObject?: ScopeRequirement;
  };
}

/**
 * Scope requirements for action objects
 */
export interface ScopeRequirement {
  /**
   * Object must be visible
   */
  visible?: boolean;

  /**
   * Object must be touchable/reachable
   */
  touchable?: boolean;

  /**
   * Object must be carried by the actor
   */
  carried?: boolean;

  /**
   * Object must be in the same location
   */
  present?: boolean;

  /**
   * Custom scope check function
   */
  customCheck?: (obj: any, context: GameContext) => boolean;
}

/**
 * Helper to create an IF action definition
 */
export function createIFAction(config: {
  id: IFActions;
  verbs: string[];
  requiresLight?: boolean;
  scopeRequirements?: IFActionDefinition['scopeRequirements'];
  metadata?: ActionDefinition['metadata'];
  phases: ActionDefinition['phases'];
}): IFActionDefinition {
  return {
    id: config.id,
    name: config.id,
    requiresLight: config.requiresLight,
    scopeRequirements: config.scopeRequirements,
    metadata: config.metadata,
    phases: config.phases,

    registerVerbs(registry: ActionVerbRegistry) {
      registry.registerAction(config.id, config.verbs);
    }
  };
}

/**
 * Base class for IF action implementations
 * Provides common functionality for IF actions
 */
export abstract class BaseIFAction implements IFActionDefinition {
  abstract id: IFActions;
  abstract name: string;

  requiresLight?: boolean;
  scopeRequirements?: IFActionDefinition['scopeRequirements'];
  metadata?: ActionDefinition['metadata'];

  // Subclasses must implement phases
  abstract phases: ActionDefinition['phases'];

  // Subclasses must define their verbs
  protected abstract verbs: string[];

  registerVerbs(registry: ActionVerbRegistry): void {
    registry.registerAction(this.id, this.verbs);
  }

  /**
   * Helper to check if an object meets scope requirements
   */
  protected checkScope(
    obj: any,
    requirement: ScopeRequirement | undefined,
    context: GameContext
  ): boolean {
    if (!requirement) return true;

    if (requirement.visible && !context.isVisible(obj.id)) {
      return false;
    }

    if (requirement.touchable && !context.isAccessible(obj.id)) {
      return false;
    }

    if (requirement.carried) {
      const inventory = context.player.relationships['contains'] || [];
      if (!inventory.includes(obj.id)) {
        return false;
      }
    }

    if (requirement.present) {
      const location = context.currentLocation.id;
      const objLocation = context.findLocationOf(obj.id);
      if (objLocation !== location) {
        return false;
      }
    }

    if (requirement.customCheck && !requirement.customCheck(obj, context)) {
      return false;
    }

    return true;
  }
}

