// packages/stdlib/src/world-model/traits/standard/__tests__/identity.test.ts

import { IdentityTrait } from '../identity';

describe('IdentityTrait', () => {
  test('should create with defaults', () => {
    const trait = new IdentityTrait();
    expect(trait.type).toBe('identity');
    expect(trait.name).toBe('');
    expect(trait.description).toBe('');
    expect(trait.aliases).toEqual([]);
    expect(trait.properName).toBe(false);
    expect(trait.article).toBe('a');
  });

  test('should create with data', () => {
    const trait = new IdentityTrait({
      name: 'golden key',
      description: 'A small golden key with intricate engravings.',
      aliases: ['key', 'gold key'],
      article: 'a'
    });

    expect(trait.name).toBe('golden key');
    expect(trait.description).toBe('A small golden key with intricate engravings.');
    expect(trait.aliases).toEqual(['key', 'gold key']);
  });

  test('should validate required fields', () => {
    const trait = new IdentityTrait();
    expect(() => trait.validate()).toThrow('identity.name: is required');

    trait.name = '   ';
    expect(() => trait.validate()).toThrow('identity.name: cannot be empty');

    trait.name = 'valid name';
    expect(() => trait.validate()).not.toThrow();
  });

  test('should validate article values', () => {
    const trait = new IdentityTrait({ name: 'apple' });

    trait.article = 'an';
    expect(() => trait.validate()).not.toThrow();

    trait.article = 'invalid';
    expect(() => trait.validate()).toThrow('identity.article: must be one of: a, an, the, some, ');
  });

  test('should handle proper names', () => {
    const trait = new IdentityTrait({
      name: 'Alice',
      properName: true,
      article: ''
    });

    expect(trait.getFullName()).toBe('Alice');
  });

  test('should get full name with article', () => {
    const trait = new IdentityTrait({
      name: 'apple',
      article: 'an'
    });

    expect(trait.getFullName()).toBe('an apple');
  });

  test('should get all names including aliases', () => {
    const trait = new IdentityTrait({
      name: 'bronze sword',
      aliases: ['sword', 'weapon', 'blade']
    });

    expect(trait.getAllNames()).toEqual(['bronze sword', 'sword', 'weapon', 'blade']);
  });

  test('should match names case-insensitively', () => {
    const trait = new IdentityTrait({
      name: 'Mystical Orb',
      aliases: ['orb', 'sphere']
    });

    expect(trait.matchesName('mystical orb')).toBe(true);
    expect(trait.matchesName('MYSTICAL ORB')).toBe(true);
    expect(trait.matchesName('orb')).toBe(true);
    expect(trait.matchesName('Sphere')).toBe(true);
    expect(trait.matchesName('ball')).toBe(false);
  });

  test('should validate unique aliases', () => {
    const trait = new IdentityTrait({
      name: 'item',
      aliases: ['thing', 'object', 'thing'] // duplicate
    });

    expect(() => trait.validate()).toThrow('identity.aliases: contains duplicate values');
  });
});

/**
 * Test file for Phase 5 World Model Service implementation
 */

import { WorldModelService } from '../world-model-service';
import { TraitType } from '../../traits/trait-types';
import { IdentityTrait } from '../../traits/standard/identity';
import { ContainerTrait } from '../../traits/standard/container';
import { PortableTrait } from '../../traits/standard/portable';
import { QueryOperator, SpatialQuery } from '../query-builder';

// Test function to verify Phase 5 implementation
export function testWorldModelService() {
  console.log('Testing World Model Service - Phase 5');

  // Create world model service
  const world = new WorldModelService({
    playerId: 'player',
    startingRoom: 'kitchen',
    enforceCapacity: true,
    validateTraits: true
  });

  // Test 1: Entity CRUD operations
  console.log('\n1. Testing Entity CRUD:');

  // Create player
  const player = world.createEntity('player', [
    new IdentityTrait({ name: 'You', description: 'As good-looking as ever.' })
  ]);
  console.log('Created player:', player.id);

  // Create room
  const kitchen = world.createEntity('kitchen', [
    new IdentityTrait({ 
      name: 'Kitchen',
      description: 'A well-equipped kitchen with modern appliances.'
    })
  ]);
  console.log('Created kitchen:', kitchen.id);

  // Create container
  const fridge = world.createEntity('fridge', [
    new IdentityTrait({ 
      name: 'refrigerator',
      description: 'A large stainless steel refrigerator.',
      aliases: ['fridge']
    }),
    new ContainerTrait({ capacity: 10, open: false })
  ]);
  console.log('Created fridge:', fridge.id);

  // Create item
  const apple = world.createEntity('apple', [
    new IdentityTrait({
      name: 'apple',
      description: 'A crisp red apple.'
    }),
    new PortableTrait({ weight: 0.2 })
  ]);
  console.log('Created apple:', apple.id);

  // Test 2: Spatial relationships
  console.log('\n2. Testing Spatial Relationships:');

  // Move entities
  world.moveEntity('player', 'kitchen');
  world.moveEntity('fridge', 'kitchen');
  world.moveEntity('apple', 'fridge');

  console.log('Player location:', world.getLocation('player'));
  console.log('Kitchen contents:', world.getContents('kitchen').map(e => e.id));
  console.log('Fridge contents:', world.getContents('fridge').map(e => e.id));

  // Test 3: Query Builder
  console.log('\n3. Testing Query Builder:');

  // Find all containers
  const containers = world.query()
    .hasTrait(TraitType.CONTAINER)
    .execute();
  console.log('Containers:', containers.map(e => e.id));

  // Find portable items in kitchen
  const portableInKitchen = world.query()
    .hasTrait(TraitType.PORTABLE)
    .in('kitchen')
    .execute();
  console.log('Portable items in kitchen:', portableInKitchen.map(e => e.id));

  // Find items by name
  const itemsWithA = world.query()
    .hasTrait(TraitType.IDENTITY)
    .where(TraitType.IDENTITY, 'name', QueryOperator.STARTS_WITH, 'a')
    .execute();
  console.log('Items starting with "a":', itemsWithA.map(e => e.id));

  // Test 4: Scope Service
  console.log('\n4. Testing Scope Service:');

  const scope = world.getScope();
  const playerScope = scope.calculateScope('player');

  console.log('Visible entities:', Array.from(playerScope.visible));
  console.log('Reachable entities:', Array.from(playerScope.reachable));
  console.log('In darkness:', playerScope.inDarkness);

  // Test visibility
  console.log('Is apple visible from player?', world.isVisibleFrom('apple', 'player'));
  console.log('Is apple reachable from player?', world.isReachableFrom('apple', 'player'));

  // Open fridge and check again
  world.updateTrait('fridge', TraitType.CONTAINER, { open: true });
  const updatedScope = scope.calculateScope('player');
  console.log('\nAfter opening fridge:');
  console.log('Visible entities:', Array.from(updatedScope.visible));
  console.log('Is apple visible now?', world.isVisibleFrom('apple', 'player'));

  // Test 5: State Serialization
  console.log('\n5. Testing State Serialization:');

  const state = world.serialize();
  console.log('Serialized state has', state.entities.length, 'entities');
  console.log('State version:', state.version);

  // Create new world and deserialize
  const world2 = new WorldModelService({
    playerId: 'player',
    startingRoom: 'void'
  });

  world2.deserialize(state);
  const restoredEntities = world2.getAllEntities();
  console.log('Restored', restoredEntities.length, 'entities');
  console.log('Player location after restore:', world2.getLocation('player'));

  console.log('\n Phase 5 World Model Service implementation complete!');
}

// Run tests if this file is executed directly
if (require.main === module) {
  testWorldModelService();
}

