// packages/stdlib/src/world-model/traits/standard/__tests__/location.test.ts

import { LocationTrait } from '../location';

describe('LocationTrait', () => {
  test('should create with defaults', () => {
    const trait = new LocationTrait();
    expect(trait.type).toBe('location');
    expect(trait.location).toBeNull();
    expect(trait.previousLocation).toBeNull();
  });

  test('should create with initial location', () => {
    const trait = new LocationTrait({ location: 'room1' });
    expect(trait.location).toBe('room1');
    expect(trait.previousLocation).toBeNull();
  });

  test('should track previous location on update', () => {
    const trait = new LocationTrait({ location: 'room1' });
    trait.location = 'room2';
    expect(trait.location).toBe('room2');
    expect(trait.previousLocation).toBe('room1');
  });

  test('should validate location type', () => {
    const trait = new LocationTrait();

    // Valid locations
    trait.location = 'room1';
    expect(() => trait.validate()).not.toThrow();

    trait.location = null;
    expect(() => trait.validate()).not.toThrow();

    // Invalid location types
    trait.location = 123 as any;
    expect(() => trait.validate()).toThrow('location.location: must be a string or null');

    trait.location = {} as any;
    expect(() => trait.validate()).toThrow('location.location: must be a string or null');
  });

  test('should handle location history', () => {
    const trait = new LocationTrait();

    // Start with no location
    expect(trait.location).toBeNull();
    expect(trait.previousLocation).toBeNull();

    // Move to first location
    trait.location = 'room1';
    expect(trait.location).toBe('room1');
    expect(trait.previousLocation).toBeNull();

    // Move to second location
    trait.location = 'room2';
    expect(trait.location).toBe('room2');
    expect(trait.previousLocation).toBe('room1');

    // Move to third location
    trait.location = 'room3';
    expect(trait.location).toBe('room3');
    expect(trait.previousLocation).toBe('room2');
  });

  test('should handle returning to previous location', () => {
    const trait = new LocationTrait({ location: 'room1' });

    trait.location = 'room2';
    expect(trait.location).toBe('room2');
    expect(trait.previousLocation).toBe('room1');

    // Return to previous
    trait.location = 'room1';
    expect(trait.location).toBe('room1');
    expect(trait.previousLocation).toBe('room2');
  });

  test('should handle null locations', () => {
    const trait = new LocationTrait({ location: 'room1' });

    trait.location = null;
    expect(trait.location).toBeNull();
    expect(trait.previousLocation).toBe('room1');

    trait.location = 'room2';
    expect(trait.location).toBe('room2');
    expect(trait.previousLocation).toBeNull();
  });
});

// packages/stdlib/src/world-model/traits/standard/__tests__/portable.test.ts

import { PortableTrait } from '../portable';

describe('PortableTrait', () => {
  test('should create with defaults', () => {
    const trait = new PortableTrait();
    expect(trait.type).toBe('portable');
    expect(trait.weight).toBe(1);
    expect(trait.bulk).toBe(1);
    expect(trait.carriedBy).toBeNull();
  });

  test('should create with custom values', () => {
    const trait = new PortableTrait({
      weight: 5.5,
      bulk: 3,
      carriedBy: 'player'
    });

    expect(trait.weight).toBe(5.5);
    expect(trait.bulk).toBe(3);
    expect(trait.carriedBy).toBe('player');
  });

  test('should validate weight', () => {
    const trait = new PortableTrait();

    // Valid weights
    trait.weight = 0;
    expect(() => trait.validate()).not.toThrow();

    trait.weight = 0.1;
    expect(() => trait.validate()).not.toThrow();

    trait.weight = 100;
    expect(() => trait.validate()).not.toThrow();

    // Invalid weights
    trait.weight = -1;
    expect(() => trait.validate()).toThrow('portable.weight: must be non-negative');

    trait.weight = 'heavy' as any;
    expect(() => trait.validate()).toThrow('portable.weight: must be a number');
  });

  test('should validate bulk', () => {
    const trait = new PortableTrait();

    // Valid bulk values
    trait.bulk = 0;
    expect(() => trait.validate()).not.toThrow();

    trait.bulk = 10;
    expect(() => trait.validate()).not.toThrow();

    // Invalid bulk values
    trait.bulk = -1;
    expect(() => trait.validate()).toThrow('portable.bulk: must be non-negative');

    trait.bulk = 'large' as any;
    expect(() => trait.validate()).toThrow('portable.bulk: must be a number');
  });

  test('should track carrier', () => {
    const trait = new PortableTrait();

    expect(trait.carriedBy).toBeNull();
    expect(trait.isCarried()).toBe(false);

    trait.carriedBy = 'player';
    expect(trait.carriedBy).toBe('player');
    expect(trait.isCarried()).toBe(true);

    trait.carriedBy = null;
    expect(trait.carriedBy).toBeNull();
    expect(trait.isCarried()).toBe(false);
  });

  test('should calculate combined weight and bulk', () => {
    const trait = new PortableTrait({ weight: 2.5, bulk: 3 });

    expect(trait.weight).toBe(2.5);
    expect(trait.bulk).toBe(3);

    // Can be used for inventory calculations
    const totalWeight = trait.weight * 2; // If carrying 2 items
    expect(totalWeight).toBe(5);
  });

  test('should handle edge cases', () => {
    const trait = new PortableTrait();

    // Zero weight/bulk items
    trait.weight = 0;
    trait.bulk = 0;
    expect(() => trait.validate()).not.toThrow();

    // Very heavy items
    trait.weight = 9999;
    trait.bulk = 9999;
    expect(() => trait.validate()).not.toThrow();
  });
});

// packages/stdlib/src/world-model/traits/standard/__tests__/supporter.test.ts

import { SupporterTrait } from '../supporter';

describe('SupporterTrait', () => {
  test('should create with defaults', () => {
    const trait = new SupporterTrait();
    expect(trait.type).toBe('supporter');
    expect(trait.capacity).toBe(10);
    expect(trait.supportedEntities).toEqual([]);
    expect(trait.allowStacking).toBe(true);
    expect(trait.surfaceType).toBe('flat');
  });

  test('should create with custom values', () => {
    const trait = new SupporterTrait({
      capacity: 5,
      supportedEntities: ['item1', 'item2'],
      allowStacking: false,
      surfaceType: 'inclined'
    });

    expect(trait.capacity).toBe(5);
    expect(trait.supportedEntities).toEqual(['item1', 'item2']);
    expect(trait.allowStacking).toBe(false);
    expect(trait.surfaceType).toBe('inclined');
  });

  test('should validate capacity', () => {
    const trait = new SupporterTrait();

    // Valid capacities
    trait.capacity = 0;
    expect(() => trait.validate()).not.toThrow();

    trait.capacity = 100;
    expect(() => trait.validate()).not.toThrow();

    // Invalid capacities
    trait.capacity = -1;
    expect(() => trait.validate()).toThrow('supporter.capacity: must be non-negative');

    trait.capacity = 'unlimited' as any;
    expect(() => trait.validate()).toThrow('supporter.capacity: must be a number');
  });

  test('should validate surface type', () => {
    const trait = new SupporterTrait();

    // Valid surface types
    trait.surfaceType = 'flat';
    expect(() => trait.validate()).not.toThrow();

    trait.surfaceType = 'inclined';
    expect(() => trait.validate()).not.toThrow();

    trait.surfaceType = 'irregular';
    expect(() => trait.validate()).not.toThrow();

    // Invalid surface type
    trait.surfaceType = 'curved' as any;
    expect(() => trait.validate()).toThrow('supporter.surfaceType: must be one of: flat, inclined, irregular');
  });

  test('should check if full', () => {
    const trait = new SupporterTrait({ capacity: 3 });

    expect(trait.isFull()).toBe(false);

    trait.supportedEntities = ['item1', 'item2'];
    expect(trait.isFull()).toBe(false);

    trait.supportedEntities = ['item1', 'item2', 'item3'];
    expect(trait.isFull()).toBe(true);

    trait.supportedEntities = ['item1', 'item2', 'item3', 'item4'];
    expect(trait.isFull()).toBe(true); // Over capacity
  });

  test('should calculate remaining capacity', () => {
    const trait = new SupporterTrait({ capacity: 5 });

    expect(trait.getRemainingCapacity()).toBe(5);

    trait.supportedEntities = ['item1', 'item2'];
    expect(trait.getRemainingCapacity()).toBe(3);

    trait.supportedEntities = ['item1', 'item2', 'item3', 'item4', 'item5'];
    expect(trait.getRemainingCapacity()).toBe(0);
  });

  test('should check if entity is supported', () => {
    const trait = new SupporterTrait({
      supportedEntities: ['book', 'lamp', 'vase']
    });

    expect(trait.hasEntity('book')).toBe(true);
    expect(trait.hasEntity('lamp')).toBe(true);
    expect(trait.hasEntity('vase')).toBe(true);
    expect(trait.hasEntity('chair')).toBe(false);
    expect(trait.hasEntity('table')).toBe(false);
  });

  test('should add entities', () => {
    const trait = new SupporterTrait({ capacity: 3 });

    expect(trait.addEntity('item1')).toBe(true);
    expect(trait.supportedEntities).toEqual(['item1']);

    expect(trait.addEntity('item2')).toBe(true);
    expect(trait.supportedEntities).toEqual(['item1', 'item2']);

    expect(trait.addEntity('item3')).toBe(true);
    expect(trait.supportedEntities).toEqual(['item1', 'item2', 'item3']);

    // Should fail when full
    expect(trait.addEntity('item4')).toBe(false);
    expect(trait.supportedEntities).toEqual(['item1', 'item2', 'item3']);

    // Should fail to add duplicate
    expect(trait.addEntity('item1')).toBe(false);
  });

  test('should remove entities', () => {
    const trait = new SupporterTrait({
      supportedEntities: ['item1', 'item2', 'item3']
    });

    expect(trait.removeEntity('item2')).toBe(true);
    expect(trait.supportedEntities).toEqual(['item1', 'item3']);

    expect(trait.removeEntity('item1')).toBe(true);
    expect(trait.supportedEntities).toEqual(['item3']);

    expect(trait.removeEntity('item3')).toBe(true);
    expect(trait.supportedEntities).toEqual([]);

    // Should fail to remove non-existent
    expect(trait.removeEntity('item4')).toBe(false);
  });

  test('should handle stacking restrictions', () => {
    const trait = new SupporterTrait({
      capacity: 5,
      allowStacking: false
    });

    // For game logic to check
    expect(trait.allowStacking).toBe(false);

    // This would be used by the world model to enforce stacking rules
    // e.g., preventing putting things on top of fragile items
  });

  test('should validate unique supported entities', () => {
    const trait = new SupporterTrait({
      supportedEntities: ['item1', 'item2', 'item1'] // duplicate
    });

    expect(() => trait.validate()).toThrow('supporter.supportedEntities: contains duplicate values');
  });
});

