    expect(trait.type).toBe('fixed');
    expect(trait.reason).toBe('It is fixed in place.');
    expect(trait.canBeMoved).toBe(false);
  });

  test('should create with custom reason', () => {
    const trait = new FixedTrait({
      reason: 'The massive stone altar is far too heavy to move.'
    });

    expect(trait.reason).toBe('The massive stone altar is far too heavy to move.');
    expect(trait.canBeMoved).toBe(false);
  });

  test('should create with conditional movement', () => {
    const trait = new FixedTrait({
      reason: 'The bookshelf is bolted to the wall.',
      canBeMoved: true // Can be moved under special conditions
    });

    expect(trait.reason).toBe('The bookshelf is bolted to the wall.');
    expect(trait.canBeMoved).toBe(true);
  });

  test('should validate reason', () => {
    const trait = new FixedTrait();

    // Valid reasons
    trait.reason = 'It is too heavy.';
    expect(() => trait.validate()).not.toThrow();

    trait.reason = '';
    expect(() => trait.validate()).not.toThrow(); // Empty is allowed

    // Invalid reason type
    trait.reason = 123 as any;
    expect(() => trait.validate()).toThrow('fixed.reason: must be a string');
  });

  test('should provide movement prevention info', () => {
    const trait1 = new FixedTrait({
      reason: 'The throne is carved from solid stone.',
      canBeMoved: false
    });

    const trait2 = new FixedTrait({
      reason: 'The painting is secured with a magnetic lock.',
      canBeMoved: true // Could be moved if lock is disabled
    });

    // Game logic would check these properties
    expect(trait1.canBeMoved).toBe(false);
    expect(trait1.reason).toContain('carved from solid stone');

    expect(trait2.canBeMoved).toBe(true);
    expect(trait2.reason).toContain('magnetic lock');
  });

  test('should handle various fixed scenarios', () => {
    // Permanently fixed
    const altar = new FixedTrait({
      reason: 'The altar is part of the temple foundation.',
      canBeMoved: false
    });
    expect(altar.canBeMoved).toBe(false);

    // Conditionally fixed
    const safe = new FixedTrait({
      reason: 'The safe is locked to the floor with heavy bolts.',
      canBeMoved: true // Could be moved with right tools
    });
    expect(safe.canBeMoved).toBe(true);

    // Default fixed
    const statue = new FixedTrait();
    expect(statue.canBeMoved).toBe(false);
    expect(statue.reason).toBe('It is fixed in place.');
  });
});

