  test('should create with custom values', () => {
    const trait = new EdibleTrait({
      nutrition: 5,
      taste: 'delicious',
      poisonous: false,
      portions: 3,
      consumeMessage: 'The apple is crisp and sweet.'
    });

    expect(trait.nutrition).toBe(5);
    expect(trait.taste).toBe('delicious');
    expect(trait.poisonous).toBe(false);
    expect(trait.portions).toBe(3);
    expect(trait.consumeMessage).toBe('The apple is crisp and sweet.');
  });

  test('should validate nutrition', () => {
    const trait = new EdibleTrait();

    // Valid nutrition values
    trait.nutrition = 0;
    expect(() => trait.validate()).not.toThrow();

    trait.nutrition = 10;
    expect(() => trait.validate()).not.toThrow();

    // Invalid nutrition values
    trait.nutrition = -1;
    expect(() => trait.validate()).toThrow('edible.nutrition: must be non-negative');

    trait.nutrition = 'filling' as any;
    expect(() => trait.validate()).toThrow('edible.nutrition: must be a number');
  });

  test('should validate taste', () => {
    const trait = new EdibleTrait();

    // Valid tastes
    const validTastes = ['delicious', 'good', 'bland', 'bad', 'disgusting'];
    for (const taste of validTastes) {
      trait.taste = taste as any;
      expect(() => trait.validate()).not.toThrow();
    }

    // Invalid taste
    trait.taste = 'yummy' as any;
    expect(() => trait.validate()).toThrow('edible.taste: must be one of: delicious, good, bland, bad, disgusting');
  });

  test('should validate portions', () => {
    const trait = new EdibleTrait();

    // Valid portions
    trait.portions = 1;
    expect(() => trait.validate()).not.toThrow();

    trait.portions = 10;
    expect(() => trait.validate()).not.toThrow();

    // Invalid portions
    trait.portions = 0;
    expect(() => trait.validate()).toThrow('edible.portions: must be at least 1');

    trait.portions = -1;
    expect(() => trait.validate()).toThrow('edible.portions: must be at least 1');

    trait.portions = 2.5;
    expect(() => trait.validate()).toThrow('edible.portions: must be a whole number');
  });

  test('should track consumption', () => {
    const trait = new EdibleTrait({ portions: 3 });

    expect(trait.hasPortions()).toBe(true);
    expect(trait.portions).toBe(3);

    // Consume one portion
    expect(trait.consume()).toBe(true);
    expect(trait.portions).toBe(2);
    expect(trait.hasPortions()).toBe(true);

    // Consume another
    expect(trait.consume()).toBe(true);
    expect(trait.portions).toBe(1);
    expect(trait.hasPortions()).toBe(true);

    // Consume last portion
    expect(trait.consume()).toBe(true);
    expect(trait.portions).toBe(0);
    expect(trait.hasPortions()).toBe(false);

    // Try to consume when empty
    expect(trait.consume()).toBe(false);
    expect(trait.portions).toBe(0);
  });

  test('should handle poisonous food', () => {
    const mushroom = new EdibleTrait({
      nutrition: 2,
      taste: 'good',
      poisonous: true,
      consumeMessage: 'The mushroom tastes earthy and rich.'
    });

    expect(mushroom.poisonous).toBe(true);
    expect(mushroom.taste).toBe('good'); // Can taste good but still be poisonous
  });

  test('should provide consumption info', () => {
    const foods = [
      new EdibleTrait({
        nutrition: 10,
        taste: 'delicious',
        consumeMessage: 'The steak is perfectly cooked and satisfying.'
      }),
      new EdibleTrait({
        nutrition: 0,
        taste: 'disgusting',
        poisonous: true,
        consumeMessage: 'You immediately regret eating the rotten meat.'
      }),
      new EdibleTrait({
        nutrition: 3,
        taste: 'bland',
        portions: 5,
        consumeMessage: 'The bread is dry but filling.'
      })
