    expect(() => trait.validate()).not.toThrow();

    trait.coverage = 0.5;
    expect(() => trait.validate()).not.toThrow();

    trait.coverage = 1;
    expect(() => trait.validate()).not.toThrow();

    // Invalid coverage
    trait.coverage = -0.1;
    expect(() => trait.validate()).toThrow('wearable.coverage: must be between 0 and 1');

    trait.coverage = 1.1;
    expect(() => trait.validate()).toThrow('wearable.coverage: must be between 0 and 1');
