// packages/stdlib/src/world-model/traits/interactive/__tests__/readable.test.ts

import { ReadableTrait } from '../readable';

describe('ReadableTrait', () => {
  test('should create with defaults', () => {
    const trait = new ReadableTrait();
    expect(trait.type).toBe('readable');
    expect(trait.text).toBe('');
    expect(trait.shortText).toBe('');
    expect(trait.requiresLight).toBe(true);
    expect(trait.language).toBe('common');
    expect(trait.pages).toBeNull();
  });

  test('should create with custom values', () => {
    const trait = new ReadableTrait({
      text: 'Once upon a time...',
      shortText: 'A fairy tale',
      requiresLight: false,
      language: 'elvish',
      pages: 42
    });

    expect(trait.text).toBe('Once upon a time...');
    expect(trait.shortText).toBe('A fairy tale');
    expect(trait.requiresLight).toBe(false);
    expect(trait.language).toBe('elvish');
    expect(trait.pages).toBe(42);
  });

  test('should validate text', () => {
    const trait = new ReadableTrait();

    // Empty text is valid (blank pages)
    trait.text = '';
    expect(() => trait.validate()).not.toThrow();

    // Valid text
    trait.text = 'Lorem ipsum dolor sit amet';
    expect(() => trait.validate()).not.toThrow();

    // Invalid text type
    trait.text = 123 as any;
    expect(() => trait.validate()).toThrow('readable.text: must be a string');
  });

  test('should validate pages', () => {
    const trait = new ReadableTrait();

    // Null pages is valid (single sheet)
    trait.pages = null;
    expect(() => trait.validate()).not.toThrow();

    // Valid page counts
    trait.pages = 1;
    expect(() => trait.validate()).not.toThrow();

    trait.pages = 1000;
    expect(() => trait.validate()).not.toThrow();

    // Invalid page counts
    trait.pages = 0;
    expect(() => trait.validate()).toThrow('readable.pages: must be at least 1');

    trait.pages = -1;
    expect(() => trait.validate()).toThrow('readable.pages: must be at least 1');

    trait.pages = 3.5;
    expect(() => trait.validate()).toThrow('readable.pages: must be a whole number');
  });

  test('should handle different readable types', () => {
    // Book
    const book = new ReadableTrait({
      text: 'Chapter 1: The Beginning\n\nIt was a dark and stormy night...',
      shortText: 'A mystery novel',
      pages: 300,
      requiresLight: true
    });
    expect(book.pages).toBe(300);
    expect(book.requiresLight).toBe(true);

    // Note
    const note = new ReadableTrait({
      text: 'Meet me at midnight by the old oak tree. -A',
      shortText: 'A hastily written note',
      pages: 1
    });
    expect(note.pages).toBe(1);

    // Magic scroll (glows)
    const scroll = new ReadableTrait({
      text: 'Incantation of Fire: Ignis Magnus!',
      shortText: 'A glowing magical scroll',
      requiresLight: false, // Self-illuminating
      language: 'arcane'
    });
    expect(scroll.requiresLight).toBe(false);

    // Sign
    const sign = new ReadableTrait({
      text: 'DANGER: Do Not Enter!',
      shortText: 'A warning sign',
      pages: null // Not a paged item
    });
    expect(sign.pages).toBeNull();
  });

  test('should support multiple languages', () => {
    const texts = [
      new ReadableTrait({ text: 'Hello', language: 'common' }),
      new ReadableTrait({ text: 'Vedui', language: 'elvish' }),
      new ReadableTrait({ text: 'Khazad', language: 'dwarvish' }),
      new ReadableTrait({ text: '', language: 'arcane' })
    ];

    expect(texts[0].language).toBe('common');
    expect(texts[1].language).toBe('elvish');
    expect(texts[2].language).toBe('dwarvish');
    expect(texts[3].language).toBe('arcane');
  });

  test('should provide summary text', () => {
    const longBook = new ReadableTrait({
      text: 'A'.repeat(1000), // Very long text
      shortText: 'An epic saga',
      pages: 500
    });

    // Short text provides summary without reading full content
    expect(longBook.shortText).toBe('An epic saga');
    expect(longBook.text.length).toBe(1000);
  });

  test('should handle blank or damaged texts', () => {
    // Blank book
    const blank = new ReadableTrait({
      text: '',
      shortText: 'A blank journal',
      pages: 100
    });
    expect(blank.text).toBe('');

    // Damaged text
    const damaged = new ReadableTrait({
      text: 'The rest of the text is too damaged to read...',
      shortText: 'A water-damaged letter',
      pages: 1
    });
    expect(damaged.text).toContain('damaged');
  });
});

