import { SwitchableTrait } from '../switchable';

describe('SwitchableTrait', () => {
  test('should create with defaults', () => {
    const trait = new SwitchableTrait();
    expect(trait.type).toBe('switchable');
    expect(trait.on).toBe(false);
    expect(trait.switchType).toBe('toggle');
    expect(trait.activateVerb).toBe('switch');
    expect(trait.powerSource).toBeNull();
  });

  test('should create with custom values', () => {
    const trait = new SwitchableTrait({
      on: true,
      switchType: 'button',
      activateVerb: 'press',
      powerSource: 'battery'
    });

    expect(trait.on).toBe(true);
    expect(trait.switchType).toBe('button');
    expect(trait.activateVerb).toBe('press');
    expect(trait.powerSource).toBe('battery');
  });

  test('should validate switch type', () => {
    const trait = new SwitchableTrait();

    // Valid switch types
    const validTypes = ['toggle', 'button', 'lever', 'dial', 'touch'];
    for (const type of validTypes) {
      trait.switchType = type as any;
      expect(() => trait.validate()).not.toThrow();
    }

    // Invalid switch type
    trait.switchType = 'knob' as any;
    expect(() => trait.validate()).toThrow('switchable.switchType: must be one of: toggle, button, lever, dial, touch');
  });

  test('should toggle state', () => {
    const trait = new SwitchableTrait();

    expect(trait.on).toBe(false);
    expect(trait.isOn()).toBe(false);

    trait.toggle();
    expect(trait.on).toBe(true);
    expect(trait.isOn()).toBe(true);

    trait.toggle();
    expect(trait.on).toBe(false);
    expect(trait.isOn()).toBe(false);
  });

  test('should turn on and off', () => {
    const trait = new SwitchableTrait();

    // Turn on
    trait.turnOn();
    expect(trait.on).toBe(true);

    // Turn on again (no change)
    trait.turnOn();
    expect(trait.on).toBe(true);

    // Turn off
    trait.turnOff();
    expect(trait.on).toBe(false);

    // Turn off again (no change)
    trait.turnOff();
    expect(trait.on).toBe(false);
  });

  test('should handle different switch types', () => {
    // Light switch
    const lightSwitch = new SwitchableTrait({
      switchType: 'toggle',
      activateVerb: 'flip'
    });
    expect(lightSwitch.activateVerb).toBe('flip');

    // Push button
    const button = new SwitchableTrait({
      switchType: 'button',
      activateVerb: 'press'
    });
    expect(button.activateVerb).toBe('press');

    // Lever
    const lever = new SwitchableTrait({
      switchType: 'lever',
      activateVerb: 'pull'
    });
    expect(lever.activateVerb).toBe('pull');

    // Touch panel
    const touchPanel = new SwitchableTrait({
      switchType: 'touch',
      activateVerb: 'touch',
      powerSource: 'mains'
    });
    expect(touchPanel.switchType).toBe('touch');
    expect(touchPanel.powerSource).toBe('mains');
  });

  test('should track power source', () => {
    // Battery powered
    const flashlight = new SwitchableTrait({
      powerSource: 'battery'
    });
    expect(flashlight.powerSource).toBe('battery');

    // Mains powered
    const lamp = new SwitchableTrait({
      powerSource: 'mains'
    });
    expect(lamp.powerSource).toBe('mains');

    // No power source (mechanical)
    const mechanism = new SwitchableTrait({
      powerSource: null
    });
    expect(mechanism.powerSource).toBeNull();
  });

  test('should support custom activation verbs', () => {
    const devices = [
      new SwitchableTrait({ activateVerb: 'flip' }),
      new SwitchableTrait({ activateVerb: 'press' }),
      new SwitchableTrait({ activateVerb: 'turn' }),
      new SwitchableTrait({ activateVerb: 'activate' }),
      new SwitchableTrait({ activateVerb: 'engage' })
    ];

    expect(devices[0].activateVerb).toBe('flip');
    expect(devices[1].activateVerb).toBe('press');
    expect(devices[2].activateVerb).toBe('turn');
    expect(devices[3].activateVerb).toBe('activate');
    expect(devices[4].activateVerb).toBe('engage');
  });

  test('should handle state changes', () => {
    const trait = new SwitchableTrait();
    let stateChanges = 0;

    // Track state changes (in real usage, this would emit events)
    const originalToggle = trait.toggle.bind(trait);
    trait.toggle = function() {
      originalToggle();
      stateChanges++;
    };

    trait.toggle();
    expect(stateChanges).toBe(1);
    expect(trait.on).toBe(true);

    trait.toggle();
    expect(stateChanges).toBe(2);
    expect(trait.on).toBe(false);
  });
});

