    for (const intensity of validIntensities) {
      trait.intensity = intensity as any;
      expect(() => trait.validate()).not.toThrow();
    }

    // Invalid intensity
    trait.intensity = 'super-bright' as any;
    expect(() => trait.validate()).toThrow('lightSource.intensity: must be one of: dim, normal, bright, blinding');
  });

  test('should validate radius', () => {
    const trait = new LightSourceTrait();

    // Valid radius values
    trait.radius = 0; // Point light
    expect(() => trait.validate()).not.toThrow();

    trait.radius = 100; // Very large radius
    expect(() => trait.validate()).not.toThrow();

    // Invalid radius values
    trait.radius = -1;
    expect(() => trait.validate()).toThrow('lightSource.radius: must be non-negative');

    trait.radius = 'far' as any;
    expect(() => trait.validate()).toThrow('lightSource.radius: must be a number');
  });

  test('should check if providing light', () => {
    const trait = new LightSourceTrait();

    expect(trait.isProvidingLight()).toBe(false);

    trait.providing = true;
    expect(trait.isProvidingLight()).toBe(true);

    trait.providing = false;
    expect(trait.isProvidingLight()).toBe(false);
  });

  test('should turn light on and off', () => {
    const trait = new LightSourceTrait();

    expect(trait.providing).toBe(false);

    trait.turnOn();
    expect(trait.providing).toBe(true);

    trait.turnOff();
    expect(trait.providing).toBe(false);
  });

  test('should handle different light sources', () => {
    // Candle
    const candle = new LightSourceTrait({
      intensity: 'dim',
      radius: 3,
      color: 'yellow',
      requiresPower: false
    });
    expect(candle.intensity).toBe('dim');
    expect(candle.color).toBe('yellow');

    // Torch
    const torch = new LightSourceTrait({
      intensity: 'normal',
      radius: 5,
      color: 'orange',
      requiresPower: false
    });
    expect(torch.radius).toBe(5);

    // Electric lamp
    const lamp = new LightSourceTrait({
      intensity: 'bright',
      radius: 8,
      color: 'white',
      requiresPower: true
    });
    expect(lamp.requiresPower).toBe(true);

    // Magic crystal
    const crystal = new LightSourceTrait({
      intensity: 'normal',
      radius: 10,
      color: 'blue',
      requiresPower: false,
      providing: true // Always on
    });
    expect(crystal.color).toBe('blue');
    expect(crystal.providing).toBe(true);
  });

  test('should calculate light coverage', () => {
    const sources = [
      new LightSourceTrait({ radius: 0 }), // Point light
      new LightSourceTrait({ radius: 5 }), // Normal radius
      new LightSourceTrait({ radius: 20 }) // Large radius
    ];

    expect(sources[0].radius).toBe(0);
    expect(sources[1].radius).toBe(5);
    expect(sources[2].radius).toBe(20);

    // Intensity affects perceived brightness, not radius
    const dimLight = new LightSourceTrait({ intensity: 'dim', radius: 10 });
    const brightLight = new LightSourceTrait({ intensity: 'bright', radius: 10 });

    expect(dimLight.radius).toBe(10);
    expect(brightLight.radius).toBe(10);
    expect(dimLight.intensity).not.toBe(brightLight.intensity);
  });

  test('should support colored light', () => {
    const lights = [
      new LightSourceTrait({ color: 'white' }),
      new LightSourceTrait({ color: 'yellow' }),
      new LightSourceTrait({ color: 'red' }),
      new LightSourceTrait({ color: 'green' }),
      new LightSourceTrait({ color: 'blue' }),
      new LightSourceTrait({ color: 'purple' })
    ];

    expect(lights[0].color).toBe('white');
    expect(lights[1].color).toBe('yellow');
    expect(lights[2].color).toBe('red');
    expect(lights[3].color).toBe('green');
    expect(lights[4].color).toBe('blue');
    expect(lights[5].color).toBe('purple');
  });

  test('should handle power requirements', () => {
    // Flashlight needs power
    const flashlight = new LightSourceTrait({
      requiresPower: true,
      providing: false
    });

    // Won't light without power in real game logic
    flashlight.turnOn();
    expect(flashlight.providing).toBe(true);
    expect(flashlight.requiresPower).toBe(true);

    // Fire doesn't need power
    const fire = new LightSourceTrait({
      requiresPower: false,
      providing: true
    });

    expect(fire.requiresPower).toBe(false);
    expect(fire.isProvidingLight()).toBe(true);
  });
});

// packages/stdlib/src/world-model/traits/interactive/__tests__/light-sensitive.test.ts

import { LightSensitiveTrait } from '../light-sensitive';

describe('LightSensitiveTrait', () => {
  test('should create with defaults', () => {
    const trait = new LightSensitiveTrait();
    expect(trait.type).toBe('lightSensitive');
    expect(trait.minimumLight).toBe('normal');
    expect(trait.inDarknessMessage).toBe('It is too dark to see that properly.');
  });

  test('should create with custom values', () => {
    const trait = new LightSensitiveTrait({
      minimumLight: 'bright',
      inDarknessMessage: 'You need more light to read the ancient text.'
    });

    expect(trait.minimumLight).toBe('bright');
    expect(trait.inDarknessMessage).toBe('You need more light to read the ancient text.');
  });

  test('should validate minimum light level', () => {
    const trait = new LightSensitiveTrait();

    // Valid light levels
    const validLevels = ['dim', 'normal', 'bright'];
    for (const level of validLevels) {
      trait.minimumLight = level as any;
      expect(() => trait.validate()).not.toThrow();
    }

    // Invalid light level
    trait.minimumLight = 'blinding' as any;
    expect(() => trait.validate()).toThrow('lightSensitive.minimumLight: must be one of: dim, normal, bright');
  });

  test('should check if light requirements are met', () => {
    const trait = new LightSensitiveTrait({ minimumLight: 'normal' });

    // Light level hierarchy: none < dim < normal < bright < blinding
    expect(trait.hasEnoughLight('none')).toBe(false);
    expect(trait.hasEnoughLight('dim')).toBe(false);
    expect(trait.hasEnoughLight('normal')).toBe(true);
    expect(trait.hasEnoughLight('bright')).toBe(true);
    expect(trait.hasEnoughLight('blinding')).toBe(true);
  });

  test('should handle different minimum light requirements', () => {
    // Dim light requirement (easy to see)
    const dimRequired = new LightSensitiveTrait({ minimumLight: 'dim' });
    expect(dimRequired.hasEnoughLight('none')).toBe(false);
    expect(dimRequired.hasEnoughLight('dim')).toBe(true);
    expect(dimRequired.hasEnoughLight('normal')).toBe(true);

    // Normal light requirement (standard)
    const normalRequired = new LightSensitiveTrait({ minimumLight: 'normal' });
    expect(normalRequired.hasEnoughLight('dim')).toBe(false);
    expect(normalRequired.hasEnoughLight('normal')).toBe(true);
    expect(normalRequired.hasEnoughLight('bright')).toBe(true);

    // Bright light requirement (hard to see)
    const brightRequired = new LightSensitiveTrait({ minimumLight: 'bright' });
    expect(brightRequired.hasEnoughLight('normal')).toBe(false);
    expect(brightRequired.hasEnoughLight('bright')).toBe(true);
    expect(brightRequired.hasEnoughLight('blinding')).toBe(true);
  });

  test('should provide appropriate darkness messages', () => {
    // Reading material
    const book = new LightSensitiveTrait({
      minimumLight: 'normal',
      inDarknessMessage: 'It is too dark to read.'
    });
    expect(book.inDarknessMessage).toBe('It is too dark to read.');

    // Fine detail work
    const clockwork = new LightSensitiveTrait({
      minimumLight: 'bright',
      inDarknessMessage: 'You need bright light to see the intricate mechanisms.'
    });
    expect(clockwork.inDarknessMessage).toContain('bright light');

    // Secret writing
    const invisibleInk = new LightSensitiveTrait({
      minimumLight: 'bright',
      inDarknessMessage: 'The hidden text only appears under bright light.'
    });
    expect(invisibleInk.minimumLight).toBe('bright');
  });

  test('should handle complete darkness', () => {
    const traits = [
      new LightSensitiveTrait({ minimumLight: 'dim' }),
      new LightSensitiveTrait({ minimumLight: 'normal' }),
      new LightSensitiveTrait({ minimumLight: 'bright' })
    ];

    // Nothing visible in complete darkness
    for (const trait of traits) {
      expect(trait.hasEnoughLight('none')).toBe(false);
    }
  });

  test('should support light level comparisons', () => {
    const trait = new LightSensitiveTrait();

    // Test light level ordering
    const levels: Array<'none' | 'dim' | 'normal' | 'bright' | 'blinding'> = 
      ['none', 'dim', 'normal', 'bright', 'blinding'];

    // Each level should be sufficient for requirements at or below it
    const dimTrait = new LightSensitiveTrait({ minimumLight: 'dim' });
    const normalTrait = new LightSensitiveTrait({ minimumLight: 'normal' });
    const brightTrait = new LightSensitiveTrait({ minimumLight: 'bright' });

    // Test each combination
    expect(dimTrait.hasEnoughLight('none')).toBe(false);
    expect(dimTrait.hasEnoughLight('dim')).toBe(true);
    expect(normalTrait.hasEnoughLight('dim')).toBe(false);
    expect(normalTrait.hasEnoughLight('normal')).toBe(true);
    expect(brightTrait.hasEnoughLight('normal')).toBe(false);
    expect(brightTrait.hasEnoughLight('bright')).toBe(true);
  });
});

