// packages/stdlib/src/world-model/traits/advanced/__tests__/player.test.ts

import { PlayerTrait } from '../player';

describe('PlayerTrait', () => {
  test('should create with defaults', () => {
    const trait = new PlayerTrait();
    expect(trait.type).toBe('player');
    expect(trait.score).toBe(0);
    expect(trait.moves).toBe(0);
    expect(trait.inventory).toEqual([]);
    expect(trait.wearing).toEqual([]);
    expect(trait.maxInventory).toBe(10);
    expect(trait.maxWeight).toBe(100);
    expect(trait.health).toBe(100);
    expect(trait.maxHealth).toBe(100);
  });

  test('should create with custom values', () => {
    const trait = new PlayerTrait({
      score: 50,
      moves: 10,
      inventory: ['sword', 'shield'],
      wearing: ['armor'],
      maxInventory: 15,
      maxWeight: 150,
      health: 80,
      maxHealth: 120
    });

    expect(trait.score).toBe(50);
    expect(trait.moves).toBe(10);
    expect(trait.inventory).toEqual(['sword', 'shield']);
    expect(trait.wearing).toEqual(['armor']);
    expect(trait.maxInventory).toBe(15);
    expect(trait.maxWeight).toBe(150);
    expect(trait.health).toBe(80);
    expect(trait.maxHealth).toBe(120);
  });

  test('should validate score and moves', () => {
    const trait = new PlayerTrait();

    // Valid values
    trait.score = 0;
    trait.moves = 0;
    expect(() => trait.validate()).not.toThrow();

    trait.score = 1000;
    trait.moves = 500;
    expect(() => trait.validate()).not.toThrow();

    // Negative score is allowed (penalties)
    trait.score = -10;
    expect(() => trait.validate()).not.toThrow();

    // Invalid moves
    trait.moves = -1;
    expect(() => trait.validate()).toThrow('player.moves: cannot be negative');
  });

  test('should validate health', () => {
    const trait = new PlayerTrait();

    // Valid health
    trait.health = 50;
    trait.maxHealth = 100;
    expect(() => trait.validate()).not.toThrow();

    // Health can be 0 (unconscious/dead)
    trait.health = 0;
    expect(() => trait.validate()).not.toThrow();

    // Invalid health
    trait.health = -1;
    expect(() => trait.validate()).toThrow('player.health: cannot be negative');

    trait.health = 150;
    trait.maxHealth = 100;
    expect(() => trait.validate()).toThrow('player.health: cannot exceed maxHealth');
  });

  test('should validate inventory limits', () => {
    const trait = new PlayerTrait();

    // Valid limits
    trait.maxInventory = 0; // No inventory
    expect(() => trait.validate()).not.toThrow();

    trait.maxInventory = 100;
    expect(() => trait.validate()).not.toThrow();

    // Invalid limits
    trait.maxInventory = -1;
    expect(() => trait.validate()).toThrow('player.maxInventory: must be non-negative');
  });

  test('should track game progress', () => {
    const trait = new PlayerTrait();

    // Add score
    trait.addScore(10);
    expect(trait.score).toBe(10);

    trait.addScore(25);
    expect(trait.score).toBe(35);

    // Negative score
    trait.addScore(-5);
    expect(trait.score).toBe(30);

    // Increment moves
    trait.incrementMoves();
    expect(trait.moves).toBe(1);

    trait.incrementMoves();
    trait.incrementMoves();
    expect(trait.moves).toBe(3);
  });

  test('should manage health', () => {
    const trait = new PlayerTrait({ health: 100, maxHealth: 100 });

    // Take damage
    trait.takeDamage(20);
    expect(trait.health).toBe(80);

    trait.takeDamage(30);
    expect(trait.health).toBe(50);

    // Heal
    trait.heal(25);
    expect(trait.health).toBe(75);

    // Can't exceed max health
    trait.heal(50);
    expect(trait.health).toBe(100);

    // Can't go below 0
    trait.takeDamage(150);
    expect(trait.health).toBe(0);
  });

  test('should check if alive', () => {
    const trait = new PlayerTrait({ health: 50 });

    expect(trait.isAlive()).toBe(true);

    trait.health = 1;
    expect(trait.isAlive()).toBe(true);

    trait.health = 0;
    expect(trait.isAlive()).toBe(false);
  });

  test('should manage inventory', () => {
    const trait = new PlayerTrait({ maxInventory: 3 });

    // Add items
    expect(trait.canCarry()).toBe(true);
    expect(trait.addToInventory('key')).toBe(true);
    expect(trait.inventory).toEqual(['key']);

    expect(trait.addToInventory('torch')).toBe(true);
    expect(trait.inventory).toEqual(['key', 'torch']);

    expect(trait.addToInventory('sword')).toBe(true);
    expect(trait.inventory).toEqual(['key', 'torch', 'sword']);

    // Inventory full
    expect(trait.canCarry()).toBe(false);
    expect(trait.addToInventory('shield')).toBe(false);
    expect(trait.inventory).toEqual(['key', 'torch', 'sword']);

    // Remove item
    expect(trait.removeFromInventory('torch')).toBe(true);
    expect(trait.inventory).toEqual(['key', 'sword']);
    expect(trait.canCarry()).toBe(true);

    // Remove non-existent
    expect(trait.removeFromInventory('potion')).toBe(false);
  });

  test('should check inventory contents', () => {
    const trait = new PlayerTrait({
      inventory: ['sword', 'shield', 'potion', 'potion']
    });

    expect(trait.hasInInventory('sword')).toBe(true);
    expect(trait.hasInInventory('shield')).toBe(true);
    expect(trait.hasInInventory('potion')).toBe(true);
    expect(trait.hasInInventory('key')).toBe(false);

    // Count items
    expect(trait.countInInventory('potion')).toBe(2);
    expect(trait.countInInventory('sword')).toBe(1);
    expect(trait.countInInventory('key')).toBe(0);
  });

  test('should manage worn items', () => {
    const trait = new PlayerTrait();

    // Wear items
    expect(trait.wear('hat')).toBe(true);
    expect(trait.wearing).toEqual(['hat']);
    expect(trait.isWearing('hat')).toBe(true);

    expect(trait.wear('cloak')).toBe(true);
    expect(trait.wearing).toEqual(['hat', 'cloak']);

    // Can't wear same item twice
    expect(trait.wear('hat')).toBe(false);

    // Remove worn items
    expect(trait.removeWorn('hat')).toBe(true);
    expect(trait.wearing).toEqual(['cloak']);
    expect(trait.isWearing('hat')).toBe(false);

    expect(trait.removeWorn('boots')).toBe(false); // Not wearing
  });

  test('should validate unique inventory items', () => {
    const trait = new PlayerTrait({
      inventory: ['item1', 'item2', 'item1'] // duplicate
    });

    // Note: This might be allowed for stackable items
    // Adjust validation based on game requirements
    expect(() => trait.validate()).not.toThrow(); // Allow duplicates for now
  });

  test('should track player statistics', () => {
    const trait = new PlayerTrait();

    // Simulate game progression
    trait.incrementMoves();
    trait.addScore(10);
    trait.takeDamage(20);
    trait.addToInventory('treasure');
    trait.wear('armor');

    expect(trait.moves).toBe(1);
    expect(trait.score).toBe(10);
    expect(trait.health).toBe(80);
    expect(trait.inventory).toContain('treasure');
    expect(trait.wearing).toContain('armor');
  });
});

// packages/stdlib/src/world-model/traits/advanced/__tests__/npc.test.ts

import { NPCTrait } from '../npc';

describe('NPCTrait', () => {
  test('should create with defaults', () => {
    const trait = new NPCTrait();
    expect(trait.type).toBe('npc');
    expect(trait.conversationState).toBe('initial');
    expect(trait.mood).toBe('neutral');
    expect(trait.relationship).toBe(0);
    expect(trait.memory).toEqual({});
    expect(trait.goals).toEqual([]);
    expect(trait.schedule).toBeNull();
  });

  test('should create with custom values', () => {
    const trait = new NPCTrait({
      conversationState: 'quest-given',
      mood: 'happy',
      relationship: 50,
      memory: { metPlayer: true, questGiven: 'find-artifact' },
      goals: ['patrol', 'guard-door'],
      schedule: { morning: 'tavern', afternoon: 'market', evening: 'home' }
    });

    expect(trait.conversationState).toBe('quest-given');
    expect(trait.mood).toBe('happy');
    expect(trait.relationship).toBe(50);
    expect(trait.memory.metPlayer).toBe(true);
    expect(trait.goals).toContain('patrol');
    expect(trait.schedule?.morning).toBe('tavern');
  });

  test('should validate mood', () => {
    const trait = new NPCTrait();

    // Valid moods
    const validMoods = ['hostile', 'angry', 'neutral', 'friendly', 'happy'];
    for (const mood of validMoods) {
      trait.mood = mood as any;
      expect(() => trait.validate()).not.toThrow();
    }

    // Invalid mood
    trait.mood = 'ecstatic' as any;
    expect(() => trait.validate()).toThrow('npc.mood: must be one of: hostile, angry, neutral, friendly, happy');
  });

  test('should validate relationship', () => {
    const trait = new NPCTrait();

    // Valid relationship values
    trait.relationship = -100;
    expect(() => trait.validate()).not.toThrow();

    trait.relationship = 0;
    expect(() => trait.validate()).not.toThrow();

    trait.relationship = 100;
    expect(() => trait.validate()).not.toThrow();

    // Invalid relationship values
    trait.relationship = -101;
    expect(() => trait.validate()).toThrow('npc.relationship: must be between -100 and 100');

    trait.relationship = 101;
    expect(() => trait.validate()).toThrow('npc.relationship: must be between -100 and 100');
  });

  test('should manage conversation state', () => {
    const trait = new NPCTrait();

    expect(trait.conversationState).toBe('initial');

    trait.setConversationState('introduced');
    expect(trait.conversationState).toBe('introduced');

    trait.setConversationState('quest-given');
    expect(trait.conversationState).toBe('quest-given');
  });

  test('should manage mood changes', () => {
    const trait = new NPCTrait({ mood: 'neutral' });

    trait.setMood('happy');
    expect(trait.mood).toBe('happy');

    trait.setMood('angry');
    expect(trait.mood).toBe('angry');

    // Mood affects behavior
    trait.setMood('hostile');
    expect(trait.mood).toBe('hostile');
  });

  test('should manage relationship changes', () => {
    const trait = new NPCTrait();

    // Improve relationship
    trait.adjustRelationship(10);
    expect(trait.relationship).toBe(10);

    trait.adjustRelationship(20);
    expect(trait.relationship).toBe(30);

    // Worsen relationship
    trait.adjustRelationship(-50);
    expect(trait.relationship).toBe(-20);

    // Clamp to bounds
    trait.adjustRelationship(200);
    expect(trait.relationship).toBe(100); // Clamped to max

    trait.adjustRelationship(-300);
    expect(trait.relationship).toBe(-100); // Clamped to min
  });

  test('should manage memory', () => {
    const trait = new NPCTrait();

    // Remember things
    trait.remember('playerName', 'Alex');
    expect(trait.memory.playerName).toBe('Alex');

    trait.remember('questCompleted', true);
    expect(trait.memory.questCompleted).toBe(true);

    trait.remember('lastMeetingTurn', 42);
    expect(trait.memory.lastMeetingTurn).toBe(42);

    // Check memories
    expect(trait.recalls('playerName')).toBe(true);
    expect(trait.recalls('questCompleted')).toBe(true);
    expect(trait.recalls('neverSet')).toBe(false);

    // Forget things
    trait.forget('playerName');
    expect(trait.recalls('playerName')).toBe(false);
    expect(trait.memory.playerName).toBeUndefined();
  });

  test('should manage goals', () => {
    const trait = new NPCTrait();

    // Add goals
    trait.addGoal('find-player');
    expect(trait.goals).toEqual(['find-player']);

    trait.addGoal('return-home');
    expect(trait.goals).toEqual(['find-player', 'return-home']);

    // Check goals
    expect(trait.hasGoal('find-player')).toBe(true);
    expect(trait.hasGoal('attack-enemy')).toBe(false);

    // Remove goals
    trait.removeGoal('find-player');
    expect(trait.goals).toEqual(['return-home']);
    expect(trait.hasGoal('find-player')).toBe(false);

    // Clear all goals
    trait.addGoal('new-goal');
    trait.clearGoals();
    expect(trait.goals).toEqual([]);
  });

  test('should handle different NPC types', () => {
    // Merchant NPC
    const merchant = new NPCTrait({
      mood: 'friendly',
      memory: { shopOpen: true, dailySales: 0 },
      goals: ['sell-goods', 'make-profit']
    });
    expect(merchant.goals).toContain('sell-goods');

    // Guard NPC
    const guard = new NPCTrait({
      mood: 'neutral',
      memory: { onDuty: true, suspiciousActivity: false },
      goals: ['patrol', 'check-passes']
    });
    expect(guard.mood).toBe('neutral');

    // Quest giver NPC
    const sage = new NPCTrait({
      conversationState: 'waiting-for-hero',
      memory: { prophecyRevealed: false },
      goals: ['find-chosen-one', 'save-kingdom']
    });
    expect(sage.conversationState).toBe('waiting-for-hero');
  });

  test('should handle schedules', () => {
    const trait = new NPCTrait({
      schedule: {
        morning: 'market',
        afternoon: 'tavern',
        evening: 'home',
        night: 'home'
      }
    });

    expect(trait.schedule?.morning).toBe('market');
    expect(trait.schedule?.afternoon).toBe('tavern');
    expect(trait.schedule?.evening).toBe('home');
    expect(trait.schedule?.night).toBe('home');
  });

  test('should track complex interactions', () => {
    const trait = new NPCTrait();

    // First meeting
    trait.remember('metPlayer', true);
    trait.remember('firstMeetingTurn', 1);
    trait.setMood('neutral');

    // Give quest
    trait.setConversationState('quest-given');
    trait.remember('activeQuest', 'find-artifact');
    trait.adjustRelationship(10);

    // Quest completed
    trait.remember('questCompleted', true);
    trait.adjustRelationship(50);
    trait.setMood('happy');

    // Check final state
    expect(trait.memory.metPlayer).toBe(true);
    expect(trait.memory.questCompleted).toBe(true);
    expect(trait.relationship).toBe(60);
    expect(trait.mood).toBe('happy');
  });
});

