import { DoorTrait } from '../door';

describe('DoorTrait', () => {
  test('should create with defaults', () => {
    const trait = new DoorTrait();
    expect(trait.type).toBe('door');
    expect(trait.connects).toEqual([]);
    expect(trait.direction).toEqual({});
    expect(trait.bidirectional).toBe(true);
    expect(trait.hidden).toBe(false);
    expect(trait.hiddenMessage).toBe('');
  });

  test('should create with custom values', () => {
    const trait = new DoorTrait({
      connects: ['room1', 'room2'],
      direction: { room1: 'north', room2: 'south' },
      bidirectional: false,
      hidden: true,
      hiddenMessage: 'A secret door is concealed behind the bookshelf.'
    });

    expect(trait.connects).toEqual(['room1', 'room2']);
    expect(trait.direction.room1).toBe('north');
    expect(trait.direction.room2).toBe('south');
    expect(trait.bidirectional).toBe(false);
    expect(trait.hidden).toBe(true);
    expect(trait.hiddenMessage).toContain('secret door');
  });

  test('should validate connections', () => {
    const trait = new DoorTrait();

    // Valid connections
    trait.connects = ['room1', 'room2'];
    expect(() => trait.validate()).not.toThrow();

    // Invalid - too few connections
    trait.connects = ['room1'];
    expect(() => trait.validate()).toThrow('door.connects: must connect exactly 2 rooms');

    // Invalid - too many connections
    trait.connects = ['room1', 'room2', 'room3'];
    expect(() => trait.validate()).toThrow('door.connects: must connect exactly 2 rooms');

    // Invalid - duplicate rooms
    trait.connects = ['room1', 'room1'];
    expect(() => trait.validate()).toThrow('door.connects: cannot connect a room to itself');
  });

  test('should validate directions', () => {
    const trait = new DoorTrait({
      connects: ['room1', 'room2']
    });

    // Valid directions
    const validDirs = ['north', 'south', 'east', 'west', 'northeast', 'northwest', 'southeast', 'southwest', 'up', 'down', 'in', 'out'];
    trait.direction.room1 = 'north';
    expect(() => trait.validate()).not.toThrow();

    // All valid directions
    for (const dir of validDirs) {
      trait.direction.room1 = dir as any;
      expect(() => trait.validate()).not.toThrow();
    }

    // Invalid direction
    trait.direction.room1 = 'forward' as any;
    expect(() => trait.validate()).toThrow('door.direction: invalid direction');
  });

  test('should get connections', () => {
    const trait = new DoorTrait({
      connects: ['hallway', 'library']
    });

    expect(trait.getConnectedRooms()).toEqual(['hallway', 'library']);
    expect(trait.connectsRoom('hallway')).toBe(true);
    expect(trait.connectsRoom('library')).toBe(true);
    expect(trait.connectsRoom('kitchen')).toBe(false);
  });

  test('should get other room', () => {
    const trait = new DoorTrait({
      connects: ['room1', 'room2']
    });

    expect(trait.getOtherRoom('room1')).toBe('room2');
    expect(trait.getOtherRoom('room2')).toBe('room1');
    expect(trait.getOtherRoom('room3')).toBeNull();
  });

  test('should handle directions', () => {
    const trait = new DoorTrait({
      connects: ['hallway', 'bedroom'],
      direction: { hallway: 'north', bedroom: 'south' }
    });

    expect(trait.getDirection('hallway')).toBe('north');
    expect(trait.getDirection('bedroom')).toBe('south');
    expect(trait.getDirection('kitchen')).toBeNull();
  });

  test('should handle bidirectional doors', () => {
    // Bidirectional door (default)
    const door1 = new DoorTrait({
      connects: ['room1', 'room2'],
      bidirectional: true
    });

    expect(door1.canTraverse('room1', 'room2')).toBe(true);
    expect(door1.canTraverse('room2', 'room1')).toBe(true);

    // One-way door
    const door2 = new DoorTrait({
      connects: ['entrance', 'exit'],
      bidirectional: false
    });

    expect(door2.canTraverse('entrance', 'exit')).toBe(true);
    expect(door2.canTraverse('exit', 'entrance')).toBe(false);
  });

  test('should handle hidden doors', () => {
    const trait = new DoorTrait({
      connects: ['study', 'secret-room'],
      hidden: true,
      hiddenMessage: 'You notice an unusual pattern in the wallpaper.'
    });

    expect(trait.hidden).toBe(true);
    expect(trait.hiddenMessage).toContain('unusual pattern');

    // Reveal door
    trait.reveal();
    expect(trait.hidden).toBe(false);

    // Hide door again
    trait.hide();
    expect(trait.hidden).toBe(true);
  });

  test('should handle different door types', () => {
    // Standard door
    const standardDoor = new DoorTrait({
      connects: ['room1', 'room2'],
      direction: { room1: 'east', room2: 'west' }
    });
    expect(standardDoor.bidirectional).toBe(true);

    // Secret passage
    const secretPassage = new DoorTrait({
      connects: ['library', 'vault'],
      hidden: true,
      hiddenMessage: 'A draft suggests a hidden passage.'
    });
    expect(secretPassage.hidden).toBe(true);

    // One-way chute
    const chute = new DoorTrait({
      connects: ['upper-level', 'basement'],
      direction: { 'upper-level': 'down' },
      bidirectional: false
    });
    expect(chute.canTraverse('upper-level', 'basement')).toBe(true);
    expect(chute.canTraverse('basement', 'upper-level')).toBe(false);

    // Portal
    const portal = new DoorTrait({
      connects: ['world1', 'world2'],
      direction: { world1: 'in', world2: 'out' }
    });
    expect(portal.getDirection('world1')).toBe('in');
  });

  test('should validate door configuration', () => {
    const trait = new DoorTrait({
      connects: ['north-room', 'south-room'],
      direction: { 'north-room': 'south', 'south-room': 'north' }
    });

    // Check consistency
    expect(trait.connects.length).toBe(2);
    expect(trait.getDirection('north-room')).toBe('south');
    expect(trait.getDirection('south-room')).toBe('north');

    // Both rooms should be connected
    expect(trait.connectsRoom('north-room')).toBe(true);
    expect(trait.connectsRoom('south-room')).toBe(true);
  });
});

