import { ScriptableTrait } from '../scriptable';

describe('ScriptableTrait', () => {
  test('should create with defaults', () => {
    const trait = new ScriptableTrait();
    expect(trait.type).toBe('scriptable');
    expect(trait.scripts).toEqual({});
  });

  test('should create with custom scripts', () => {
    const scripts = {
      onExamine: 'return "You notice strange markings.";',
      onTake: 'if (player.hasItem("gloves")) return true; else return "Too hot to touch!";'
    };

    const trait = new ScriptableTrait({ scripts });
    expect(trait.scripts.onExamine).toContain('strange markings');
    expect(trait.scripts.onTake).toContain('gloves');
  });

  test('should check for script existence', () => {
    const trait = new ScriptableTrait({
      scripts: {
        onExamine: 'return "Custom examine text";',
        onUse: 'player.addScore(10); return "You activated it!";'
      }
    });

    expect(trait.hasScript('onExamine')).toBe(true);
    expect(trait.hasScript('onUse')).toBe(true);
    expect(trait.hasScript('onTake')).toBe(false);
    expect(trait.hasScript('onDrop')).toBe(false);
  });

  test('should get scripts', () => {
    const trait = new ScriptableTrait({
      scripts: {
        beforeTake: 'return player.strength >= 10;',
        afterTake: 'world.reveal("secret-door");'
      }
    });

    expect(trait.getScript('beforeTake')).toBe('return player.strength >= 10;');
    expect(trait.getScript('afterTake')).toBe('world.reveal("secret-door");');
    expect(trait.getScript('nonexistent')).toBeNull();
  });

  test('should add and remove scripts', () => {
    const trait = new ScriptableTrait();

    // Add scripts
    trait.setScript('onExamine', 'return "It glows faintly.";');
    expect(trait.hasScript('onExamine')).toBe(true);
    expect(trait.scripts.onExamine).toBe('return "It glows faintly.";');

    trait.setScript('onTouch', 'player.takeDamage(5); return "Ouch!";');
    expect(trait.hasScript('onTouch')).toBe(true);

    // Remove script
    trait.removeScript('onExamine');
    expect(trait.hasScript('onExamine')).toBe(false);
    expect(trait.scripts.onExamine).toBeUndefined();
  });

  test('should list all hooks', () => {
    const trait = new ScriptableTrait({
      scripts: {
        onExamine: 'script1',
        beforeTake: 'script2',
        onUse: 'script3'
      }
    });

    const hooks = trait.getHooks();
    expect(hooks).toEqual(['onExamine', 'beforeTake', 'onUse']);
    expect(hooks.length).toBe(3);
  });

  test('should handle various script hooks', () => {
    const trait = new ScriptableTrait({
      scripts: {
        // Examination hooks
        onExamine: 'return "A mysterious artifact.";',
        onLook: 'return "It pulses with energy.";',

        // Interaction hooks
        beforeTake: 'return player.level >= 5;',
        afterTake: 'world.spawnEnemy("guardian");',
        onDrop: 'world.shake(); return "The ground trembles!";',

        // Use hooks
        onUse: 'player.heal(20); return "You feel refreshed.";',
        onActivate: 'world.openPortal("dimension-x");',

        // State change hooks
        onOpen: 'return "You open it carefully.";',
        onClose: 'world.lockDoor("vault"); return "Click!";',
        onUnlock: 'player.addKey("master"); return "Unlocked!";',

        // Combat hooks
        onAttack: 'return weapon.damage * 2;',
        onDefend: 'player.shield += 10;',

        // Special event hooks
        onTurnEnd: 'if (turn % 10 === 0) world.event("moon-phase");',
        onPlayerEnter: 'world.startMusic("boss-theme");',
        onPlayerLeave: 'world.stopMusic();'
      }
    });

    expect(trait.getHooks().length).toBe(15);
    expect(trait.hasScript('onExamine')).toBe(true);
    expect(trait.hasScript('onTurnEnd')).toBe(true);
  });

  test('should support conditional scripts', () => {
    const trait = new ScriptableTrait({
      scripts: {
        onExamine: `
          if (player.hasItem("magnifying-glass")) {
            return "You see tiny inscriptions: 'XYZZY'";
          } else {
            return "You see some kind of markings.";
          }
        `,
        beforeTake: `
          if (world.time === "night") {
            return false; // Can't take at night
          }
          return true;
        `
      }
    });

    expect(trait.scripts.onExamine).toContain('magnifying-glass');
    expect(trait.scripts.beforeTake).toContain('night');
  });

  test('should handle script validation', () => {
    const trait = new ScriptableTrait();

    // Valid script (would be validated by script engine)
    trait.setScript('onUse', 'player.addScore(10); return "Success!";');
    expect(() => trait.validate()).not.toThrow();

    // Scripts are strings, type checking
    trait.scripts = { onExamine: 123 as any };
    expect(() => trait.validate()).toThrow('scriptable.scripts: all scripts must be strings');

    trait.scripts = { onExamine: null as any };
    expect(() => trait.validate()).toThrow('scriptable.scripts: all scripts must be strings');
  });

  test('should clear all scripts', () => {
    const trait = new ScriptableTrait({
      scripts: {
        hook1: 'script1',
        hook2: 'script2',
        hook3: 'script3'
      }
    });

    expect(trait.getHooks().length).toBe(3);

    trait.clearScripts();
    expect(trait.scripts).toEqual({});
    expect(trait.getHooks().length).toBe(0);
  });

  test('should support complex game logic scripts', () => {
    const trait = new ScriptableTrait({
      scripts: {
        // Puzzle mechanism
        onUse: `
          const sequence = world.getVar("puzzleSequence") || [];
          sequence.push(this.id);
          world.setVar("puzzleSequence", sequence);

          if (sequence.length === 4) {
            if (sequence.join(",") === "red,blue,green,yellow") {
              world.unlock("treasure-room");
              return "The mechanism clicks open!";
            } else {
              world.setVar("puzzleSequence", []);
              return "The sequence resets.";
            }
          }
          return "The button glows briefly.";
        `,

        // Time-based event
        onTurnEnd: `
          const lastActivation = this.getVar("lastActivation") || 0;
          if (world.turn - lastActivation >= 10) {
            this.setVar("lastActivation", world.turn);
            world.spawn("monster", this.location);
          }
        `
      }
    });

    expect(trait.scripts.onUse).toContain('puzzleSequence');
    expect(trait.scripts.onTurnEnd).toContain('lastActivation');
  });
});

