import { DialogueTrait } from '../dialogue';

describe('DialogueTrait', () => {
  test('should create with defaults', () => {
    const trait = new DialogueTrait();
    expect(trait.type).toBe('dialogue');
    expect(trait.dialogueTree).toEqual({});
    expect(trait.currentNode).toBe('start');
    expect(trait.visitedNodes).toEqual([]);
  });

  test('should create with custom dialogue tree', () => {
    const tree = {
      start: {
        text: 'Hello, traveler!',
        responses: [
          { text: 'Hello', next: 'greeting' },
          { text: 'Goodbye', next: 'end' }
        ]
      },
      greeting: {
        text: 'How can I help you?',
        responses: [
          { text: 'I need information', next: 'info' },
          { text: 'Nothing, thanks', next: 'end' }
        ]
      }
    };

    const trait = new DialogueTrait({
      dialogueTree: tree,
      currentNode: 'start'
    });

    expect(trait.dialogueTree.start.text).toBe('Hello, traveler!');
    expect(trait.dialogueTree.start.responses).toHaveLength(2);
  });

  test('should get current dialogue node', () => {
    const trait = new DialogueTrait({
      dialogueTree: {
        start: {
          text: 'Welcome!',
          responses: [{ text: 'Hi', next: 'greeting' }]
        },
        greeting: {
          text: 'How are you?',
          responses: []
        }
      }
    });

    const node = trait.getCurrentNode();
    expect(node?.text).toBe('Welcome!');
    expect(node?.responses).toHaveLength(1);

    trait.currentNode = 'greeting';
    const node2 = trait.getCurrentNode();
    expect(node2?.text).toBe('How are you?');
  });

  test('should navigate dialogue tree', () => {
    const trait = new DialogueTrait({
      dialogueTree: {
        start: {
          text: 'Hello!',
          responses: [
            { text: 'Hi', next: 'greeting' },
            { text: 'Bye', next: 'end' }
          ]
        },
        greeting: {
          text: 'Nice to meet you!',
          responses: [{ text: 'Likewise', next: 'end' }]
        },
        end: {
          text: 'Farewell!',
          responses: []
        }
      }
    });

    expect(trait.currentNode).toBe('start');
    expect(trait.visitedNodes).toEqual([]);

    // Choose first response
    trait.selectResponse(0);
    expect(trait.currentNode).toBe('greeting');
    expect(trait.visitedNodes).toEqual(['start']);

    // Choose response
    trait.selectResponse(0);
    expect(trait.currentNode).toBe('end');
    expect(trait.visitedNodes).toEqual(['start', 'greeting']);
  });

  test('should handle invalid response selection', () => {
    const trait = new DialogueTrait({
      dialogueTree: {
        start: {
          text: 'Hello!',
          responses: [{ text: 'Hi', next: 'end' }]
        }
      }
    });

    // Valid response
    expect(trait.selectResponse(0)).toBe(true);

    // Reset
    trait.currentNode = 'start';

    // Invalid response index
    expect(trait.selectResponse(5)).toBe(false);
    expect(trait.currentNode).toBe('start'); // No change

    // Negative index
    expect(trait.selectResponse(-1)).toBe(false);
  });

  test('should check if node was visited', () => {
    const trait = new DialogueTrait({
      dialogueTree: {
        start: { text: 'Hi', responses: [{ text: 'Hello', next: 'node1' }] },
        node1: { text: 'Question?', responses: [{ text: 'Yes', next: 'node2' }] },
        node2: { text: 'Answer!', responses: [] }
      }
    });

    expect(trait.hasVisited('start')).toBe(false);
    expect(trait.hasVisited('node1')).toBe(false);

    trait.selectResponse(0); // Go to node1
    expect(trait.hasVisited('start')).toBe(true);
    expect(trait.hasVisited('node1')).toBe(false);

    trait.selectResponse(0); // Go to node2
    expect(trait.hasVisited('node1')).toBe(true);
    expect(trait.hasVisited('node2')).toBe(false);
  });

  test('should reset dialogue', () => {
    const trait = new DialogueTrait({
      dialogueTree: {
        start: { text: 'Hi', responses: [{ text: 'Next', next: 'end' }] },
        end: { text: 'Bye', responses: [] }
      }
    });

    // Progress dialogue
    trait.selectResponse(0);
    expect(trait.currentNode).toBe('end');
    expect(trait.visitedNodes).toEqual(['start']);

    // Reset
    trait.reset();
    expect(trait.currentNode).toBe('start');
    expect(trait.visitedNodes).toEqual([]);
  });

  test('should go to specific node', () => {
    const trait = new DialogueTrait({
      dialogueTree: {
        start: { text: 'Start', responses: [] },
        middle: { text: 'Middle', responses: [] },
        end: { text: 'End', responses: [] }
      }
    });

    trait.goToNode('middle');
    expect(trait.currentNode).toBe('middle');
    expect(trait.visitedNodes).toEqual(['start']);

    trait.goToNode('end');
    expect(trait.currentNode).toBe('end');
    expect(trait.visitedNodes).toEqual(['start', 'middle']);
  });

  test('should handle conditional responses', () => {
    const trait = new DialogueTrait({
      dialogueTree: {
        merchant: {
          text: 'Want to buy something?',
          responses: [
            { 
              text: 'Show me weapons', 
              next: 'weapons',
              condition: 'player.level >= 5'
            },
            { 
              text: 'Show me potions', 
              next: 'potions',
              condition: 'true' // Always available
            },
            {
              text: 'Secret items?',
              next: 'secret',
              condition: 'player.hasItem("merchant-token")'
            }
          ]
        }
      }
    });

    const node = trait.getCurrentNode();
    expect(node?.responses).toHaveLength(3);

    // In actual implementation, conditions would be evaluated
    // to filter available responses
  });

  test('should handle complex dialogue trees', () => {
    const trait = new DialogueTrait({
      dialogueTree: {
        start: {
          text: 'I am the gatekeeper. What do you seek?',
          responses: [
            { text: 'Passage through the gate', next: 'passage' },
            { text: 'Information', next: 'info' },
            { text: 'Nothing', next: 'end' }
          ]
        },
        passage: {
          text: 'Do you have the key?',
          responses: [
            { text: 'Yes', next: 'check-key', condition: 'player.hasItem("gate-key")' },
            { text: 'No', next: 'no-key' },
            { text: 'What key?', next: 'key-info' }
          ]
        },
        'check-key': {
          text: 'Ah, you may pass. Safe travels!',
