// packages/stdlib/src/world-model/traits/advanced/__tests__/merchant.test.ts

import { MerchantTrait } from '../merchant';

describe('MerchantTrait', () => {
  test('should create with defaults', () => {
    const trait = new MerchantTrait();
    expect(trait.type).toBe('merchant');
    expect(trait.inventory).toEqual([]);
    expect(trait.currency).toBe('gold');
    expect(trait.buyMultiplier).toBe(0.5);
    expect(trait.sellMultiplier).toBe(1);
    expect(trait.restockTurns).toBe(100);
    expect(trait.lastRestock).toBe(0);
  });

  test('should create with custom values', () => {
    const trait = new MerchantTrait({
      inventory: [
        { itemId: 'sword', price: 100, quantity: 5 },
        { itemId: 'potion', price: 25, quantity: 10 }
      ],
      currency: 'coins',
      buyMultiplier: 0.3,
      sellMultiplier: 1.2,
      restockTurns: 50
    });

    expect(trait.inventory).toHaveLength(2);
    expect(trait.currency).toBe('coins');
    expect(trait.buyMultiplier).toBe(0.3);
    expect(trait.sellMultiplier).toBe(1.2);
    expect(trait.restockTurns).toBe(50);
  });

  test('should validate multipliers', () => {
    const trait = new MerchantTrait();

    // Valid multipliers
    trait.buyMultiplier = 0.1;
    trait.sellMultiplier = 2;
    expect(() => trait.validate()).not.toThrow();

    // Invalid buy multiplier
    trait.buyMultiplier = -0.1;
    expect(() => trait.validate()).toThrow('merchant.buyMultiplier: must be positive');

    trait.buyMultiplier = 0;
    expect(() => trait.validate()).toThrow('merchant.buyMultiplier: must be positive');

    // Invalid sell multiplier
    trait.buyMultiplier = 0.5; // Reset to valid
    trait.sellMultiplier = -1;
    expect(() => trait.validate()).toThrow('merchant.sellMultiplier: must be positive');
  });

  test('should validate inventory', () => {
    const trait = new MerchantTrait();

    // Valid inventory
    trait.inventory = [
      { itemId: 'sword', price: 100, quantity: 5 },
      { itemId: 'shield', price: 75, quantity: 3 }
    ];
    expect(() => trait.validate()).not.toThrow();

    // Invalid price
    trait.inventory = [
      { itemId: 'sword', price: -10, quantity: 5 }
    ];
    expect(() => trait.validate()).toThrow('merchant.inventory: item sword has invalid price');

    // Invalid quantity
    trait.inventory = [
      { itemId: 'sword', price: 100, quantity: -1 }
    ];
    expect(() => trait.validate()).toThrow('merchant.inventory: item sword has invalid quantity');

    // Duplicate items
    trait.inventory = [
      { itemId: 'sword', price: 100, quantity: 5 },
      { itemId: 'sword', price: 120, quantity: 3 }
    ];
    expect(() => trait.validate()).toThrow('merchant.inventory: duplicate item sword');
  });

  test('should check if has item', () => {
    const trait = new MerchantTrait({
      inventory: [
        { itemId: 'sword', price: 100, quantity: 5 },
        { itemId: 'potion', price: 25, quantity: 0 }
      ]
    });

    expect(trait.hasItem('sword')).toBe(true);
    expect(trait.hasItem('potion')).toBe(false); // Quantity 0
    expect(trait.hasItem('armor')).toBe(false);
  });

  test('should get item details', () => {
    const trait = new MerchantTrait({
      inventory: [
        { itemId: 'sword', price: 100, quantity: 5 }
      ]
    });

    const item = trait.getItem('sword');
    expect(item).toEqual({ itemId: 'sword', price: 100, quantity: 5 });

    const missing = trait.getItem('shield');
    expect(missing).toBeNull();
  });

  test('should calculate prices', () => {
    const trait = new MerchantTrait({
      inventory: [
        { itemId: 'sword', price: 100, quantity: 5 }
      ],
      buyMultiplier: 0.5,
      sellMultiplier: 1.2
    });

    // Sell price (what merchant sells for)
    expect(trait.getSellPrice('sword')).toBe(120); // 100 * 1.2

    // Buy price (what merchant buys for)
    expect(trait.getBuyPrice('sword')).toBe(50); // 100 * 0.5

    // Non-existent item
    expect(trait.getSellPrice('shield')).toBe(0);
    expect(trait.getBuyPrice('shield')).toBe(0);
  });

  test('should handle transactions', () => {
    const trait = new MerchantTrait({
      inventory: [
        { itemId: 'sword', price: 100, quantity: 5 },
        { itemId: 'potion', price: 25, quantity: 10 }
      ]
    });

    // Sell item (merchant sells to player)
    expect(trait.sellItem('sword', 2)).toBe(true);
    expect(trait.getItem('sword')?.quantity).toBe(3);

    expect(trait.sellItem('potion', 5)).toBe(true);
    expect(trait.getItem('potion')?.quantity).toBe(5);

    // Can't sell more than available
    expect(trait.sellItem('potion', 10)).toBe(false);
    expect(trait.getItem('potion')?.quantity).toBe(5);

    // Can't sell non-existent item
    expect(trait.sellItem('armor', 1)).toBe(false);
  });

  test('should buy items from player', () => {
    const trait = new MerchantTrait({
      inventory: [
        { itemId: 'sword', price: 100, quantity: 2 }
      ]
    });

    // Buy existing item (increases quantity)
    expect(trait.buyItem('sword', 3)).toBe(true);
    expect(trait.getItem('sword')?.quantity).toBe(5);

    // Buy new item (adds to inventory)
    expect(trait.buyItem('gem', 1, 500)).toBe(true);
    expect(trait.hasItem('gem')).toBe(true);
    expect(trait.getItem('gem')).toEqual({
      itemId: 'gem',
      price: 500,
      quantity: 1
    });
  });

  test('should handle restocking', () => {
    const trait = new MerchantTrait({
      inventory: [
        { itemId: 'sword', price: 100, quantity: 5, restockQuantity: 5 },
        { itemId: 'potion', price: 25, quantity: 10, restockQuantity: 20 }
      ],
      restockTurns: 10,
      lastRestock: 0
    });

    // Sell some items
    trait.sellItem('sword', 4);
    trait.sellItem('potion', 10);

    expect(trait.getItem('sword')?.quantity).toBe(1);
    expect(trait.getItem('potion')?.quantity).toBe(0);

    // Check if needs restock
    expect(trait.needsRestock(5)).toBe(false); // Only 5 turns passed
    expect(trait.needsRestock(15)).toBe(true); // 15 turns passed

    // Restock
    trait.restock(15);
    expect(trait.getItem('sword')?.quantity).toBe(5);
    expect(trait.getItem('potion')?.quantity).toBe(20);
    expect(trait.lastRestock).toBe(15);
  });

  test('should handle different merchant types', () => {
    // Weapon merchant
    const weaponMerchant = new MerchantTrait({
      inventory: [
        { itemId: 'sword', price: 100, quantity: 5 },
        { itemId: 'axe', price: 120, quantity: 3 },
        { itemId: 'bow', price: 80, quantity: 4 }
      ],
      sellMultiplier: 1.5 // Higher prices for weapons
    });
    expect(weaponMerchant.getSellPrice('sword')).toBe(150);

    // Potion merchant
    const potionMerchant = new MerchantTrait({
      inventory: [
        { itemId: 'health-potion', price: 50, quantity: 20 },
        { itemId: 'mana-potion', price: 75, quantity: 15 },
        { itemId: 'antidote', price: 25, quantity: 10 }
      ],
      restockTurns: 20 // Restocks frequently
    });
    expect(potionMerchant.inventory.length).toBe(3);

    // General store
    const generalStore = new MerchantTrait({
      inventory: [
        { itemId: 'rope', price: 10, quantity: 50 },
        { itemId: 'torch', price: 5, quantity: 100 },
        { itemId: 'food', price: 15, quantity: 30 }
      ],
      buyMultiplier: 0.3, // Buys cheap
      sellMultiplier: 0.9 // Sells cheap too
    });
    expect(generalStore.getBuyPrice('rope')).toBe(3);
  });

  test('should track merchant state', () => {
    const trait = new MerchantTrait({
      inventory: [
        { itemId: 'rare-sword', price: 1000, quantity: 1 },
        { itemId: 'common-sword', price: 100, quantity: 10 }
      ]
    });

    // Sell rare item
    trait.sellItem('rare-sword', 1);
    expect(trait.hasItem('rare-sword')).toBe(false);

    // Track total value
    const totalValue = trait.inventory.reduce(
      (sum, item) => sum + item.price * item.quantity,
      0
    );
    expect(totalValue).toBe(1000); // Only common swords left
  });
});

// packages/stdlib/src/world-model/services/__tests__/query-builder.test.ts

import { QueryBuilder, QueryOperator } from '../query-builder';
import { EntityStore } from '../../traits/entity-store';
import { IFEntity } from '../../traits/if-entity';
import { IdentityTrait } from '../../traits/standard/identity';
import { PortableTrait } from '../../traits/standard/portable';
import { ContainerTrait } from '../../traits/standard/container';
import { LocationTrait } from '../../traits/standard/location';
import { OpenableTrait } from '../../traits/interactive/openable';
import { LightSourceTrait } from '../../traits/interactive/light-source';
import { TraitType } from '../../traits/trait-types';

describe('QueryBuilder', () => {
  let store: EntityStore;
  let builder: QueryBuilder;

  beforeEach(() => {
    store = new EntityStore();
    builder = new QueryBuilder(store);

    // Create test entities
    const room = new IFEntity('room');
    room.add(new IdentityTrait({ name: 'Living Room' }));
    room.add(new LocationTrait());
    store.set(room);

    const box = new IFEntity('box');
    box.add(new IdentityTrait({ name: 'wooden box', aliases: ['box', 'container'] }));
    box.add(new ContainerTrait({ capacity: 5, open: false }));
    box.add(new OpenableTrait({ open: false }));
    box.add(new LocationTrait({ location: 'room' }));
    store.set(box);

    const key = new IFEntity('key');
    key.add(new IdentityTrait({ name: 'brass key', aliases: ['key'] }));
    key.add(new PortableTrait({ weight: 0.1 }));
    key.add(new LocationTrait({ location: 'box' }));
    store.set(key);

    const lamp = new IFEntity('lamp');
    lamp.add(new IdentityTrait({ name: 'oil lamp' }));
    lamp.add(new PortableTrait({ weight: 2 }));
    lamp.add(new LightSourceTrait({ providing: true, intensity: 'normal' }));
    lamp.add(new LocationTrait({ location: 'room' }));
    store.set(lamp);
  });

  test('should find entities by trait', () => {
    const portable = builder.hasTrait(TraitType.PORTABLE).execute();
    expect(portable.map(e => e.id)).toEqual(['key', 'lamp']);

    const containers = builder.hasTrait(TraitType.CONTAINER).execute();
    expect(containers.map(e => e.id)).toEqual(['box']);
  });

  test('should find entities by multiple traits', () => {
    const openableContainers = builder
      .hasTrait(TraitType.CONTAINER)
      .hasTrait(TraitType.OPENABLE)
      .execute();

    expect(openableContainers.map(e => e.id)).toEqual(['box']);

    const portableLights = builder
      .hasTrait(TraitType.PORTABLE)
      .hasTrait(TraitType.LIGHT_SOURCE)
      .execute();

    expect(portableLights.map(e => e.id)).toEqual(['lamp']);
  });

  test('should find entities without traits', () => {
    const nonPortable = builder.notHasTrait(TraitType.PORTABLE).execute();
    expect(nonPortable.map(e => e.id)).toEqual(['room', 'box']);

    const nonContainers = builder.notHasTrait(TraitType.CONTAINER).execute();
    expect(nonContainers.map(e => e.id)).toEqual(['room', 'key', 'lamp']);
  });

  test('should filter by trait properties', () => {
    // Find closed containers
    const closed = builder
      .hasTrait(TraitType.CONTAINER)
      .where(TraitType.CONTAINER, 'open', QueryOperator.EQUALS, false)
      .execute();

    expect(closed.map(e => e.id)).toEqual(['box']);

    // Find light items (weight < 1)
    const lightItems = builder
      .hasTrait(TraitType.PORTABLE)
      .where(TraitType.PORTABLE, 'weight', QueryOperator.LESS_THAN, 1)
      .execute();

    expect(lightItems.map(e => e.id)).toEqual(['key']);
  });

  test('should use string operators', () => {
    // Find entities with names starting with 'w'
    const startsWithW = builder
      .hasTrait(TraitType.IDENTITY)
      .where(TraitType.IDENTITY, 'name', QueryOperator.STARTS_WITH, 'w')
